namespace ADEXS.DetectTypeService;

public partial class DetectTypeProcessor
{
	private readonly IServiceInstrumentation _serviceInstrumentation;

	private Activity? StartActivity([CallerMemberName] string name = "") =>
		_serviceInstrumentation?.ActivitySource
			?.StartActivity($"{InstrumentationNames.DetectTypeName}.{name}")
			?.SetStatus(ActivityStatusCode.Error)
			?.SetTag(TraceSemanticConventions.AttributeCodeFunction, name)
			?.SetTag(TraceSemanticConventions.AttributeMessageType, TraceSemanticConventions.MessageTypeValues.Received)
			?.SetTag(MqWorkersServiceTraceSemanticConventions.AttributeDocuSignMessageTaskName, nameof(TaskTypes.DetectTypeTask))
			?.SetMetricName(InstrumentationNames.DetectTypeName)
			?.SetQueueTaskTags(Task)
			?.SetTag(ServiceTraceSemanticConventions.AttributeDocuSignFileDataId, DeserializedPayload.FileDataUid);
}
