﻿using System;
using System.Diagnostics;
using System.Net.Mime;

using ADEXS.Core.Domain;
using ADEXS.Core.Domain.Model;
using ADEXS.Core.Domain.Model.QueueTasks;
using ADEXS.Core.Domain.Persistence;
using ADEXS.Core.Domain.Persistence.Model;
using ADEXS.Core.Domain.PhysicalStorage;
using ADEXS.Core.Util.Extensions;
using ADEXS.Core.Util.FileIO;
using ADEXS.Core.Util.ServiceLocator;
using ADEXS.DetectTypeService.Config;
using ADEXS.DetectTypeService.Extensions.Telemetry;
using ADEXS.DetectTypeService.MimeLookup;
using ADEXS.DetectTypeService.Telemetry;
using ADEXS.MqWorkers.DispatchedWorker;
using ADEXS.ServiceFramework;

using DocuSign.CLM.ContentStorage;

namespace ADEXS.DetectTypeService
{
	public partial class DetectTypeProcessor : CommonTaskProcessor<DetectTypeTaskPayload>
	{
		private readonly IAtlasPersistenceFactory _atlasPersistenceFactory;

		private readonly IAtlasPhysicalStorageFactory _atlasPhysicalStorageFactory;

		private readonly IMimeDetector _mimeDetector;

		private readonly IDetectTypeServiceFeaturesConfig _featuresConfig;

		public DetectTypeProcessor()
			: this(
				ServiceLocator.GetInstance<IAtlasPersistenceFactory>(),
				ServiceLocator.GetInstance<IAtlasPhysicalStorageFactory>(),
				ServiceLocator.GetInstance<IMimeDetector>(),
				ServiceLocator.GetInstance<IServiceInstrumentation>(),
				ServiceLocator.GetInstance<IDetectTypeServiceFeaturesConfig>())
		{
		}

		public DetectTypeProcessor(
			IAtlasPersistenceFactory atlasPersistenceFactory,
			IAtlasPhysicalStorageFactory atlasPhysicalStorageFactory,
			IMimeDetector mimeDetector,
			IServiceInstrumentation serviceInstrumentation,
			IDetectTypeServiceFeaturesConfig featuresConfig)
		{
			_atlasPersistenceFactory = atlasPersistenceFactory;
			_atlasPhysicalStorageFactory = atlasPhysicalStorageFactory;
			_mimeDetector = mimeDetector;
			_serviceInstrumentation = serviceInstrumentation;
			_featuresConfig = featuresConfig;
		}

		public override ProcessTaskResult ProcessTask()
		{
			using var activity = StartActivity();

			// get and report the status of whether Optimizely is the feature flag provider
			Logger.InfoNoPII($"Verification: IsOptimizelyVerificationFlagEnabled is {_featuresConfig.IsOptimizelyVerificationFlagEnabled} for account '{Task.AccountId}' (context accountId: '{AtlasContext.Current.AccountId}')");

			ProcessTaskResult result = new ProcessTaskResult();
			result.TaskResult = TaskResult.Failure;
			var fileDataPersistence = _atlasPersistenceFactory.Get<IFileDataPersistence>(Task.AccountId);

			FileData fd = fileDataPersistence.LoadByUid(DeserializedPayload.FileDataUid);
			if (fd == null)
			{
				Logger.WarnNoPII($"FileDataUid {DeserializedPayload.FileDataUid} does not exist in the db; ignoring");
				result.TaskResult = TaskResult.Success;
				result.Message = "Filedata Doesn't Exist";
			}
			else
			{
				try
				{
					// first try and detect the MIME type
					string mimeType;
					using (TempFile temp = new TempFile(fd.Extension))
					{
						//We could possibly optimize this by reading the first 256 bytes and passing them to the underlying COM library urlmon.dll.
						//See https://msdn.microsoft.com/en-us/library/ms775107(v=vs.85).aspx (FindMimeFromData function)
						_atlasPhysicalStorageFactory.GetPhysicalStorage(fd.AccountId).GetFile(fd.AccountId, fd.FileName, StorageConstants.StorageFormat.Native, temp.FileName);
						mimeType = _mimeDetector.DetectMimeType(temp.FileName);
					}
					Task.LastErrorMessage = string.Format("Detected MIME type '{0}' for fileDataUid {1}.", mimeType, fd.FileDataUid);

					// if still not sure try to map the extension to the MIME type
					if (string.IsNullOrEmpty(mimeType) || (mimeType == MediaTypeNames.Application.Octet))
					{
						mimeType = FileUtil.GetMimeTypeByExtension(fd.Extension);
						Task.LastErrorMessage = string.Format("Matched extension to MIME type '{0}' for fileDataUid {1} with extension '{2}'.", mimeType, fd.FileDataUid, fd.Extension);
					}

					// once we have a new MIME type, save it
					if (!string.IsNullOrEmpty(mimeType) && mimeType != fd.MIMEType)
					{
						fileDataPersistence.UpdateMIMEType(fd.Uid, mimeType);
						Task.LastErrorMessage = string.Format("Detected and set MIME type '{0}' for fileDataUid {1} with extension '{2}'.", mimeType, fd.FileDataUid, fd.Extension);
					}
					Logger.InfoNoPII(Task.LastErrorMessage);
					result.TaskResult = TaskResult.Success;
					activity?.SetStatus(ActivityStatusCode.Ok)
						?.SetTag(ServiceTraceSemanticConventions.AttributeDocuSignDetectTypeMimeType, mimeType);
				}
				catch (Exception e)
				{
					result.TaskResult = TaskResult.Failure;
					e.Trace();
					Logger.ErrorNoPII($"Failed to process fileDataUid {fd.FileDataUid} with exception ", e);
					try
					{
						Task.LastErrorMessage = e.Message;
					}
					catch (Exception e2)
					{
						Logger.ErrorNoPII($"Failed to mark fileDataUid {fd.FileDataUid} as failed with exception ", e2);
					}
				}
			}

			activity?.SetQueueTaskResultTags(Task, result);
			return result;
		}
	}
}

﻿using System;
using System.Collections.Generic;
using ADEXS.Core.Util.Core;
using ADEXS.MQ;
using ADEXS.Core.Domain;
using System.Threading;
using ADEXS.Core.Domain.Persistence.Model;
using System.IO;
using ADEXS.Core.Domain.Model;
using System.Diagnostics;

using ADEXS.Core.Domain.FeatureFlags;
using ADEXS.Core.Types;
using ADEXS.Core.Util.Config;
using ADEXS.Core.Util.Config.Context;
using ADEXS.Core.Util.Extensions;
using ADEXS.Core.Util.ServiceLocator;
using ADEXS.Core.Util.Text;
using ADEXS.ServiceFramework;
using CLM.MessageDefines;
using CLM.MessageDefines.Constants;
using CLM.MessageDefines.Interfaces;
using DocuSign.CLM.Monitoring;
using DocuSign.Monitoring;

using ADEXS.MqWorkers.DispatchedWorker.Telemetry;

using StructureMap;
using ADEXS.MqWorkers.Config.Context;
using ADEXS.MqWorkers.Extensions;
using ADEXS.MqWorkers.Telemetry;

using CLM.MessageDefines.Tasks;

using DocuSign.Msf.AspNet.OpenTelemetry.SemanticConventions;
using DocuSign.OneConfig.Extensions;
using ADEXS.MqWorkers.Config;

namespace ADEXS.Services.MessageWorkerService
{
	//Should probably make an interface that encapsulates the methods for loading and
	//updating the Tasks so that we don't have to be tied to MessageQueues for this type of worker
	public class DispatchedWorker<T, U> : AbstractServiceQueue<T> where T : class, IQueueTask, new() where U : class, ITaskProcessor<T>, new()
	{
		private const string DESIRED_WORKER_COUNT = "_WorkerCount";
		private const string DESIRED_WORKER_MULTIPLIER = "_WorkerMultiplier";
		private const string TASK_DISPATCHERS = "_TaskDispatchers";
		private const string DESIRED_STAND_ALONE = "_StandAlone";
		private const string DEFAULT_TASK_DISPATCHERS = "DefaultTaskDispatchers";
		private const int DEFAULT_WORKER_COUNT = 5;
		private const double DEAFULT_CORE_TO_WORKER_MULTIPLIER = 0;
		private readonly string TASKPROCESSOR = Configuration.GetStringAppSetting(PROCESSSTART_NAME, "ADEXS.TaskProcessor.exe");
		private const string PROCESSSTART_NAME = "PROCESSSTART_NAME";
		private string _typeName;
		private readonly TaskTypes _taskType;
		private bool _standAlone;

		private readonly IContainer _container;         // optionally injected DI container
		private readonly IWatchedSingletonConfig<ISiteConfig> _siteConfig;
		private readonly Func<U> _createProcessor;
		private int _fetchMod;

		[ThreadStatic]
		private static int _errorCount = 0;

		public override string Name => base.Name + TypeName;

		[EventSource("Workers", "CLM.DispatchedWorker")]
		private static readonly PropertyBagEventSource _workersEventSource = new PropertyBagEventSource();

		[Counter("Workers Count", "CLM.DispatchedWorker", MeasureProperty = "Count", InstanceProperty = "Description")]
		[EventSource("Workers Count", "CLM.DispatchedWorker")]
		private static readonly CountEventSource _workersCountEventSource = new CountEventSource();

		[EventSource("Worker Logging Exceptions", "CLM.DispatchedWorker")]
		[Counter("Worker Logging Exceptions", "CLM.DispatchedWorker", InstanceProperty = "Description")]
		private static ExceptionEventSource _workerLoggingExceptionEventSource = new ExceptionEventSource();

		static DispatchedWorker()
		{
			// Kazmon emits from a generic type do not work without this call
			MonitoringRuntime.RegisterGenericType(typeof(DispatchedWorker<T, U>));
		}

		public string TypeName
		{
			get
			{
				_typeName ??= _taskType.ToString();
				return _typeName;
			}
		}

		public IMessageQueueWrapperFactory MessageQueueWrapperFactory { get; set; }

		/// <summary>
		/// Legacy Constructor -- delegates to the new overload; passes DI container extracted from ServiceLocator.
		/// This method will be deprecated in the future -- prefer the (IServicedComponent, IContainer) overload.
		/// </summary>
		/// <param name="component">the component this dispatched worker services</param>
		/// <param name="taskType">the task that will be run by this worker</param>
		public DispatchedWorker(IServicedComponent component, TaskTypes taskType, IWatchedSingletonConfig<ISiteConfig> siteConfig) : this(component, taskType, ServiceLocator.Container, Activator.CreateInstance<U>, siteConfig)
		{
		}

		/// <summary>
		/// Constructor overload that accepts a DI container (stepping stone to moving away from SL pattern).
		/// </summary>
		/// <param name="component">the component this worker services</param>
		/// <param name="taskType">the task that will be run by this worker</param>
		/// <param name="container">the DI container for service discovery and IOC</param>
		internal DispatchedWorker(IServicedComponent component, TaskTypes taskType, IContainer container, Func<U> processorFactory, IWatchedSingletonConfig<ISiteConfig> siteConfig) : base(component)
		{
			_container = container;
			_createProcessor = processorFactory ?? (() => new U());
			ServiceLocator.BuildUp(this);
			_taskType = taskType;
			//This is here to prevent a regression following ATL-81324
			if (TypeName != typeof(T).Name)
				throw new ArgumentException($"Failed to initialize DispatchedWorker: Task Name ({typeof(T).Name}) must match task type ({TypeName})");
			_waitOnNoItems = false;
			_standAlone = Configuration.GetBooleanAppSetting(TypeName + DESIRED_STAND_ALONE, false);
			if (_standAlone) _log.DebugNoPII($"Worker running in StandAlone mode for {TypeName}");
			_siteConfig = siteConfig;
		}

		private ProcessTaskResult RunExternalTaskProcessor(ProcessTaskRequest taskRequest)
		{
			var accountId = taskRequest.Task.AccountId;
			bool useAnonymousPipes = TasksManagementFeatureFlags.UseAnonymousPipeForExternalTaskProcessors &&
			                         Configuration.GetBooleanAppSetting(
				                         $"{accountId}_UseAnonymousPipeForExternalTaskProcessors", true);
			_log.InfoNoPII(useAnonymousPipes
				? "Worker using Anonymous Pipes for Request and Results"
				: "Worker using Temp Files for Request and Results");
			ExternalTaskWorker externalTaskWorker = ExternalTaskWorkerFactory.GetExternalTaskWorker(useAnonymousPipes);
			return externalTaskWorker.RunExternalProcessor(taskRequest,TASKPROCESSOR);
		}

		protected override int MaxQueueDepth()
		{
			return Configuration.GetInt32AppSetting(TypeName + "_WorkerMaxQueueDepth", Configuration.GetInt32AppSetting(TypeName + WORKER_MAXSET, WORKER_MAXSET_DEFAULT)) * DesiredWorkerCount();
		}

		protected override IList<T> Fetch(int fetchCount)
		{
			List<T> tasks = new List<T>();

			try
			{
				PeriodicPulse();
			}
			catch (Exception ex)
			{
				_log.WarnNoPII($"Could not pulse for ''{TypeName}''.", ex);
			}

			try
			{
				tasks = FetchFromQueues(fetchCount);
			}
			catch (Exception ex)
			{
				_log.WarnNoPII($"Could not load the items for the queue: {TypeName}", ex);
				_lastFetchDate = DateTime.UtcNow;
			}

			AttemptRestorePropertiesFromPayload(tasks);

			return tasks;
		}

		private List<T> FetchFromQueues(int fetchCount)
		{
			var queuePaths = DefaultQueue.GetWorkerMqPaths(TypeName, TaskUtilities.GetMessageBrokerTypeByTaskType(TypeName));

			// keep incrementing to pull from a different queue on each fetch, fetching from all queues seems to have an adverse effect on the channels
			_fetchMod %= queuePaths.Length;
			var queuePath = queuePaths[_fetchMod];
			_fetchMod++;

			return FetchSingleQueue(fetchCount, queuePath);
		}

		private List<T> FetchSingleQueue(int fetchCount, string path)
		{
			bool isMainPath = path == DefaultQueue.GetWorkerMqPath(TypeName, TaskUtilities.GetMessageBrokerTypeByTaskType(TypeName));
			int waitTime = isMainPath ? TaskUtilities.InitialReceiveWaitMS(TypeName) : Configuration.GetInt32AppSetting("InitialReceiveWaitAdditionalQueueMS", 20);
			using (IMessageQueueWrapper pdfMQ = MessageQueueWrapperFactory.CreateReceiveInstance(Configuration.GetStringAppSetting(TypeName + "_WorkerMQPath", path)))
			{
				return pdfMQ.ReceiveMultiple<T>(waitTime, MQApplicationSettings.DefaultExtraReceiveWaitMS, fetchCount);
			}
		}

		/// <summary>
		/// Restore the task type properties from the payload. This will only live temporarily as we move
		/// all tasks toward using the CommonTask payload. If we fail to restore from properties we should log to kazmon
		/// but we should not not block tasks from getting processed. We are also logging when we find a task
		/// without a payload set.
		/// </summary>
		private void AttemptRestorePropertiesFromPayload(List<T> tasks)
		{
			// App Setting to disable this behavior just in case of issues
			var restoreFromPayload = Configuration.GetBooleanAppSetting($"RestoreTaskFromPayload_All", true) &&
				Configuration.GetBooleanAppSetting($"RestoreTaskFromPayload_{TypeName}", true);

			foreach (var task in tasks)
			{
				try
				{
					if (task is CommonTask cTask)
					{
						if (string.IsNullOrEmpty(cTask.Payload))
						{
							// Log to kazmon to ensure that we don't miss any tasks
							CommonTaskPayloadHelper.CommonTaskPayloadMissingEventSource.Emit(1, cTask.Label);
						}
						else
						{
							if (restoreFromPayload)
								cTask.RestorePropertiesFromPayload();
						}
					}
				}
				catch (Exception ex)
				{
					CommonTaskPayloadHelper.PayloadRestoreExceptionEventSource.Emit(ex);
				}
			}
		}

		private QueueItemType _lastSentQueueStatus = QueueItemType.QueueStopped;
		public override void AdjustWorkers()
		{
			base.AdjustWorkers();

			int desiredWorkerCount = DesiredWorkerCount(out double multiplier);
			SetEnabled(desiredWorkerCount > 0);
			QueueItemType currentQueueStatus = (desiredWorkerCount > 0) ? QueueItemType.QueueStarted : QueueItemType.QueueStopped;
			if (currentQueueStatus == _lastSentQueueStatus) return;

			EmitWorkerConfiguration(currentQueueStatus, desiredWorkerCount, multiplier);
			string adminQueuePath = "Unset";
			try
			{
				T startTask = new T
				{
					LastProcessedDate = DateTime.UtcNow,
					SendMachine = Environment.MachineName,
					QueueItemType = currentQueueStatus,
					UniqueId = Guid.NewGuid().ToString()
				};

				if (!_standAlone)
				{
					foreach (string machine in DispatcherMachines())
					{
						adminQueuePath = GetAdminQueuePath(machine);
						using (IMessageQueueWrapper mq = MessageQueueWrapperFactory.CreateInstance<T>(adminQueuePath))
						{
							mq.Send(startTask);
						}
					}
					_lastSentQueueStatus = currentQueueStatus;
				}
			}
			catch (Exception ex)
			{
				_log.ErrorNoPII("DispatchedWorker:AdjustWorkers - Could not contact the administration queue: " + adminQueuePath, ex);
			}
		}

		private void EmitWorkerConfiguration(QueueItemType queueItemtype, int desiredWorkerCount, double multiplier)
		{
			try
			{
				_workersCountEventSource.Emit(desiredWorkerCount, TypeName);

				using (var propertyBag = _workersEventSource.CreateEventPropertyBag(queueItemtype.ToString()))
				{
					propertyBag.AddOrUpdate("TaskType", TypeName);
					propertyBag.AddOrUpdate("DesiredWorkerCount", desiredWorkerCount.ToString());
					propertyBag.AddOrUpdate("Multiplier", multiplier.ToString());
					propertyBag.AddOrUpdate("ProcessorCount", Environment.ProcessorCount.ToString());
				}
			}
			catch (Exception ex)
			{
				_workerLoggingExceptionEventSource.Emit(ex);
			}
		}

		private string GetMachineName() => System.Net.Dns.GetHostName();
		private string GetTaskTypeName() => typeof(T).FullName;

		private static int _queueStateTouchIntervalMin = 60;
		private DateTime _nextStateTouchTime = DateTime.UtcNow.AddMinutes(_queueStateTouchIntervalMin);

		protected virtual void PeriodicPulse()
		{
			if (_nextStateTouchTime < DateTime.UtcNow)
			{
				QueueStatePersistence.AuthenticationInstance.Touch(GetMachineName(), GetTaskTypeName());
				_nextStateTouchTime = DateTime.UtcNow.AddMinutes(_queueStateTouchIntervalMin);
			}
		}

		/// <summary>
		/// Blast the local queue back to the dispatcher so that it can be
		/// dispatched to another worker.
		///
		/// Also sends a disable me message
		/// </summary>
		public override void Stop()
		{
			try
			{
				//Try to do a prelim kill before the stop of the workers
				KillTaskProcessors();
			}
			catch
			{
				_log.DebugNoPII("Didn't kill processes first time");
			}

			base.Stop();
			try
			{
				SetEnabled(false);
				if (!_standAlone && (_lastSentQueueStatus != QueueItemType.QueueStopped))
				{
					foreach (string machine in DispatcherMachines())
					{
						T stopTask = new T
						{
							LastProcessedDate = DateTime.UtcNow,
							QueueItemType = QueueItemType.QueueStopped,
							SendMachine = Environment.MachineName,
							UniqueId = Guid.NewGuid().ToString()
						};
						using (IMessageQueueWrapper mq = MessageQueueWrapperFactory.CreateInstance<T>(GetAdminQueuePath(machine)))
						{
							mq.Send(stopTask);
						}
					}
					_lastSentQueueStatus = QueueItemType.QueueStopped;
				}

				KillTaskProcessors();
			}
			catch (Exception ex)
			{
				_log.ErrorNoPII("DispatchedWorker:Stop - Exception generated during stop:", ex);
			}
			EmitWorkerConfiguration(QueueItemType.QueueStopped, 0, 0);
		}

		private void KillTaskProcessors()
		{
			var nestedContainer = CreateNestedContainer(null);
			var processor = CreateTaskProcessor(nestedContainer);  // necessary to determine if processor would run out-of-process
			// attempt to kill any active out-of-process processors
			if (processor.RunInSeparateProcess && _siteConfig.IsOnPremEnvironment())
			{
				string processName = Path.GetFileNameWithoutExtension(TASKPROCESSOR);
				Process[] tokill = System.Diagnostics.Process.GetProcessesByName(processName);
				if (tokill.Length > 0)
				{
					_log.DebugNoPII($"Killing {processName} processes.");
					foreach (Process p in tokill)
					{
						try
						{
							p.Kill();
						}
						catch (Exception ex)
						{
							_log.WarnNoPII($"Failed to kill process with pid: {p.Id}", ex);
						}
					}
				}
			}
		}

		private void SetEnabled(bool enable)
		{
			if (_standAlone) return;        // short-circuit any communication message based services
			bool alreadySet = false;
			string taskTypeName = GetTaskTypeName();
			_log.DebugNoPII($"Task type: {taskTypeName}, enable set to {enable}");

			foreach (QueueState state in QueueStatePersistence.AuthenticationInstance.LoadAll())
			{
				if (state.MachineName.Equals(GetMachineName(), StringComparison.InvariantCultureIgnoreCase)
					&& state.TaskTypeName == taskTypeName)
				{
					if (enable)
					{
						_log.DebugNoPII($"Machine already exists for task type {taskTypeName}, attempting to enable it");
						RegisterQueueState(state);
						alreadySet = true;
					}
					else
					{
						_log.DebugNoPII($"Machine exists for task type {taskTypeName}, attempting to disable it");
						state.Enabled = false;
						QueueStatePersistence.AuthenticationInstance.Save(state);
					}
				}
			}

			if (enable && !alreadySet)
			{
				// must be a new entry in the queuestate table
				_log.DebugNoPII($"Machine does not already exist for task type {taskTypeName}, attempting to create it");
				RegisterQueueState(null);
			}
		}
		/// <summary>
		/// This will either create the queuestate or update
		/// it as enabled.
		/// </summary>
		private void RegisterQueueState(QueueState queueState)
		{
			if (queueState == null)
			{
				queueState = new QueueState();
				Type taskType = typeof(T);
				queueState.MachineName = GetMachineName();
				queueState.TaskAssemblyName = taskType.Assembly.GetName().Name;
				queueState.TaskTypeName = GetTaskTypeName();
				_log.DebugNoPII($"Creating new entry in QueueState for tasktype: {queueState.TaskTypeName}");
			}

			queueState.Enabled = true;
			queueState.WorkerCount = DesiredWorkerCount(out double multiplier);

			_log.DebugNoPII($"Saving QueueState for task type: {queueState.TaskTypeName} with properties - machine name: {queueState.MachineName}," +
				$"assembly name: {queueState.TaskAssemblyName} enabled: {queueState.Enabled}, worker count: {queueState.WorkerCount}");

			QueueStatePersistence.AuthenticationInstance.Save(queueState);
			EmitWorkerConfiguration(QueueItemType.QueueStarted, queueState.WorkerCount, multiplier);
		}

		protected override TaskResult Process(T task, out string message)
		{
			message = null;

			AtlasContext.Current.AccountId = task.AccountId;
			AtlasContext.Current.TransactionId = task.CorrelationId;
			if (task is IUserTask usrTask)
			{
				AtlasContext.Current.UserAccountUid = usrTask.UserAccountUid;
			}

			Guid corrToken;
			Guid.TryParse(task.CorrelationId, out corrToken);

			using var activity = StartActivity(task, corrToken);

			string telemetryLabel = task.Label;
			using (LogContext.Set(task.CorrelationId))
			using (var logger = new TaskServiceRequestEventLogger(corrToken, telemetryLabel))
			{
				// introduces request-scoped container for user context state (and any other future per-request DI state)
				using var nestedContainer = CreateNestedContainer(AtlasContext.Current);

				// The DataPoint enum is defined in DocuSign sources. Trying to use some, hopefully with close enough semantics.
				ServiceContext.Log(DataPoint.AccountId, task.AccountId);
				ServiceContext.Log(DataPoint.QueueItemId, task.UniqueId);
				ServiceContext.Log(DataPoint.TimeQueued, $"{task.CreatedDate:yyyy-MM-dd HH:mm:ss.fff}");
				if (Guid.Empty != AtlasContext.Current.UserAccountUid)
				{
					ServiceContext.Log(DataPoint.UserId, AtlasContext.Current.UserAccountUid);
				}

				try
				{
					// The nugget inside the monitoring wrapper.
					TaskResult taskResult = ProcessTask(task, nestedContainer, out message, activity);

					ServiceContext.Log(DataPoint.ApiStatus, $"{taskResult}");
					logger.Success = (taskResult != TaskResult.Failure);
					activity?.SetStatusFromTask(taskResult);
					return taskResult;
				}
				catch (Exception ex)
				{
					ex.Trace().Report();
					throw;
 				}
				finally
				{
					if (!logger.Success && null != message)
					{
						ServiceContext.Log(DataPoint.ApiErrorMessage, message);
					}
				}
			}
		}

		private TaskResult ProcessTask(T task, IContainer nestedContainer, out string message, Activity activity)
		{
			ProcessTaskResult result = new ProcessTaskResult() { Message = "Context Set" };

			if (TaskUtilities.CheckSuspendedTaskTypeForAccount(TypeName, task.AccountId))
			{
				message = $"task failed because Aid {task.AccountId} is in the suspended account list for {TypeName}";
				_log.InfoNoPII(message);
				task.ErrorCount = int.MaxValue;
				return TaskResult.Failure;
			}

			var processor = CreateTaskProcessor(nestedContainer);

			AtlasContext.Current.ActionType = processor.ActionType;

			if (processor.RunInSeparateProcess && !processor.HandleSeparateProcess && _siteConfig.IsOnPremEnvironment())
			{
		 		ProcessTaskRequest taskRequest = new ProcessTaskRequest()
				{
					AccountId = AtlasContext.Current.AccountId,
					UserAccountUid = AtlasContext.Current.UserAccountUid,
					ActionType = AtlasContext.Current.ActionType,
					ProcessorTypeName = processor.GetType().AssemblyQualifiedName,
					Task = task,
					TaskType = _taskType,
					CorrelationToken = MonitoringContext.CorrelationToken ?? Guid.Empty
				};
				result = RunExternalTaskProcessor(taskRequest);
			}
			else
			{
				processor.Task = task;
				try
				{
					processor.PreProcess();
					result = processor.ProcessTask();
				}
				finally
				{
					processor.PostProcess();
				}
			}

			message = result.Message;

			activity?.SetQueueTaskResultTags(task, new MqWorkers.DispatchedWorker.ProcessTaskResult { Message = message });

			return result.TaskResult;
		}

		/// <summary>
		/// Returns an instance of the ITaskProcessor that should be used when processing a request.
		/// </summary>
		/// <remarks>
		/// This method takes into account whether we are using a scoped DI container, and if so, will
		/// attempt to get the task processor from there to ensure that other scoped DI services (eg user context)
		/// are also correctly set up. If the supplied `container` parameter is null we create a non-scoped
		/// processor instance, otherwise we demand it from the container (which may throw if none is registered).
		/// </remarks>
		/// <param name="container">Optional (nullable) DI container from which we acquire scoped processors</param>
		/// <returns>An instance of ITaskProcessor{T} that can process the task.</returns>
		private U CreateTaskProcessor(IContainer container)
		{
			// NOTE: If we have a nested DI container, defer to it for the ITaskProcessor instance.
			//       If there is no nested container, or it doesn't have a ITaskProcessor registered,
			//       instantiate one one locally.
			return container?.GetInstance<U>() ?? _createProcessor.Invoke();
		}

		protected override void UpdateFailure(T task, string message)
		{
			SendTaskStatusUpdate(task, () =>
			{
				task.LastErrorMessage = message;
				task.LastProcessedDate = DateTime.UtcNow;
				//this will requeue it if needed
				task.QueueItemType = QueueItemType.Ack;
			}, () =>
			{
				/*
				 * Again errors should pause the movement of the items.
				 * For this worker.
				 */
				if (_errorCount++ % Configuration.GetInt32AppSetting("ErrorCountSleepMod", 100) == 0)
				{
					Thread.Sleep(SleepMilliSeconds());
				}
			});
		}

		protected override void UpdateUnlock(T task, string message)
		{
			SendTaskStatusUpdate(task, () =>
			{
				task.LastErrorMessage = message;
				task.LastProcessedDate = DateTime.UtcNow;
				//task will requeue it
				task.QueueItemType = QueueItemType.Task;
			});
		}

		protected override void UpdateSuccess(T task, string message)
		{
			SendTaskStatusUpdate(task, () =>
			{
				task.QueueItemType = QueueItemType.Completed;
			});
		}

		private void SendTaskStatusUpdate(T task, Action beforeSend, Action afterSend = null)
		{
			using (LogContext.Set(task.CorrelationId))
			{
				if (_standAlone) return;        // short-circuit any communication message based services

				beforeSend();

				string sendMachine = task.SendMachine; // Send() changes the SendMachine property, so store it here in case we need to restore it after an exception.

				using (IMessageQueueWrapper adminQueue = MessageQueueWrapperFactory.CreateInstance<T>(GetAdminQueuePath(task.SendMachine)))
				{
					try
					{
						adminQueue.Send(task);
					}
					catch (IOException ioe)
					{
						_log.WarnNoPII($"Detected IOException.  Will retry once.", ioe);

						adminQueue.ReSend(task);
					}
					catch (Exception)
					{
						// Restore original SendMachine, for the sake of the UpdateFailure() call from AbstractServiceQueue.
						task.SendMachine = sendMachine;
						throw;
					}
				}

				if (afterSend != null)
				{
					afterSend();
				}
			}
		}

		/// <summary>
		/// Crate a nested DI container for the user context (and potentially other request-scoped DI state).
		/// </summary>
		/// <remarks>
		/// Introduced as part of ATL-72998 to add support for feature flag integration, which requires access to
		/// the request-scoped Account/User identity for flag resolution decisions.
		/// </remarks>
		/// <param name="atlasContext">Atlas request context for this request/task</param>
		/// <returns>Disposable DI container for this request-scoped state</returns>
		private IContainer CreateNestedContainer(IAtlasContext atlasContext)
		{
			var nestedContainer = _container.GetNestedContainer();
			try
			{
				if (atlasContext != null)
				{
					var userContextSetter = nestedContainer.TryGetInstance<IUserContextSetter>();
					if (userContextSetter != null)
					{
						userContextSetter.AccountId = atlasContext.AccountId;
						userContextSetter.UserId = atlasContext.UserAccountUid; // this may be null, but that's ok
					}
					else
					{
						// Should we have a specific OT telemetry event for this condition? It's most like incorrectly configured DI setup, but may not be a defect.
						_log.WarnNoPII($"Unable to create or acquire IUserContextSetter - transactionId {atlasContext.TransactionId} will proceed without scoped user context.");
					}
				}

				var taskContextSetter = nestedContainer.TryGetInstance<ITaskTypeContextSetter>();

				if (taskContextSetter != null)
				{
					taskContextSetter.TaskType = _taskType;
				}
				else
				{
					_log.WarnNoPII($"Unable to create or acquire ITaskTypeContextSetter - {TypeName} will proceed without scoped task type context.");
				}

				return nestedContainer;
			}
			catch (Exception)
			{
				nestedContainer?.Dispose();  // avoid leaking nested container
				throw;
			}
		}

		public virtual int StandardSleepSeconds => 5;
		protected override int SleepSeconds()
		{
			return Configuration.GetInt32AppSetting(TypeName + "_SleepSeconds", StandardSleepSeconds);
		}

		public virtual int StandardWorkerCount => DEFAULT_WORKER_COUNT;

		public override int DesiredWorkerCount()
		{
			return DesiredWorkerCount(out _);
		}

		private int DesiredWorkerCount(out double multiplier)
		{
			multiplier = 0;
			int count = -1;
			if (IsOneConfigEnabledForWorkerCount())
			{
				var mqTasksconfigs = ServiceLocator.GetInstance<IWatchedSingletonConfig<IMqTasksConfig>>()?.Value;
				var taskConfigs = mqTasksconfigs?.TaskConfigs;
				if (taskConfigs != null && taskConfigs.TryGetValue(TypeName, out var taskConfig))
				{
					count = taskConfig.WorkerCount;
					multiplier = taskConfig.WorkerMultiplier;
				}
				else
				{
					_log.InfoNoPII($"Failed to retrieve task configuration from INI file for TaskName: {TypeName}. Falling back to legacy processing.");
				}
			}
			if(count < 0)
			{
				// Rely on appsetting if oneConfig is disabled or if it cannot retrive from oneconfig
				count = Configuration.GetInt32AppSetting(TypeName + DESIRED_WORKER_COUNT, StandardWorkerCount);
				multiplier = Configuration.GetDoubleAppSetting(TypeName + DESIRED_WORKER_MULTIPLIER, DEAFULT_CORE_TO_WORKER_MULTIPLIER);
			}

			// we still want to be able to force the count to zero in order to turn off a worker group effectively
			// let's check the worker count based on the number of logical cores and a configured multiplier
			if (count != 0 && multiplier > 0)
			{
				// use the number of logical cores and a multiplier to determine a good worker count number.
				int numCores = Environment.ProcessorCount;
				count = Math.Max((int)(numCores * multiplier), 1);  // we really never want a > 0 multiplier to cause a zero worker count
			}
			return count;
		}

		private bool IsOneConfigEnabledForWorkerCount()
		{
			bool isOneConfigEnabled = false;
			if(ConfigWrapper.UseOneConfigMqTasksConfig)
			{
				var appconfigs = ServiceLocator.GetInstance<IWatchedSingletonConfig<IAppConfig>>()?.Value;
				if (Configuration.GetBooleanAppSetting("UseOneConfigMqTasks_" + appconfigs?.AppName, true))
				{
					isOneConfigEnabled = true;
				}
			}
			return isOneConfigEnabled;
		}

		private IEnumerable<string> DispatcherMachines()
		{
			return StringUtil.Split('|', Configuration.GetStringAppSetting(TypeName + TASK_DISPATCHERS, Configuration.GetStringAppSetting(DEFAULT_TASK_DISPATCHERS, GetMachineName())));
		}

		private string GetAdminQueuePath(string machine)
		{
			return string.Format(Configuration.GetStringAppSetting(TypeName + "_EventMQPathFormat", DefaultQueue.GetAdminMqPathFormat(TypeName, TaskUtilities.GetMessageBrokerTypeByTaskType(TypeName))), machine ?? GetMachineName());
		}

		private Activity StartActivity(T task, Guid correlationToken) =>
			ServiceInstrumentation.ActivitySource
				?.StartActivity(
					task.Label ?? $"{nameof(ADEXS.Services.MessageWorkerService)}",
					ActivityKind.Consumer,
					parentContext: correlationToken.ToActivityContext())
				?.SetStatus(ActivityStatusCode.Error)
				?.SetTag(TraceSemanticConventions.AttributeMessageType, TraceSemanticConventions.MessageTypeValues.Received)
				?.SetTag(ServiceTraceSemanticConventions.AttributeDocuSignMessageTaskName, TypeName)
				?.SetTag(ServiceTraceSemanticConventions.AttributeDocuSignMessageQueueType, ServiceTraceSemanticConventions.DocuSignMessageQueueTypeWorker)
				?.SetQueueTaskTags(task);
	}
}

