		/// <summary>
		/// Loads (and locks) an existing instance.  Fails if the instance doesn't exist or can't be locked.
		/// </summary>
		public static WorkflowInstance LoadExisting(int accountId, Guid instanceUid, IWINSDataStore data)
        {
	        DateTime lockExpirationDate;
            InstancePersistence.LockResult lockResult = data.LockInstance(accountId, instanceUid, out lockExpirationDate);
            switch (lockResult)
            {
                case InstancePersistence.LockResult.AlreadyCompleted:
                    throw new InstanceAlreadyCompletedException(string.Format("InstanceUid {0} is already completed", instanceUid));
                case InstancePersistence.LockResult.DoesNotExist:
                    throw new ApplicationException(string.Format("Invalid instanceUid {0}", instanceUid));
                case InstancePersistence.LockResult.LockedByOther:
                    throw new InstanceLockedException(string.Format("InstanceUid {0} is locked by another process", instanceUid));
                case InstancePersistence.LockResult.Locked:
                    // This is our success condition
                    break;
                default:
                    throw new ApplicationException(string.Format("Unhandled lockResult for instanceUid {0}: {1}", instanceUid, lockResult));
            }
            Instance instance = data.LoadInstanceByUid(accountId, Constants.WorkflowUser, instanceUid);
            if (instance == null) throw new ApplicationException(string.Format("Failed to load workflow instanceUid {1} for account {0}.", accountId, instanceUid));

			try
            {
                if (instance.ScopeUid != Guid.Empty)
                {
                    Scope scope = ServiceLocator.GetInstance<IDocumentScopeRepository>().GetScopeByUid(accountId, instance.ScopeUid);
                    if (scope != null)
                        instance.ScopeName = scope.Name;
                }
            }
            catch (Exception ex)
            {
                // the failure to load the scope should not cause the failure to load the instance, log it and move on
                _log.WarnNoPII(string.Format("Could not load the scope '{0}' for workflow instance '{1}'.", instance.ScopeUid, instanceUid), ex);
            }

			Revision revision = data.LoadRevisionByUid(accountId, Constants.WorkflowUser, instance.RevisionUid);
            if (revision == null) throw new ApplicationException(string.Format("Failed to load workflow definition version {1} for account {0}.", accountId, instance.RevisionUid));
            // ATL-68146: Populate RevisionCreatedDate here, because LoadInstanceByUid() doesn't populate it.
            instance.RevisionCreatedDate = revision.CreatedDate;
            WorkflowMap workflowMap = null;
            instance.DefinitionUid = revision.DefinitionUid;

			try
            {
	            workflowMap = ServiceLocator.GetInstance<IDefinitionReaderFactory>().GetDefinitionReaderFromRevision(revision).BuildWorkflowMap(revision);
            }
            catch (InvalidDesignDefinitionException ex)
            {
	            try
	            {
		            new WorkflowInstance(instance, data).Abort(UserAccount.WorkflowUserAccount(accountId), ex.Message);
	            }
				catch(Exception e)
				{
					_log.WarnNoPII($"Error aborting workflow instance '{instanceUid}' with invalid definition", e);
				}

	            throw;
            }

            var workflowInstance = new WorkflowInstance(instance, workflowMap, data);
	        workflowInstance.LockExpirationDate = lockExpirationDate;
	        return workflowInstance;

        }

        private readonly IWINSDataStore _data;
