		/// <summary>
		///
		/// </summary>
		/// <param name="previousActivity"></param>
		/// <param name="linkName"></param>
		/// <param name="localContextId"></param>
		/// <param name="followEmptyPathByDefault"></param>
		/// <param name="useLinkName">when true, uses linkName as the InputActivityOutput instead of getting it from previousActivity.Output</param>
		public void QueueActivitiesByLink(InstanceActivity previousActivity, string linkName, int localContextId = 0, bool followEmptyPathByDefault = true, bool useLinkName = false)
        {
            IEnumerable<WorkflowMap.Link> links;
            bool found = false;
            if (_workflowMap.TryGetLinks(previousActivity.Name, linkName, out links))
            {
                foreach (WorkflowMap.Link link in links)
                {
                    QueueActivityByName(previousActivity, link.ToName, localContextId, linkName, useLinkName);
                    found = true;
                }
            }
            // If we got no matches, try to go for the default (empty) output.
            if (followEmptyPathByDefault && !found && !string.IsNullOrEmpty(linkName)) QueueActivitiesByLink(previousActivity, string.Empty, localContextId);
        }

        /// <summary>
        ///
        /// </summary>
        /// <param name="previousActivity"></param>
        /// <param name="name"></param>
        /// <param name="localContextId"></param>
        /// <param name="linkName"></param>
        /// <param name="useLinkName">when true, uses linkName as the InputActivityOutput instead of getting it from previousActivity.Output</param>
        public void QueueActivityByName(InstanceActivity previousActivity, string name, int localContextId = 0, string linkName = null, bool useLinkName = false)
        {
            IWINSActivity activity;
            if (!_workflowMap.TryGetActivity(name, out activity)) throw new ApplicationException(string.Format("InstanceUid {0}: Tried to execute invalid activity '{1}'", Uid, PII.Encode(ApplicationSettings.PIIHashingStrategy, name)));

            if (localContextId <= 0) localContextId = (previousActivity == null ? int.MinValue : previousActivity.LocalContextId);

            // Create a new activity
            InstanceActivity instanceActivity = new InstanceActivity
            {
                AccountId = _instance.AccountId,
                InstanceUid = _instance.Uid,
                Name = name,
                ActivityTypeName = activity.LegacyElementName(),
                Status = Enums.WorkflowStepStatus.Pending,
                InputActivityOutput = useLinkName ? linkName : (previousActivity == null ? string.Empty : previousActivity.Output),
                InputActivityUid = (previousActivity == null ? Guid.Empty : previousActivity.Uid),
                LocalContextId = localContextId,
                IsWinsActivity = true,
                CreatedDate = DateTime.UtcNow //make sure that the in memory copy has a value
            };

            try
            {
                _data.CreateActivity(instanceActivity);
            }
            catch (Exception ex)
            {
                _log.ErrorWithPII($"Failed to queue activity {{0}} for instance {_instance?.Uid} in account {_instance?.AccountId}", ex, name);
                _data.ActivityFailure(_instance, instanceActivity);
            }

            _unfinishedActivities[instanceActivity.Uid] = instanceActivity;
            QueueActivity(instanceActivity);
        }

        public void QueueEvent(Event e)
        {
            if (_instance.Status != Enums.WorkflowStepStatus.Failed && _instance.Status != Enums.WorkflowStepStatus.Aborted)
            {
                lock (_lockObject)
                {
                    Queue<Event> queue = (e.IsControlEvent ? _queuedControlEvents : _queuedEvents);
                    if (e.Uid != Guid.Empty)
                    {
                        // Ensure this item isn't already in the list
                        if (queue.Any(e2 => e2.Uid == e.Uid)) return;
                    }
                    queue.Enqueue(e);
                    WorkflowEventWorkload wrk = new WorkflowEventWorkload(e, "EventQueued")
					{
						RevisionUid = _instance.RevisionUid,
						WorkflowDB = DataStore.GetDbNameForAccount(e.AccountId)
					};
                    wrk.Log();
	            }
            }
        }

        public void QueueIncompleteActivities()
        {
            InstanceActivities activities = _data.LoadIncompleteActivitiesByInstanceUid(AccountId, Constants.WorkflowUser, Uid);
            if (activities == null || activities.Count == 0) return;
            lock (_lockObject)
            {
                foreach (InstanceActivity activity in activities) _unfinishedActivities[activity.Uid] = activity;
            }

        }

        public bool CheckActivityForCompletion(InstanceActivity instanceActivity)
        {
            // We only care about Waiting steps.
            if (instanceActivity.Status != Enums.WorkflowStepStatus.Waiting) return false;

            IWINSActivity activity = _SafeGetActivity(instanceActivity);
            if (activity == null) throw new ApplicationException(string.Format("Cannot find expected activity '{0}' in workflow map for instanceUid '{1}'.", instanceActivity.ActivityTypeName, Uid));
            using (ScmNDC.Push(BuildNDCTags(instanceActivity)))
            {
	            _log.DebugNoPII($"CheckActivityForCompletion called for ActivityUid {instanceActivity.Uid} ({instanceActivity.Name}).");
	            try
	            {
		            IWINSWorkflowContext ctx = _CreateContext(activity, instanceActivity);
		            ctx.CheckForCompletion();
		            if (instanceActivity.Status == Enums.WorkflowStepStatus.Completed)
		            {
			            _data.ActivityCompleted(instanceActivity, (activity is AbstractHumanActivity));
			            SaveInstanceActivityVariables(instanceActivity, ctx);

						RemoveProperties(instanceActivity, activity);

						if (!ShouldUseScopeEventHandler()) CompleteScopeStep(instanceActivity, activity, ctx);

						activity.ActivityFinished(ctx);

						QueueActivitiesByLink(instanceActivity, instanceActivity.Output);
			            _unfinishedActivities.Remove(instanceActivity.Uid);
			            return true;
		            }
                    _log.DebugWithPII($"CheckActivityForCompletion completed for Activity ({{0}}) in status: {instanceActivity.Status}.", instanceActivity.Name);
	            }
				catch (ExpressionApiRequestThrottledException expressionApiThrottledException)
				{
					expressionApiThrottledException.WorkflowThrottledStatus = Enums.WorkflowThrottledStatus.ThrottledDueToExpressionRateLimit;
					PauseWhenThrottled(instanceActivity, expressionApiThrottledException);
					return false;
				}
				catch (SmallBenApiRequestThrottledException smallBenApiThrottledException)
				{
					smallBenApiThrottledException.WorkflowThrottledStatus = Enums.WorkflowThrottledStatus.ThrottledDueToExpressionRateLimit;
					PauseWhenThrottled(instanceActivity, smallBenApiThrottledException);
					return false;
				}
				catch (Exception e)
	            {
                    _log.ErrorNoPII($"CheckActivityForCompletion failed.", e);
	            }
            }
            return false;
        }

        public bool DoNextTask()
        {
	        LastProcessDate = DateTime.UtcNow;

	        CheckAndHandleInstanceStateChanges();

			// If we're in a non-processing state, we want to respond only to control events.
			// If we have no control events, we can just skip everything else.
			if (!_ShouldProcessEvent())
	        {
		        return _ExecuteFirstEvent(controlOnly: true);
	        }

	        // If we have any queued events, do one of those.
	        if (_ExecuteFirstEvent(controlOnly: false)) return true;

	        if (!_ShouldProcess()) return false;

	        // Otherwise, check to see if any activities need to be executed; if so, do those.
	        if (_ExecuteFirstActivity()) return true;

	        // If we get here, it might be that we have some unfinished activities in the database that for some reason weren't loaded into memory.
	        // Check for that, and, if so, load them now.
	        QueueIncompleteActivities();
	        // Try to execute an activity again -- we may have just loaded some
	        if (_ExecuteFirstActivity()) return true;
	        // If we can complete a waiting activity, do that.
	        if (_CheckWaitingActivities()) return true;

	        // Otherwise, we've got nothing to do and can quit.
	        if (!HasQueuedWork && !HasUnfinishedActivities)
	        {
		        _MarkCompleted();
				// If we processed nothing (the activity count is 0) we don't want to mark ourselves as waiting.  This is mostly because
				// we don't want to update our UpdatedDate (see ATL-20570).  This case occurs when our workflow is already waiting and has been
				// picked up for the day and each activity checked to see if it's done waiting.  We don't want this to trigger an update if
				// nothing has actually changed.
	        }
	        else if (_inMemoryActivityCount > 0 || _instance.Status != Enums.WorkflowStepStatus.Waiting)
	        {
				var workflowConfig = WorkflowConfig(_instance.AccountId);
				var enableSmallBenRateLimits = workflowConfig?.EnableSmallBenRateLimits ?? false;

				if (_instance.Status == Enums.WorkflowStepStatus.Paused
				    && ((string.Equals(ADEXS.Core.Workflow.Infrastructure.FeatureFlags.WorkflowFeatureFlags.EnableExpressionApiRateLimit, "live", StringComparison.OrdinalIgnoreCase)
				         && _instance.WorkflowThrottledStatus == Enums.WorkflowThrottledStatus.ThrottledDueToExpressionRateLimit)
					|| (enableSmallBenRateLimits && _instance.WorkflowThrottledStatus == Enums.WorkflowThrottledStatus.ThrottledDueToSmallBenRateLimit)))
				{
					// keep throttled workflows in paused status
					return false;
				}
				_MarkWaiting();
			}

		    return false;
        }

        public void ExecuteActivity(InstanceActivity instanceActivity)
        {
	        LastProcessDate = DateTime.UtcNow;

			using var span = StartActivity()
				?.SetTag(ServiceTraceSemanticConventions.AttributeDocuSignWorkflowActivityUid, instanceActivity.Uid);

            IWINSActivity activity = _SafeGetActivity(instanceActivity);
            if (activity == null) throw new ApplicationException(string.Format("Cannot find expected activity {0} in workflow map for instanceUid {1}", instanceActivity.ActivityTypeName, Uid));

            using (ScmNDC.Push(BuildNDCTags(instanceActivity)))
            {
	            _log.InfoWithPII($"ExecuteActivity called for Activity ({{0}}).", instanceActivity.Name);
	            IWINSWorkflowContext ctx = null;
	            Stopwatch sw = Stopwatch.StartNew();
	            try
	            {
		            _lastActivityUid = instanceActivity.Uid;

					if (!_ShouldProcess()) throw new ApplicationException(string.Format("InstanceUid {0} is in an invalid state to execute activity {1}", Uid, PII.Encode(ApplicationSettings.PIIHashingStrategy, instanceActivity.Name)));

		            // Check for runaway workflows.
		            if (++_inMemoryActivityCount >= MAX_IN_MEMORY_ACTIVITY_COUNT)
		            {
			            _log.WarnNoPII(string.Format("In-memory activity count exceeded: {0} activities without entering into a Waiting state", _inMemoryActivityCount));
			            throw new PublicException(PublicException.FailReason.InvalidState, string.Format("In-memory activity count exceeded: {0} activities without entering into a Waiting state", _inMemoryActivityCount));
		            }

					// Governance, check if activity execution limit has been reached
					if (PauseWhenActivityExecutionLimitReached())
					{
						// In this case, the workflow is paused, purge date is set and an e-mail has been sent to support team.
						// The current activity will not be executed, unless the limits are increased and the workflow resumed.
						return;
					}

					_data.ActivityExecuting(_instance, instanceActivity);

		            ctx = _CreateContext(activity, instanceActivity);

					if (!ShouldUseScopeEventHandler() && IsTrackItActivity(activity))
					{
						FireWorkflowScopeEvent(null, ScopeEventType.StepInitiated, activity, ctx);
					}

					activity.Execute(ctx);
		            _ActivityWorkCompleted(instanceActivity, activity, ctx, sw);
					span?.SetStatus(ActivityStatusCode.Ok);
	            }
				catch (ExpressionApiRequestThrottledException expressionApiThrottledException)
				{
					expressionApiThrottledException.WorkflowThrottledStatus = Enums.WorkflowThrottledStatus.ThrottledDueToExpressionRateLimit;
					PauseWhenThrottled(instanceActivity, expressionApiThrottledException);
					return;
				}
				catch (SmallBenApiRequestThrottledException smallBenApiThrottledException)
				{
					smallBenApiThrottledException.WorkflowThrottledStatus = Enums.WorkflowThrottledStatus.ThrottledDueToSmallBenRateLimit;
					PauseWhenThrottled(instanceActivity, smallBenApiThrottledException);
					return;
				}
				catch (Exception e)
	            {
					if (e is ArgumentNullException && ctx == null)
					{
						var message =
				            string.Format(Resources.WINSResources.ActionFailed_ReferenceID_0, Guid.NewGuid()) +
				            $". Inst: {instanceActivity.AccountId} '{instanceActivity.InstanceUid}'.";
			            _log.ErrorNoPII(message, e);
						_data.CreateInstanceHistory(instanceActivity.AccountId, instanceActivity.InstanceUid, instanceActivity.Uid, message, true);
		            }

					// Oh my...
					// Look at this catch block.
					bool isTransient = e is TransientException;
		            bool useFailureOutput = ctx != null && activity.HasFailureOutput;

		            if (isTransient) throw new TransientException(e);
		            if (useFailureOutput)
		            {
						ctx.ActivityVariables.Set("Failure", e.Message);
						ctx.MarkAsCompleted(AbstractActivity.OUTPUT_FAILURE);
			            _ActivityWorkCompleted(instanceActivity, activity, ctx, sw);
		            }
		            else
		            {
			            _data.ActivityFailure(_instance, instanceActivity);
			            if (ctx == null) ctx = _CreateContext(activity, instanceActivity);
						if (!ShouldUseScopeEventHandler()) CompleteScopeStep(instanceActivity, activity, ctx);
						activity.ActivityFinished(ctx);

						try
			            {
				            if (_instance.Status == Enums.WorkflowStepStatus.Failed)
				            {
								if (ShouldUseScopeEventHandler())
								{
									InstanceHandler.Fail(ctx, activity, GetUnfinishedWINSWorkflowContextsWINSActivities(instanceActivity.EndDate));
								}
								else
								{
									WorkflowScopeEvent.ScopeEventInfo scopeEventData = new WorkflowScopeEvent.ScopeEventInfo()
									{
										AccountId = ctx.AccountId,
										ScopeUid = ctx.ScopeUid,
										ScopeName = ctx.ScopeName,
										StepName = instanceActivity.Name,
										StepDisplayName = GetActivityDisplayName(activity, ctx),
										StepTypeName = instanceActivity.ActivityTypeName,
										StepUid = instanceActivity.Uid,
										PeriodStart = instanceActivity.EndDate,
										EventType = (int)ScopeEventType.WorkflowFailed,
										Output = instanceActivity.Output,
										WorkflowUid = ctx.WorkflowInstanceUid,
										WorkflowName = ctx.WorkflowName
									};

									WorkflowScopeEvent scopeEvent = new WorkflowScopeEvent(ctx.AccountId, ctx.WorkflowInstanceUid)
									{
										EventData = scopeEventData
									};
									EventRepo.FireEvent(ctx.WorkflowUserAccount.Account, scopeEvent);

									AbortOrFailScopeStep(Enums.WorkflowStepStatus.Failed, instanceActivity.EndDate);
								}
							}
			            }
			            catch (Exception ex)
			            {
				        _log.ErrorNoPII($"Error in saving scope event {ctx.ScopeUid} for eventtype {ScopeEventType.WorkflowFailed}", ex);
			            }

			            _unfinishedActivities.Remove(instanceActivity.Uid);
			            _data.FireMessageQueueEvent(new WorkflowEvent(AccountId, _instance.Uid, (int)_instance.Status));
		            }
	            }
	            finally
	            {
		            ActivityExecutionCount++;
	            }
			}
        }

        public void ExecuteCallback(InstanceActivity instanceActivity, string name, object value)
        {
            LastProcessDate = DateTime.UtcNow;

			using var span = StartActivity()
				?.SetTag(ServiceTraceSemanticConventions.AttributeDocuSignWorkflowActivityUid, instanceActivity?.Uid);

            IWINSActivity activity = _SafeGetActivity(instanceActivity);
            if (activity == null) return;

            using (ScmNDC.Push(BuildNDCTags(instanceActivity)))
            {
	            _log.InfoWithPII($"ExecuteCallback called for Activity ({{0}}) with callback {name}.", instanceActivity.Name);

	            if (!(_ShouldProcess(instanceActivity) && _ShouldProcessEvent()))
	            {
		            if (instanceActivity.Status == Enums.WorkflowStepStatus.Completed)
		            {
						_log.InfoWithPII($"ExecuteCallback did not complete because the activity was already completed. Activity {{0}} is in an invalid state to process callback {name}. Activity status: {instanceActivity.Status}. Instance status: {Model.Status}", instanceActivity.Name);
		            }
		            else
		            {
			            _log.ErrorWithPII($"InstanceUid {Uid} is in an invalid state to process callback {name}. Activity status: {instanceActivity.Status}.  Instance status: {Model.Status}");
					}
		            return;
				}

	            IWINSWorkflowContext ctx = _CreateContext(activity, instanceActivity);

				try
		        {
					Stopwatch sw = Stopwatch.StartNew();
		            activity.ExecuteCallback(ctx, name, value);

		            if (_instance.Status != Enums.WorkflowStepStatus.Aborted)
		            {
			            _ActivityWorkCompleted(instanceActivity, activity, ctx, sw);
		            }

		            span?.SetStatus(ActivityStatusCode.Ok);
	            }
				catch (ExpressionApiRequestThrottledException expressionApiThrottledException)
				{
					expressionApiThrottledException.WorkflowThrottledStatus = Enums.WorkflowThrottledStatus.ThrottledDueToExpressionRateLimit;
					PauseWhenThrottled(instanceActivity, expressionApiThrottledException);
					return;
				}
				catch (SmallBenApiRequestThrottledException smallBenApiThrottledException)
				{
					smallBenApiThrottledException.WorkflowThrottledStatus = Enums.WorkflowThrottledStatus.ThrottledDueToSmallBenRateLimit;
					PauseWhenThrottled(instanceActivity, smallBenApiThrottledException);

					// Throw so that the calling method will handle re-queuing the failed event
					// When resumed we will retry the recurring reminder callback
					throw;
				}
	            catch (Exception e)
	            {
					// Note that we don't fail the workflow here -- see ATL-21019
					if (instanceActivity.Status == Enums.WorkflowStepStatus.Completed ||
		                Model.Status == Enums.WorkflowStepStatus.Completed)
		            {
						_log.InfoNoPII($"The callback is unnecessary: workflow activity '{instanceActivity.Uid}' [{instanceActivity.Status}] or instance '{instanceActivity.InstanceUid}' [{Model.Status}] has already completed.");
		            }
		            else
		            {
						var pe = e as PublicException;
						_log.ErrorNoPII($"ExecuteCallback failed.", pe?.InnerException ?? e);
		            }
	            }
            }
        }

        public void ExecuteEvent(Event e, bool queueOnFailure = true)
        {
            LastProcessDate = DateTime.UtcNow;
            if (e == null) return;

            using var span = StartActivity();

            using (ScmNDC.Push(BuildNDCTags(e)))
            {
	            // If we have Uid but the lock expired we don't want to process the event as it was persisted and some other worker probably has already picked it.
	            if (e.Uid != Guid.Empty && e.LockExpirationDate <= LastProcessDate)
	            {
		            _log.InfoNoPII($"Aborting event execution - ExecuteEvent called with an event that has an expired lock ({e.LockExpirationDate}) for callback {e.CallbackName}.");
					span?.SetStatus(ActivityStatusCode.Ok);
		            return;
	            }

	            _log.InfoNoPII($"ExecuteEvent called with callback {e.CallbackName}.");
	            try
	            {
		            Stopwatch sw = Stopwatch.StartNew();
		            if (e.ActivityUid == Guid.Empty)
		            {
			            // We expect these to be control codes
			            string comment = (e.CallbackObject == null ? "" : e.CallbackObject.ToString());
			            switch (e.CallbackName)
			            {
				            case Event.ControlNames.Abort:
					            Abort(e.BPMUser, comment);
					            break;
							case Event.ControlNames.PauseForAccountCancel:
								Pause(e.BPMUser, comment);
								SetPurgeDateForAccountCancel();
								break;
							case Event.ControlNames.PauseForRevisionSuspended:
								PauseForRevisionSuspended(e.BPMUser, comment);
								break;
							case Event.ControlNames.ResumeForSuspendedRevision:
								 ResumeForSuspendedRevision(e.BPMUser, comment);
								break;
							case Event.ControlNames.Pause:
								Pause(e.BPMUser, comment);
								break;
				            case Event.ControlNames.Resume:
					            Resume(e.BPMUser, comment);
					            break;
				            case Event.ControlNames.ResumeFromError:
					            ResumeFromError(e.BPMUser, comment);
					            break;
				            case Event.ControlNames.StartActivity:
					            QueueActivityByName(null, comment);
					            break;
				            default:
					            throw new ApplicationException(string.Format("Unhandled event {0}", e.CallbackName));
			            }
		            }
		            else
		            {
			            // These *may* be control codes.  If they're not control codes, then
			            // we expect them to be a callback to an activity.
			            InstanceActivity activity;
			            if (!_unfinishedActivities.TryGetValue(e.ActivityUid, out activity)) activity = _data.LoadActivityByUid(_instance.AccountId, Constants.WorkflowUser, e.ActivityUid);
			            if (activity == null || activity.InstanceUid != e.InstanceUid) throw new ApplicationException(string.Format("Invalid ActivityUid {0} for InstanceUid {1}", e.ActivityUid, e.InstanceUid));
			            switch (e.CallbackName)
			            {
				            // TODO: Start specific steps, fire specific outputs, complete specific steps, etc.
				            default:
					            ExecuteCallback(activity, e.CallbackName, e.CallbackObject);
					            break;
			            }
		            }
		            if (e.Uid != Guid.Empty) _data.EventSuccess(e.AccountId, e.Uid);
					WorkflowEventWorkload wrk = new WorkflowEventWorkload(e, "EventExecuted")
					{
						TotalMs = sw.ElapsedMilliseconds,
						RevisionUid = _instance.RevisionUid, WorkflowDB = DataStore.GetDbNameForAccount(e.AccountId)
					};
		            wrk.Log();

		            span?.SetStatus(ActivityStatusCode.Ok);
	            }
	            catch (Exception excp)
	            {
		            _log.ErrorNoPII($"Error processing eventUid {e.Uid} ({e.CallbackName}).", excp);
		            if (!queueOnFailure) throw;
		            if (e.Uid == Guid.Empty)
		            {
			            e.NextProcessDate = _data.NextProcessDate();
			            e.ErrorCount = 1;
			            e.LastErrorMessage = excp.Message;
			            _data.CreateEvent(e);
			            WorkflowEventWorkload wrk = new WorkflowEventWorkload(e, "EventCreatedOnExecuteException")
						{
							RevisionUid = _instance.RevisionUid,
							WorkflowDB = DataStore.GetDbNameForAccount(e.AccountId)
						};
			            wrk.Log();
		            }
		            else
		            {
			            _data.EventFailure(e.AccountId, e.Uid, _data.NextProcessDate(), excp.Message);
		            }
	            }
            }
        }

        public void Abort(UserAccount user, string comment)
        {
            LastProcessDate = DateTime.UtcNow;
            lock (_lockObject)
            {
                foreach (InstanceActivity activity in _unfinishedActivities.Values)
                {
	                try
	                {
		                IWINSActivity winsActivity = _SafeGetActivity(activity);
		                IWINSWorkflowContext ctx = _CreateContext(winsActivity, activity);
		                winsActivity.OnActivityAborted(ctx);
	                }
	                catch (Exception ex)
	                {
		                _log.ErrorNoPII(string.Format("Failed OnActivityAborted AccountId {0}, ActivityUid {1}", user.AccountId, activity.Uid), ex);
					}
                }
                new WorkflowControlInstance(Model).Abort(user, comment);

				if (ShouldUseScopeEventHandler())
				{
					InstanceHandler.Abort(_instance, user, GetUnfinishedWINSWorkflowContextsWINSActivities(DateTime.UtcNow));
				}
				else
				{
					AbortOrFailScopeStep(Enums.WorkflowStepStatus.Aborted, DateTime.UtcNow);
					FireWorkflowScopeEvent(user, ScopeEventType.WorkflowAborted);
				}

				_ForceReload();
            }
        }

        public int SetPurgeDateForAccountCancel()
        {
	        LastProcessDate = DateTime.UtcNow;
	        lock (_lockObject)
	        {
		        var daysUntilPurge = new WorkflowControlInstance(Model).SetPurgeDateForAccountCancel();
		        _ForceReload();
				return daysUntilPurge;
	        }
        }

        public void ClearPurgeDate()
        {
	        LastProcessDate = DateTime.UtcNow;
	        lock (_lockObject)
	        {
		        new WorkflowControlInstance(Model).ClearPurgeDate();
		        _ForceReload();
	        }
        }

		public void Pause(UserAccount user, string comment)
		{
			_log.InfoNoPII($"Pause - Pausing");
			PauseInstance(user, comment, new WorkflowControlInstance(Model).Pause);
		}

		public void PauseForRevisionSuspended(UserAccount user, string comment)
		{
			if (Model.Status == Enums.WorkflowStepStatus.Paused)
			{
				_log.InfoNoPII($"PauseForRevisionSuspended - No action taken, instance already paused");
				return;
			}

			_log.InfoNoPII($"PauseForRevisionSuspended - Pausing");
			PauseInstance(user, comment, new WorkflowControlInstance(Model).PauseForRevisionSuspended);
		}

		public void ResumeForSuspendedRevision(UserAccount user, string comment)
		{
			if (Model.Status != Enums.WorkflowStepStatus.Paused)
			{
				_log.InfoNoPII($"ResumeForSuspendedRevision - No action taken, instance not paused. Status:{Model.Status}");
				return;
			}

			// Check if instance UpdatedBy has changed since the PauseForRevisionSuspended was invoked, if it has we do not want to do anything.
			if (Model.UpdatedBy != WorkflowControlInstance.PauseForSuspendedRevisionUpdatedBy)
			{
				_log.InfoNoPII($"ResumeForSuspendedRevision - No action taken, UpdatedBy is not {WorkflowControlInstance.PauseForSuspendedRevisionUpdatedBy}");
				return;
			}

			_log.InfoNoPII($"ResumeForSuspendedRevision - Resuming");
			Resume(user, comment);
			ClearPurgeDate();
		}

		public bool PauseWhenActivityExecutionLimitReached()
		{
			try
			{
				// Governance, check whether the number of executed activities exceeded the configured limit
				_isActivityExecutionLimitReached = Governor.IsActivityExecutionLimitReached(AccountId, Uid, _instance.RevisionUid, _instance.RevisionCreatedDate, ActivityExecutionCount, out _activityExecutionCountEnforcedLimit);

				if (_isActivityExecutionLimitReached)
				{
					_log.InfoNoPII($"Workflow instance '{Uid}' for account '{AccountId}' has exceeded the maximum limit (Actual={ActivityExecutionCount}, EnforcedLimit={_activityExecutionCountEnforcedLimit}) and will be paused.");

					Pause(UserAccount.WorkflowUserAccount(AccountId), "Workflow activity execution limit reached.");

					TrySendActivityExecutionLimitExceedNotification(SetPurgeDateForAccountCancel());
				}
			}
			catch (Exception e)
			{
				_log.ErrorNoPII($"Unable to pause instance '{Uid}' for account '{AccountId}' activity execution limit", e);
			}

			return _isActivityExecutionLimitReached;
		}

		public void PauseWhenThrottled(InstanceActivity activity, WorkflowApiRequestThrottledException exception)
		{
			Requires.NotNull(activity, nameof(InstanceActivity));
			Requires.NotNull(exception, nameof(WorkflowApiRequestThrottledException));

			if (exception.WorkflowThrottledStatus == Enums.WorkflowThrottledStatus.NotThrottled)
			{
				_log.InfoNoPII($"Workflow instance '{Uid}' for account '{AccountId}' is not throttled and will not be paused.");
				return;
			}

			try {
				activity.Status = Enums.WorkflowStepStatus.Paused;
				var throttledByApi = exception.ThrottledApiAsString();

				_log.InfoNoPII($"Workflow instance '{Uid}' for account '{AccountId}' has exceeded the maximum {throttledByApi} request limit and will be paused. Message: {exception.Message}");

				PauseInstance(UserAccount.WorkflowUserAccount(AccountId), $"{throttledByApi} requests limit reached.", new WorkflowControlInstance(Model).PauseForWorkflowRateLimitExceed);

				// Send notification
				switch (exception.WorkflowThrottledStatus)
				{
					case Enums.WorkflowThrottledStatus.ThrottledDueToExpressionRateLimit:
						TrySendExpressionRateLimitExceedNotification(SetPurgeDateForWorkflowRateLimit(), GetAdvancedWorkflowNotificationUserAccountUid());
						break;
					case Enums.WorkflowThrottledStatus.ThrottledDueToSmallBenRateLimit:
						TrySendSmallBenRateLimitExceedNotification(SetPurgeDateForWorkflowRateLimit(), GetAdvancedWorkflowNotificationUserAccountUid());
						break;
					default:
						_log.WarnNoPII("No notifications sent for unsupported throttle status");
						break;
				}

				// mark instance as throttled for current workflow run session, WorkflowThrottledStatus property is not persisted and refreshed on reload
				_instance.WorkflowThrottledStatus = exception.WorkflowThrottledStatus;
			}
#pragma warning disable CA1031
			catch (Exception e)
#pragma warning restore CA1031
			{
				_log.ErrorNoPII($"Unable to pause instance '{Uid}' for account '{AccountId}' rate limit request limit with status {exception?.WorkflowThrottledStatus}", e);
			}
		}

