        /// <summary>
        /// Called when we've finished work on an activity -- either executing it or performing a callback on it.
        /// </summary>
        private void _ActivityWorkCompleted(InstanceActivity instanceActivity, IWINSActivity activity, IWINSWorkflowContext ctx, Stopwatch sw)
        {
	        // If it's flagged as completed, update in the db, queue up any next activities, and remove it from our list.
            if (instanceActivity.Status == Enums.WorkflowStepStatus.Completed)
            {
                _data.ActivityCompleted(instanceActivity, (activity is AbstractHumanActivity));
                SaveInstanceActivityVariables(instanceActivity, ctx);

				RemoveProperties(instanceActivity, activity);

				if (!ShouldUseScopeEventHandler()) CompleteScopeStep(instanceActivity, activity, ctx);

				activity.ActivityFinished(ctx);

				QueueActivitiesByLink(instanceActivity, instanceActivity.Output);
				lock (_unfinishedActivities)
                {
                    if (_unfinishedActivities.ContainsKey(instanceActivity.Uid)) _unfinishedActivities.Remove(instanceActivity.Uid);
                }
            }
            else
            {
                // Otherwise, it's still running.  Flag it as waiting so it doesn't get processed again.
                instanceActivity.Status = Enums.WorkflowStepStatus.Waiting;
                _data.SaveInstanceActivity(instanceActivity);
            }

            _log.InfoWithPII($"_ActivityWorkCompleted done for Activity ({{0}}) in status: {instanceActivity.Status} ({sw.ElapsedMilliseconds} ms).", instanceActivity.Name);


            // log workload data
            try
            {
                WorkflowWorkload ww = new WorkflowWorkload();
                ww.Add(ctx);
                ww.TotalMs = sw.ElapsedMilliseconds;
				ww.StartTime = DateTime.UtcNow.AddMilliseconds(-ww.TotalMs);
                ww.Status = instanceActivity.Status.ToString();
                ww.Output = instanceActivity.Output;
                ww.ActivityCreatedDate = instanceActivity.CreatedDate;
                ww.ActivityStartDate = instanceActivity.StartDate;
                if (DateTime.MinValue < instanceActivity.EndDate && instanceActivity.EndDate < DateTime.MaxValue)
                    ww.ActivityEndDate = instanceActivity.EndDate;
                if (DateTime.MinValue < _instance.CreatedDate && _instance.CreatedDate < DateTime.MaxValue)
	                ww.InstanceCreatedDate = _instance.CreatedDate;
                ww.ActivityKind = activity.Kind.ToString();
                ww.RevisionUid = _instance.RevisionUid.ToString();
				ww.DefinitionUid = _instance.DefinitionUid.ToString();
				ww.WorkflowDB = DataStore.GetDbNameForAccount(instanceActivity.AccountId);

				ww.Log();
            }
            catch (Exception ex)
            {
                _log.WarnNoPII("Failed to log workload.", ex);
            }
        }

		private void CompleteScopeStep(InstanceActivity instanceActivity, IWINSActivity activity, IWINSWorkflowContext ctx)
		{
            try
            {
                if (IsTrackItActivity(activity))
                {
                    Documents documents = null;
                    if (ctx.TrackActivity())
                    {
                        documents = ((ITrackItActivity)activity).GetDocuments(ctx);
					ServiceLocator.GetInstance<IDocumentScopeRepository>().CompleteStep(ctx.AccountId, ctx.ScopeUid, instanceActivity.Uid,
                            documents, instanceActivity.Output, instanceActivity.Status,
                            instanceActivity.EndDate);
                    }

                    WorkflowScopeEvent.ScopeEventInfo scopeEventData = new WorkflowScopeEvent.ScopeEventInfo()
                    {
                        AccountId = ctx.AccountId,
                        Documents = documents?.Select(d => new WorkflowScopeEvent.DocumentDetail() { Uid = d.Uid, Name = d.Name }).ToList(),
                        ScopeUid = ctx.ScopeUid,
                        ScopeName = ctx.ScopeName,
                        StepName = instanceActivity.Name,
                        StepDisplayName = GetActivityDisplayName(activity, ctx),
                        StepTypeName = instanceActivity.ActivityTypeName,
                        StepUid = instanceActivity.Uid,
                        PeriodStart = instanceActivity.EndDate,
                        EventType = (int)ScopeEventType.StepFinished,
                        Output = instanceActivity.Output,
                        WorkflowUid = ctx.WorkflowInstanceUid,
                        WorkflowName = ctx.WorkflowName
                    };
                    WorkflowScopeEvent scopeEvent = new WorkflowScopeEvent(ctx.AccountId, ctx.WorkflowInstanceUid)
                    {
                        EventData = scopeEventData
                    };
                    EventRepo.FireEvent(ctx.WorkflowUserAccount.Account, scopeEvent);
                }
            }
            catch (Exception ex)
            {
                _log.ErrorNoPII($"Error in saving scope event {ctx.ScopeUid} for eventtype {ScopeEventType.StepFinished}", ex);
            }
        }

