                    default:
                        return false;
                }
            }
        }

        //ITrackItActivity interface is implemented by activities whose data is tracked in DocumentScope and ScopeEvent tables.
        private bool IsTrackItActivity(IWINSActivity activity)
        {
            return activity is ITrackItActivity;
        }

		private void FireWorkflowScopeEvent(UserAccount user, ScopeEventType eventType, IWINSActivity activity = null, IWINSWorkflowContext ctx = null)
		{
			try
			{
				WorkflowScopeEvent.ScopeEventInfo scopeEventData = new WorkflowScopeEvent.ScopeEventInfo
				{
					AccountId = _instance.AccountId,
					ScopeUid = _instance.ScopeUid,
					ScopeName = _instance.ScopeName,
					PeriodStart = DateTime.UtcNow,
					EventType = (int)eventType,
					WorkflowUid = _instance.Uid,
					WorkflowName = _instance.Name,
					EventBy = user == null ? null : new WorkflowScopeEvent.Assignee() { Email = user.EmailAddress, Name = user.FriendlyName, Uid = user.Uid }
				};

				if (activity != null && ctx != null)
				{
					scopeEventData.StepName = ctx.ActivityName;
					scopeEventData.StepDisplayName = GetActivityDisplayName(activity, ctx);
					scopeEventData.StepTypeName = ctx.ActivityTypeName;
					scopeEventData.StepUid = ctx.ActivityUid;
				}

				EventRepo.FireEvent(_instance.AccountId, new WorkflowScopeEvent(_instance.AccountId, _instance.Uid)
				{
					EventData = scopeEventData
				});
			}
			catch (Exception ex)
			{
				_log.ErrorNoPII($"Error in saving scope event scopeUid: {_instance.ScopeUid}, instanceUid: {_instance.Uid} eventType: {eventType}", ex);
			}
		}

		private void RemoveProperties(InstanceActivity instanceActivity, IWINSActivity activity)
	    {
		    // We need to delete properties if this is not a human activity or WaitForNextStepAfterCompletion is not set
		    if (activity is AbstractHumanActivity && ServiceLocator.GetInstance<IWorkflowSettingRepository>().GetWaitForNextStepAfterCompletion(instanceActivity.AccountId))
		    {
				_data.DeleteActivityPropertiesWithExclusion(instanceActivity.AccountId, instanceActivity.InstanceUid, instanceActivity.Uid, new List<string> { nameof(AbstractHumanActivity.WaitForNextStep) });
				_log.InfoNoPII("Removing activity properties except WaitForNextStep");
		    }
		    else
		    {
			    _data.DeleteActivityProperties(instanceActivity.AccountId, instanceActivity.InstanceUid, instanceActivity.Uid);
			    _log.InfoNoPII("Removing activity properties");
		    }
	    }

		private string[] BuildNDCTags(InstanceActivity instanceActivity) => instanceActivity != null ? new[] { $"ActivityId:{instanceActivity.Uid}",$"W:{instanceActivity.InstanceUid}" } : new string[] { };
	    private string[] BuildNDCTags(Event e) => e != null ? new[] { $"ActivityId:{e.ActivityUid}", $"W:{e.InstanceUid}" } : new string[] { };

		/// <summary>
		/// Try to send an e-mail to support when a workflow instance activity execution account exceeds configured threshold.
		/// </summary>
		/// <param name="pausedPurgeDays">Number of days to purge workflow related data</param>
		/// <returns>
		/// True	-> Success.
		/// False	-> Fail to send email.
		/// </returns>
		private bool TrySendActivityExecutionLimitExceedNotification(int pausedPurgeDays)
		{
			try
			{
				var emailRecipients = new List<string>();

				// Support should get the email
				var supportEmailAddress = WorkflowSettingRepository.GetSupportNotificationEmailAddress(AccountId);
				if (!string.IsNullOrEmpty(supportEmailAddress))
				{
					emailRecipients.Add(supportEmailAddress);
				}

				// Also send to AdvancedWorkflowNotificationUserAccountUid, if configured for account
				UserAccount notificationUser = null;
				var notificationUserUid = new AccountSettings(AccountId, Constants.WorkflowUser).AdvancedWorkflowNotificationUserAccountUid;
				if (notificationUserUid != Guid.Empty)
				{
					_log.InfoNoPII($"Loading {nameof(UserAccount)} {notificationUserUid} to receive activity execution limit exceeded notification");
					notificationUser = UserAccountRepository.Instance.LoadByUid(AccountId, notificationUserUid);
				}
				if (!string.IsNullOrEmpty(notificationUser?.EmailAddress))
				{
					emailRecipients.Add(notificationUser.EmailAddress);
				}

				if (!emailRecipients.Any())
				{
					_log.InfoNoPII($"Email support address setting is empty, and there is no AdvancedWorkflowNotificationUser configured. Thus, not sending activity execution limit exceeded notification for account '{AccountId}' and workflow instance '{Uid}'.");
					return false;
				}

				_log.InfoNoPII($"Sending activity execution limit exceeded notification to {emailRecipients.Count} {(emailRecipients.Count > 1 ? "recipients" : "recipient")} for account '{AccountId}' and workflow instance '{Uid}'.");

				var email = new WorkflowPauseActivityExecutionLimitExceededNotificationEmail
				{
					AccountId = AccountId,
					WorkflowInstanceUid = Uid,
					WorkflowRevisionUid = _instance.RevisionUid,
					NumberOfActivitiesExecuted = ActivityExecutionCount,
					EmailRecipients = emailRecipients,
					PausedPurgeDays = pausedPurgeDays
				};

				ServiceLocator.GetInstance<IEmailDistributor>().Init(email).QueueIt();

				return true;
			}
			catch (Exception ex)
			{
				_log.ErrorNoPII($"An exception occurred while sending activity execution limit exceeded notification to support for account '{AccountId}' and workflow instance '{Uid}'.", ex);
				return false;
			}
		}

		private string GetActivityDisplayName(IWINSActivity activity, IWINSWorkflowContext ctx)
		{
			return activity is AbstractHumanActivity abstractHumanActivity
				? abstractHumanActivity.ActivityDisplayName.Evaluate(ctx)
				: null;
		}

		private bool ShouldUseScopeEventHandler() => WorkflowFeatureFlags.UseScopeEventHandler &&
													 Model?.EngineVersion == Core.Workflow.Constants.EngineVersionV2;

		private void PauseInstance(UserAccount user, string comment, Action<UserAccount, string> pauseMethod)
		{
			Requires.NotNull(pauseMethod, nameof(pauseMethod));
			LastProcessDate = DateTime.UtcNow;
			lock (_lockObject)
			{
				pauseMethod(user, comment);
				_ForceReload();
			}
			if (ShouldUseScopeEventHandler())
			{
				InstanceHandler.Pause(_instance, user);
			}
			else
			{
				FireWorkflowScopeEvent(user, ScopeEventType.WorkflowPaused);
			}
		}

		private Activity StartActivity([CallerMemberName] string name = "") => ServiceInstrumentation?.ActivitySource
			?.StartActivity($"{nameof(WorkflowInstance)}.{name}")
			?.SetStatus(ActivityStatusCode.Error)
			.SetTag(TraceSemanticConventions.AttributeCodeFunction, name)
			.SetTag(TraceSemanticConventions.AttributeDocuSignAccountId, _instance.AccountId)
			.SetTag(TraceSemanticConventions.AttributeDocuSignAccountId, _instance.AccountId)
			.SetTag(ServiceTraceSemanticConventions.AttributeDocuSignWorkflowInstanceUid, _instance.Uid)
			.SetTag(ServiceTraceSemanticConventions.AttributeDocuSignWorkflowVersionUid, _instance.VersionUid)
			.SetTag(ServiceTraceSemanticConventions.AttributeDocuSignWorkflowRevisionUid, _instance.RevisionUid)
			.SetTag(ServiceTraceSemanticConventions.AttributeDocuSignWorkflowDefinitionUid, _instance.DefinitionUid);

		private void CheckAndHandleInstanceStateChanges()
		{
			var environmentType = SiteConfig?.Value?.EnvironmentType;
			var isGrpcEnabled = WorkflowClientConfig?.Value?.IsGrpcClientEnabled == true;

			var isRunningOnCloudWithGrpcEnabled = environmentType == Core.Util.Config.EnvironmentType.Cloud && isGrpcEnabled;

			if (isRunningOnCloudWithGrpcEnabled && InstanceStateChangedHelper.IsInstanceStateChanged(_instance.Uid, ActivityExecutionCount, CacheRepository, WorkflowSettingRepository))
			{
				_ForceReload();
				InstanceStateChangedHelper.RemoveInstanceStateChanged(_instance.Uid, CacheRepository);
			}
		}
    }
}

﻿using System;
using System.Collections.Specialized;
using System.Diagnostics;
using System.Text;
using System.Threading;
using System.Web;

using ADEXS.Core.Domain;
using ADEXS.Core.Domain.Model;
using ADEXS.Core.Domain.Utilities;
using ADEXS.Core.Types;
using ADEXS.Core.Util.Loggers;
using ADEXS.Web.Common.Helpers;

using DocuSign.DynamicSettings;
using DocuSign.Monitoring;

namespace ADEXS.Web.Common;
/// <summary>
/// Temporary Base class to redirect the ASHX API call to new Controller API as part of the effort of web migration to .net6
/// This will eventually cleaned up once we move to .net6
/// </summary>
public abstract class BaseApiHandler : IHttpHandler
{
	[EventSource("Exception", "CLM.FeatureFlags.PopulateFeatureFlagContext")]
	[Counter("Exception", "CLM.FeatureFlags.PopulateFeatureFlagContext", InstanceProperty = "Description")]
	private static ExceptionEventSource _featureFlagContextExceptionEventSource = new ExceptionEventSource();

	private static readonly IDiscreetLogger Logger = DiscreetLogger.GetLogger(typeof(BaseApiHandler));

	// reletive path for new controller api. ex) "A/TileView/GetTiles"
	// where full url is https://localhost/atlas/A/TileView/GetTiles
	protected abstract string Path { get; }

	public virtual bool IsReusable { get; set; }

	public virtual void ProcessRequest(HttpContext context)
	{
		try
		{
			PopulateFeatureFlagContext(AtlasContext.Current.UserAccount, context);
			Activity.Current?.SetTagsWithCommonFields(context);
		}
		catch (Exception ex)
		{
			Logger.ErrorNoPII($"Failed to Populate Feature Flag Context: {LogData()}", ex);
		}

		if (ShouldRedirectHelper())
		{
			Redirect(context);
		}
		else
		{
			HandleRequest(context);
		}
	}

	protected abstract bool ShouldRedirect();
	protected abstract void HandleRequest(HttpContext context);

	private string LogData()
	{
		return $"aid:{AtlasContext.Current.AccountId} uid:{AtlasContext.Current.UserAccountUid} relativeUrl: {Path}";
	}
	private void Redirect(HttpContext context)
	{
		try
		{
			if (context == null)
			{
				throw new ArgumentNullException(nameof(context));
			}

			// Get the original path info
			var pathInfo = !string.IsNullOrEmpty(context.Request.PathInfo) ? context.Request.PathInfo : "";

			// Get the original query string
			var queryString = context.Request.QueryString;

			// URL-encode pathInfo and queryString before building the new URL
			var encodedPathInfo = HttpUtility.UrlPathEncode(pathInfo);
			var encodedQueryString = ToUrlEncodedQueryString(queryString);

			var baseUrl = AtlasUrlHelper.WebApplicationBasePath();
			var newUrl = $"{baseUrl}/{Path}{encodedPathInfo}{encodedQueryString}";

			// Set the status code to 307 Temporary Redirect
			context.Response.StatusCode = Microsoft.AspNetCore.Http.StatusCodes.Status307TemporaryRedirect;

			// Set the Location header with the new Controller URL
			context.Response.AddHeader("Location", newUrl);

		}
		catch (ThreadAbortException)
		{
			// Do nothing intentionally.
			// The above Redirect call is *meant* to abort the thread with an exception and we do _not_ want it caught by the below catch block.
			// See this article for why we are catching it here and why we do nothing in this block.
			// https://learn.microsoft.com/en-us/dotnet/standard/threading/destroying-threads#handling-threadabortexception
		}
		catch (Exception ex)
		{
			Logger.ErrorNoPII($"Failed to Redirect: {LogData()}", ex);
			throw;
		}
	}

	private bool ShouldRedirectHelper()
	{
		try
		{
			return ShouldRedirect();
		}
		catch (Exception ex)
		{
			Logger.ErrorNoPII($"Failed to evaluate ShouldRedirect: {LogData()}", ex);
			return false;
		}
	}

	private static void PopulateFeatureFlagContext(UserAccount currentUser, HttpContext context)
	{
		if (context == null)
		{
			throw new ArgumentNullException(nameof(context));
		}

		if (currentUser == null || !ApplicationSettings.EnableFeatureFlagsContext)
		{
			if (AtlasContext.Current.AccountId > 0)
			{
				DynamicSettingsContext.Items[DynamicSettingsContextItems.AccountId] = AtlasContext.Current.AccountId;
			}
			else if (context.Request.QueryString[Constants.QueryStringParameters.AccountIdAbbreviated] != null)
			{
				if (int.TryParse(context.Request.QueryString[Constants.QueryStringParameters.AccountIdAbbreviated], out var accountId))
				{
					DynamicSettingsContext.Items[DynamicSettingsContextItems.AccountId] = accountId;
				}
			}


			return;
		}

		try
		{
			DynamicSettingsContext.Items[DynamicSettingsContextItems.AccountId] = currentUser.AccountId;
			DynamicSettingsContext.Items[DynamicSettingsContextItems.UserId] = currentUser.Uid;
			if (currentUser.Account != null)
			{
				DynamicSettingsContext.Items[DynamicSettingsContextItems.AccountType] = currentUser.Account.AccountType;
				DynamicSettingsContext.Items[DynamicSettingsContextItems.PlanName] = NpsUtilities.GetPlanIdName(currentUser.Account);
			}
		}
		catch (Exception ex)
		{
			_featureFlagContextExceptionEventSource.Emit(ex);
		}
	}

	private static string ToUrlEncodedQueryString(NameValueCollection collection)
	{
		if (collection == null || collection.Count == 0)
		{
			return string.Empty;
		}

		var builder = new StringBuilder();
		var first = true;
		for (var i = 0; i < collection.Count; i++)
		{
			var key = collection.GetKey(i);
			var value = collection.Get(i);
			builder.Append(first ? '?' : '&');
			first = false;
			builder.Append(HttpUtility.UrlEncode(key));
			builder.Append('=');
			builder.Append(HttpUtility.UrlEncode(value));
		}

		return builder.ToString();
	}
}

﻿using System;
using System.Diagnostics;
using System.Threading.Tasks;
using ADEXS.Web.Telemetry;
using ADEXS.Core.Util.Extensions;
using ADEXS.Web.Common.AspNetCore.Extensions;

using DocuSign.CLM.Monitoring.HttpModules;
using DocuSign.Monitoring;
using DocuSign.Msf.AspNet.OpenTelemetry.SemanticConventions;
using DocuSign.Platform;

using Microsoft.AspNetCore.Http;
using ADEXS.Core.Domain.Utilities;

namespace ADEXS.Web.Middlewares;

/// <summary>
/// Middleware that logs requests to KazMon. Code ensures monitoring context runs in both IIS HttpContext and async CallContext.
/// </summary>
public class LogAsyncRequestHttpMiddleware : AbstractLogRequestHttpMiddleware
{
	[EventSource("LogRequestHttpMiddleware", "CLM.WebNet")]
	private static readonly PropertyBagEventSource PropBagEvent = new();

	private readonly IServiceInstrumentation _serviceInstrumentation;

	static LogAsyncRequestHttpMiddleware()
	{
		// Indicate that we're running in both IIS HttpContext and async CallContext.
		// This ensures that logging works properly.
		SystemUtil.RunningInAsyncAndIisContexts = true;
		SystemUtil.RunningInDotNetCore = true;
	}

	public LogAsyncRequestHttpMiddleware(RequestDelegate next, IServiceInstrumentation serviceInstrumentation)
		: base(next)
	{
		_serviceInstrumentation = serviceInstrumentation;
	}

	public override async Task Invoke(HttpContext context)
	{
		var activity = Activity.Current;

		try
		{
			context.AddKazMonServerVariables();
			activity?.SetTagsWithCommonFields(context);
			await base.Invoke(context);
			activity?.SetStatus(context?.Response.StatusCode < 400 ? ActivityStatusCode.Ok : ActivityStatusCode.Error);
		}
		catch (Exception ex) when (ex.TraceAndIgnore(activity, ActivityStatusCode.Error))
		{
			throw;
		}
	}

	protected override PropertyBagEventSource LogRequestHttpModulePropBagEvent => PropBagEvent;
}

