﻿using System;

using ADEXS.Caching;
using ADEXS.Core.Workflow.Repository.Interfaces;

using Microsoft;

namespace ADEXS.WINS.Helpers;
public static class InstanceStateChangedHelper
{
	private const string InstanceStateChangedRedisKeyFormat = "CLMWorkflow_StateChanged_{0}";

	public static bool IsInstanceStateChanged(Guid instanceUid, int activityExecutionCount, ICacheRepository cacheRepository, IWorkflowSettingRepository workflowSettingRepository)
	{
		Requires.NotNull(cacheRepository, nameof(cacheRepository));
		Requires.NotNull(workflowSettingRepository, nameof(workflowSettingRepository));

		if (activityExecutionCount == 0 || (activityExecutionCount > 0 && activityExecutionCount % workflowSettingRepository.GetInstanceStateCheckActivityCount() == 0))
		{
			return cacheRepository.Exists(GenerateInstanceStateChangedCacheKey(instanceUid));
		}

		return false;
	}

	public static void RemoveInstanceStateChanged(Guid instanceUid, ICacheRepository cacheRepository)
	{
		Requires.NotNull(cacheRepository, nameof(cacheRepository));
		cacheRepository.RemoveKey(GenerateInstanceStateChangedCacheKey(instanceUid));
	}

	public static void SaveInstanceStateChanged(Guid instanceUid, ICacheRepository cacheRepository, IWorkflowSettingRepository workflowSettingRepository)
	{
		Requires.NotNull(cacheRepository, nameof(cacheRepository));
		Requires.NotNull(workflowSettingRepository, nameof(workflowSettingRepository));

		var now = DateTimeOffset.UtcNow.ToString();
		cacheRepository.Save(GenerateInstanceStateChangedCacheKey(instanceUid), now, workflowSettingRepository.GetInstanceStateCheckCacheKeyExpirationSeconds());
	}

	private static string GenerateInstanceStateChangedCacheKey(Guid instanceUid)
	{
		return string.Format(InstanceStateChangedRedisKeyFormat, instanceUid);
	}
}
