public class HttpRedirectEnrichmentMiddleware
{
    private readonly RequestDelegate _next;
    private static readonly ActivitySource Source = new("clm-web");

    public HttpRedirectEnrichmentMiddleware(RequestDelegate next) => _next = next;

    public async Task InvokeAsync(HttpContext context)
    {
        Activity? activity = Activity.Current;
        string? referer = context.Request.Headers["Referer"].FirstOrDefault();
        if (!string.IsNullOrEmpty(referer))
        {
            activity?.SetTag("HTTP_REFERER", referer);
        }

        await _next(context);

        if (context.Response.StatusCode >= 300 && context.Response.StatusCode < 400)
        {
            string? location = context.Response.Headers["Location"].FirstOrDefault();
            if (!string.IsNullOrEmpty(location))
            {
                activity?.SetTag("HTTP_RESPONSE_REDIRECT_LOCATION", location);
            }
        }
    }
}
