﻿#nullable enable
using System.Diagnostics;
using System.Runtime.CompilerServices;

using ADEXS.DetectTypeService.Extensions.Telemetry;
using ADEXS.DetectTypeService.Telemetry;

using CLM.MessageDefines.Tasks;

using DocuSign.Msf.AspNet.OpenTelemetry.SemanticConventions;
using DocuSign.Msf.AspNet.OpenTelemetry.ServiceMetrics.Extensions;

using ServiceTraceSemanticConventions = ADEXS.DetectTypeService.Telemetry.ServiceTraceSemanticConventions;
using MqWorkersServiceTraceSemanticConventions = ADEXS.MqWorkers.Telemetry.ServiceTraceSemanticConventions;

namespace ADEXS.DetectTypeService;

public partial class DetectTypeProcessor
{
	private readonly IServiceInstrumentation _serviceInstrumentation;

	private Activity? StartActivity([CallerMemberName] string name = "") =>
		_serviceInstrumentation?.ActivitySource
