else
{
	builder.Services.AddAuthentication();
	builder.Services.AddAuthorization(options =>
	{
		options.AddPolicy(ADEXS.Core.Types.Constants.WorkflowActivitiesApi.AllowAllPolicyName, policy =>
			policy.Requirements.Add(new AllowAnonymousRequirement()));
		options.DefaultPolicy = options.GetPolicy(ADEXS.Core.Types.Constants.WorkflowActivitiesApi.AllowAllPolicyName);
	});
	builder.Services.AddSingleton<IAuthorizationHandler, AllowAnonymousHandler>();
}

builder.Services.AddWorkflowActivitiesServices();

