{
	options.Filters.Add(new GlobalActionFilter());
	options.Filters.Add(new AuditedRequestAttribute());
	options.Filters.Add(new WorkflowActivitiesExceptionFilterAttribute());
});

// Do not remove -- this is required for proper deserialization of InstanceVariables
