builder.Services.AddEndpointsApiExplorer();

builder.Services.AddSwaggerGen(options =>
{
	options.CustomOperationIds(c => $"{c.ActionDescriptor.RouteValues["action"]}");
	options.SupportNonNullableReferenceTypes();
	options.UseAllOfForInheritance();
	options.SwaggerDoc(WorkflowActivitiesConstants.ServiceVersion, new OpenApiInfo
	{
		Title = WorkflowActivitiesConstants.ServiceName,
		Version = WorkflowActivitiesConstants.ServiceVersion
	});
	options.DocumentFilter<PathFormatDocumentFilter>();
	options.SchemaFilter<EnumNameSchemaFilter>();
});

builder.Services.AddSwaggerGenNewtonsoftSupport();

builder.Services.AddSingleton<IMetricsEmitter, WorkflowActivitiesMetricsEmitter>();

builder.Services.AddHttpClient();

builder.Services.AddComponentsInstrumentation();

var app = builder.Build();

if (app.Environment.IsDevelopment())
{
	IdentityModelEventSource.ShowPII = true;
}

app.UseCommonScmServices();

app.UseMiddleware<LogRequestHttpMiddleware>();

app.UseAuthentication();
