app.MapServiceHealthChecks();
app.MapVersionEndpoint($"{WorkflowActivitiesConstants.ServiceUrlPrefix}version");
app.MapVersionEndpoint($"{WorkflowActivitiesConstants.ServiceVersion}/version");
app.MapControllers();

app.RunScmWebService();

