using ADEXS.FeatureFlags;
using ADEXS.Web.Common.Session;

using DocuSign.CLM.Monitoring;
using DocuSign.Msf.AspNet.OpenTelemetry.SemanticConventions;

using log4net;
using log4net.Config;

using OpenTelemetry.Metrics;
using OpenTelemetry.Trace;

using ApplicationInfo = ADEXS.Core.Domain.Utilities.ApplicationInfo;

namespace ADEXS.Web.Common;

/// <summary>
/// Base class for all SCM web applications.
/// </summary>
public abstract class ScmHttpApplication : HttpApplication
{
	private ICoreInstrumentationLifetimeService _coreInstrumentationLifetimeService;

	/// <summary>
	/// Don't use the standard Application_Start(). .Net picks one via reflection
	/// and runs it. Ends up being this one here, not the one in the derived class.
	/// </summary>
	protected void ApplicationStart()
	{
		// log4net initialization
		FileInfo logConfigFileInfo = new FileInfo(AppDomain.CurrentDomain.SetupInformation.ConfigurationFile);
		XmlConfigurator.ConfigureAndWatch(logConfigFileInfo);

		ConfigurationHelper.InitializeConfiguration();

		if (ConfigWrapper.UseOneConfig)
		{
			Bootstrap();
		}

		// Use MSF specific env variable, fallback to app settings.
		var appName = Environment.GetEnvironmentVariable("MONITORING_SYSTEM_APPLICATION") ?? AppConfig.AppName(ApplicationInfo.Name);
		var site = SiteConfig.Site(ApplicationSettings.Site);
		var environment = Environment.GetEnvironmentVariable("MONITORING_SYSTEM_ENVIRONMENT") ?? SiteConfig.Environment("Unknown");
		var partition = Environment.GetEnvironmentVariable("MONITORING_SYSTEM_PARTITION");

		KazmonMonitoring.InitializeKazmon(
			applicationName: appName,
			appVersion: typeof(ScmHttpApplication).Assembly.GetName().Version.ToString(),
			environmentName: environment,
			siteName: site,
			partitionName: partition,
			shouldEnableKazmon: () => ApplicationSettings.KazmonEnableMonitoring,
			shouldEnableSmartMon: () => ApplicationSettings.SmartMonEnable,
			shouldEnableLogFileListener: () => Configuration.GetBooleanAppSetting("KazmonEnableLogFileListener", false));

		if (Configuration.GetBooleanAppSetting("UseOpenTelemetryNetFramework", true))
		{
			_coreInstrumentationLifetimeService = ServiceLocator.Container.TryGetInstance<ICoreInstrumentationLifetimeService>();

			if (_coreInstrumentationLifetimeService != null)
			{
				// Run synchronously
				Task.Run(() => _coreInstrumentationLifetimeService.StartAsync(
					assembly: typeof(ScmHttpApplication).Assembly,
					configureTracer: tracerProviderBuilder =>
					{
						_ = tracerProviderBuilder

						.AddAspNetInstrumentation(options =>
						{

						})
					.AddSource(typeof(RedisSessionStateProviderWrapper).FullName);
					},
					configureMeter: o => o.AddAspNetInstrumentation(options => options.Enrich = (HttpContext context, ref TagList tags) =>
					{
						if (!tags.Any(t => t.Key == "http.route") &&
							context.Request != null &&
							(context.Request.Url.AbsolutePath.EndsWith("aspx", StringComparison.OrdinalIgnoreCase) ||
							 context.Request.Url.AbsolutePath.EndsWith("asmx", StringComparison.OrdinalIgnoreCase) ||
							 context.Request.Url.AbsolutePath.EndsWith("ashx", StringComparison.OrdinalIgnoreCase)))
						{
							tags.Add("url.path", context.Request.Url.AbsolutePath);
						}
					}))).GetAwaiter().GetResult();
			}
		}

		if (!ConfigWrapper.UseOneConfig)
		{
			Bootstrap();
		}
	}

	/// <summary>
	/// This one stays the standard Application_End(), as no derive class overrides it.
	/// </summary>
	protected virtual void Application_End()
	{
		try
		{
			LogManager.Shutdown();
		}
		catch { }

		UninitializeFeatureFlags();

		if (_coreInstrumentationLifetimeService != null)
		{
			// Run synchronously
			Task.Run(() => _coreInstrumentationLifetimeService.StopAsync()).GetAwaiter().GetResult();
		}

		KazmonMonitoring.ShutdownKazmon();
	}

	/// <summary>
	/// This method is called once during application startup to initialize feature flags.
	/// </summary>
	protected virtual void InitializeFeatureFlags()
	{
		if (ApplicationSettings.EnableFeatureFlags)
		{
			FeatureFlagsBootstrap.Init(ApplicationSettings.Site);
		}
		else if (ApplicationSettings.EnableFeatureFlagsContext)
		{
			FeatureFlagsBootstrap.InitializeContextOnly();
		}
	}

	/// <summary>
	/// This method is called once during application shutdown to uninitialize feature flags.
	/// </summary>
	protected virtual void UninitializeFeatureFlags()
	{
		if (ApplicationSettings.EnableFeatureFlags || ApplicationSettings.EnableFeatureFlagsContext)
		{
			FeatureFlagsBootstrap.UnInitialize();
		}
	}

	/// <summary>
	/// This method is called once during application startup to bootstrap the dependency injection container.
	/// </summary>
	protected virtual void Bootstrap()
	{
	}
}

