		private int _activityExecutionCountEnforcedLimit = 0;
		private Guid _revisionUid = Guid.Empty;
		private Guid _definitionUid = Guid.Empty;
		private DateTime _revisionCreatedDate = DateTime.MinValue;
		private bool _isActivityExecutionLimitReached = false;

		private readonly object _lockObject = new object();
        private readonly IDictionary<Guid, InstanceActivity> _unfinishedActivities = new Dictionary<Guid, InstanceActivity>();
        private readonly Queue<Event> _queuedControlEvents = new Queue<Event>();
        private readonly Queue<Event> _queuedEvents = new Queue<Event>();

        private readonly Dictionary<string, ContentDefinition> _contentDefinitions = new Dictionary<string, ContentDefinition>(StringComparer.InvariantCultureIgnoreCase);
        private readonly Dictionary<string, InstanceVariable> _variables = new Dictionary<string, InstanceVariable>(StringComparer.InvariantCultureIgnoreCase);
        private readonly Dictionary<string, IXmlVariableDefinition> _xmlVariableDefinitions = new Dictionary<string, IXmlVariableDefinition>(StringComparer.InvariantCultureIgnoreCase);

