        private WorkflowInstance(Instance instance, IWINSDataStore data)
        {
	        _data = data;
	        _lock = new TimedLock(instance.Uid.ToString());
	        _instance = instance;
			_revisionUid = _instance.RevisionUid;
			_revisionCreatedDate = instance.RevisionCreatedDate;
	        LastProcessDate = DateTime.UtcNow;
