		}

		public WorkflowInstance(Instance instance, WorkflowMap workflowMap, IWINSDataStore data) : this(instance, data)
        {
            _workflowMap = workflowMap;
            // Load up any incomplete activities.  This is pointless to do for a new workflow, but it's not expensive either,
            // so we simply avoid trying to figure that out and simply do it.
            QueueIncompleteActivities();
        }

