		public void PauseForRevisionSuspended(UserAccount user, string comment)
		{
			if (Model.Status == Enums.WorkflowStepStatus.Paused)
			{
				_log.InfoNoPII($"PauseForRevisionSuspended - No action taken, instance already paused");
				return;
			}

			_log.InfoNoPII($"PauseForRevisionSuspended - Pausing");
			PauseInstance(user, comment, new WorkflowControlInstance(Model).PauseForRevisionSuspended);
		}

		public void ResumeForSuspendedRevision(UserAccount user, string comment)
		{
			if (Model.Status != Enums.WorkflowStepStatus.Paused)
			{
				_log.InfoNoPII($"ResumeForSuspendedRevision - No action taken, instance not paused. Status:{Model.Status}");
				return;
			}

			// Check if instance UpdatedBy has changed since the PauseForRevisionSuspended was invoked, if it has we do not want to do anything.
			if (Model.UpdatedBy != WorkflowControlInstance.PauseForSuspendedRevisionUpdatedBy)
			{
				_log.InfoNoPII($"ResumeForSuspendedRevision - No action taken, UpdatedBy is not {WorkflowControlInstance.PauseForSuspendedRevisionUpdatedBy}");
				return;
			}

			_log.InfoNoPII($"ResumeForSuspendedRevision - Resuming");
			Resume(user, comment);
			ClearPurgeDate();
		}

		public bool PauseWhenActivityExecutionLimitReached()
		{
			try
			{
				// Governance, check whether the number of executed activities exceeded the configured limit
				_isActivityExecutionLimitReached = Governor.IsActivityExecutionLimitReached(AccountId, Uid, _instance.RevisionUid, _instance.RevisionCreatedDate, ActivityExecutionCount, out _activityExecutionCountEnforcedLimit);

				if (_isActivityExecutionLimitReached)
				{
					_log.InfoNoPII($"Workflow instance '{Uid}' for account '{AccountId}' has exceeded the maximum limit (Actual={ActivityExecutionCount}, EnforcedLimit={_activityExecutionCountEnforcedLimit}) and will be paused.");

					Pause(UserAccount.WorkflowUserAccount(AccountId), "Workflow activity execution limit reached.");

					TrySendActivityExecutionLimitExceedNotification(SetPurgeDateForAccountCancel());
				}
			}
			catch (Exception e)
			{
				_log.ErrorNoPII($"Unable to pause instance '{Uid}' for account '{AccountId}' activity execution limit", e);
			}

			return _isActivityExecutionLimitReached;
		}

		public void PauseWhenThrottled(InstanceActivity activity, WorkflowApiRequestThrottledException exception)
		{
			Requires.NotNull(activity, nameof(InstanceActivity));
			Requires.NotNull(exception, nameof(WorkflowApiRequestThrottledException));

			if (exception.WorkflowThrottledStatus == Enums.WorkflowThrottledStatus.NotThrottled)
			{
				_log.InfoNoPII($"Workflow instance '{Uid}' for account '{AccountId}' is not throttled and will not be paused.");
				return;
			}

			try {
				activity.Status = Enums.WorkflowStepStatus.Paused;
				var throttledByApi = exception.ThrottledApiAsString();

				_log.InfoNoPII($"Workflow instance '{Uid}' for account '{AccountId}' has exceeded the maximum {throttledByApi} request limit and will be paused. Message: {exception.Message}");

				PauseInstance(UserAccount.WorkflowUserAccount(AccountId), $"{throttledByApi} requests limit reached.", new WorkflowControlInstance(Model).PauseForWorkflowRateLimitExceed);

				// Send notification
				switch (exception.WorkflowThrottledStatus)
				{
					case Enums.WorkflowThrottledStatus.ThrottledDueToExpressionRateLimit:
						TrySendExpressionRateLimitExceedNotification(SetPurgeDateForWorkflowRateLimit(), GetAdvancedWorkflowNotificationUserAccountUid());
						break;
					case Enums.WorkflowThrottledStatus.ThrottledDueToSmallBenRateLimit:
						TrySendSmallBenRateLimitExceedNotification(SetPurgeDateForWorkflowRateLimit(), GetAdvancedWorkflowNotificationUserAccountUid());
						break;
					default:
						_log.WarnNoPII("No notifications sent for unsupported throttle status");
						break;
				}

				// mark instance as throttled for current workflow run session, WorkflowThrottledStatus property is not persisted and refreshed on reload
				_instance.WorkflowThrottledStatus = exception.WorkflowThrottledStatus;
			}
#pragma warning disable CA1031
			catch (Exception e)
#pragma warning restore CA1031
			{
				_log.ErrorNoPII($"Unable to pause instance '{Uid}' for account '{AccountId}' rate limit request limit with status {exception?.WorkflowThrottledStatus}", e);
			}
		}

