		public void TrySendExpressionRateLimitExceedNotification(int pausedPurgeDays, Guid advancedWorkflowNotificationUserAccountUid)
		{
			if (!ServiceLocator.GetInstance<IWorkflowSettingRepository>().GetEnableWorkflowLimitNotifications(AccountId))
			{
				_log.InfoNoPII($"EnableWorkflowLimitNotifications disabled for account {AccountId}, not sending expression rate limit exceeded notification");
				return;
			}

			try
			{
				if (advancedWorkflowNotificationUserAccountUid == Guid.Empty)
				{
					_log.InfoNoPII($"There is no AdvancedWorkflowNotificationUser configured. Not sending expression rate limit exceeded notification for account '{AccountId}' and workflow instance '{Uid}'.");
					return;
				}

				// Send to AdvancedWorkflowNotificationUserAccountUid, if configured for account
				_log.InfoNoPII($"Loading {nameof(UserAccount)} {advancedWorkflowNotificationUserAccountUid} to receive expression rate limit exceeded notification");
				UserAccount notificationUser = UserAccountRepository.Instance.LoadByUid(AccountId, advancedWorkflowNotificationUserAccountUid);

				if (string.IsNullOrEmpty(notificationUser?.EmailAddress))
				{
					_log.InfoNoPII($"AdvancedWorkflowNotificationUser '{advancedWorkflowNotificationUserAccountUid}' not found or missing email address. Not sending expression rate limit exceeded notification for account '{AccountId}' and workflow instance '{Uid}'.");
					return;
				}
				var emailRecipients = new List<string>() { notificationUser.EmailAddress };

				_log.InfoNoPII($"Sending expression rate limit exceeded notification to user '{advancedWorkflowNotificationUserAccountUid}' for account '{AccountId}' and workflow instance '{Uid}'.");

				var email = new WorkflowPauseExpressionRateLimitExceededNotificationEmail
				{
					AccountId = AccountId,
					WorkflowInstanceUid = Uid,
					WorkflowInstanceId = _instance.Id,
					EmailRecipients = emailRecipients,
					PausedPurgeDays = pausedPurgeDays
				};

				ServiceLocator.GetInstance<IEmailDistributor>().Init(email).QueueIt();

				return;
			}
#pragma warning disable CA1031
			catch (Exception ex)
#pragma warning restore CA1031
			{
				_log.ErrorNoPII($"An exception occurred while sending activity execution limit exceeded notification to support for account '{AccountId}' and workflow instance '{Uid}'.", ex);
				return;
			}
		}

