        /// <summary>
        /// Replaces variables provided in the dictionary returning replaced variables
        /// </summary>
        public void SetVariables(
	        InstanceActivity instanceActivity,
	        Dictionary<string, object> variables,
	        out List<InstanceVariable> oldVariables,
	        out bool areVariablesOverTotalSizeLimit,
	        out int totalVariablesSizeBytes,
	        out int totalVariablesSizeLimitBytes,
			out Dictionary<string, int> oversizedVariables,
	        out int variableValueSizeLimitBytes)
        {
            IWINSActivity activity = _SafeGetActivity(instanceActivity);
            if (activity == null) throw new ApplicationException(string.Format("Cannot find expected activity '{0}' in workflow map for instanceUid '{1}'.", instanceActivity.ActivityTypeName, Uid));
            _log.DebugWithPII($"SetVariables called for ActivityUid {instanceActivity.Uid} ({{0}}).", instanceActivity.Name);

            // We create the context so that the variable dictionaries get populated
            IWINSWorkflowContext ctx = _CreateContext(activity, instanceActivity);
            oldVariables = new List<InstanceVariable>();
            var newVariables = UpdateVariablesHelper.PopulateContextVariables(ctx, this.Model, variables);

            // Only set variables that are under the size limit
			oversizedVariables = InstanceVariableGovernance.GetOversizedVariables(ctx.AccountId, newVariables, ctx.SubmittedDate, out variableValueSizeLimitBytes);
			foreach (InstanceVariable variable in newVariables)
			{
				if (!oversizedVariables.ContainsKey(variable.Name))
				{
					oldVariables.Add(Variables[variable.Name]);
					// we could do "ctx.Variables.Set(variable.Key, variable.Value);" but that would log as the instanceActivity and we don't want that...
					Variables[variable.Name] = variable;
				}
			}

			// Undo setting the variables, if the instance's total variable size exceeds the limit
			areVariablesOverTotalSizeLimit = InstanceVariableGovernance.AreVariablesOverTotalSizeLimit(ctx.AccountId,
				Variables.Select(x => x.Value), ctx.SubmittedDate, activityConfiguration: null, out totalVariablesSizeBytes, out totalVariablesSizeLimitBytes);
			if (areVariablesOverTotalSizeLimit)
			{
				foreach (InstanceVariable variable in oldVariables)
				{
					Variables[variable.Name] = variable;
				}
			}

			return;
        }

