        private List<WINSWorkflowContextWINSActivity> GetUnfinishedWINSWorkflowContextsWINSActivities(DateTime endDate)
        {
			var list = new List<WINSWorkflowContextWINSActivity>();

			if (!HasUnfinishedActivities) return list;

            List<InstanceActivity> activities;
            lock (_unfinishedActivities)
            {
                activities = new List<InstanceActivity>(_unfinishedActivities.Values);
            }

            IWINSWorkflowContext ctx = null;
            foreach (var activity in activities)
            {
                var winsActivity = _SafeGetActivity(activity);
                ctx = _CreateContext(winsActivity, activity);

                if (ctx.TrackActivity() && IsTrackItActivity(winsActivity))
                {
					list.Add(new WINSWorkflowContextWINSActivity { EndDate = endDate, WINSActivity = winsActivity, WINSWorkflowContext = ctx });
				}
            }

			return list;
        }

		private void AbortOrFailScopeStep(Enums.WorkflowStepStatus status, DateTime endDate)
		{
			if (!HasUnfinishedActivities) return;

			List<InstanceActivity> activities;
			lock (_unfinishedActivities)
			{
				activities = new List<InstanceActivity>(_unfinishedActivities.Values);
			}
			IWINSWorkflowContext ctx = null;
			foreach (var activity in activities)
			{
				var winsActivity = _SafeGetActivity(activity);
				ctx = _CreateContext(winsActivity, activity);

				if (ctx.TrackActivity() && IsTrackItActivity(winsActivity))
				{
					var documents = ((ITrackItActivity)winsActivity).GetDocuments(ctx);

					ServiceLocator.GetInstance<IDocumentScopeRepository>().CompleteStep(ctx.AccountId, ctx.ScopeUid, activity.Uid, documents, null, status, endDate);
				}
			}
		}

		private bool _CheckWaitingActivities()
        {
            List<InstanceActivity> activities;
            lock (_unfinishedActivities)
            {
                activities = new List<InstanceActivity>(_unfinishedActivities.Values.Where(n => n.Status == Enums.WorkflowStepStatus.Waiting));
            }
            bool success = false;
            foreach (InstanceActivity activity in activities)
            {
                if (CheckActivityForCompletion(activity)) success = true;
            }
            return success;
        }

        private bool _contextInitialized = false;
        private IWINSWorkflowContext _CreateContext(IWINSActivity activity, InstanceActivity instanceActivity)
        {
            IWINSWorkflowContext ctx = CreateContext(activity, instanceActivity);
            AtlasContext.Current.UserAccount = ctx.WorkflowUserAccount;
            // Only do this once.  If it's been done, it's done.
            if (_contextInitialized) return ctx;

            // This all depends on the properties for the Start step.  We always want a Start step for normal processing;
            // however, we don't necessarily have a Start step for our tests.
            StartActivity start = _workflowMap.StartActivity;
            if (start == null)
            {
                if (_data.IsTest) return ctx;
                throw new ApplicationException(string.Format("Cannot find StartActivity for InstanceUid {0}", _instance.Uid));
            }

            // Contents
            string[][] contents = start.DefinedContents;
            if (contents != null && contents.Length > 0)
            {
                foreach (string[] t in contents)
                {
                    ContentDefinition definition = new ContentDefinition(t);
                    if (!_contentDefinitions.ContainsKey(definition.Name)) _contentDefinitions[definition.Name] = definition;
                }
            }

            _LoadVariables();

            // XML Variables
            // Because XmlVariables have dependencies on the variables as well, it's easier to feed this through the xml variable handler
            // rather than replicate the code.
            List<IXmlVariableDefinition> definitions = start.DefinedXmlVariables;
            if (definitions != null && definitions.Count > 0)
            {
                foreach (IXmlVariableDefinition definition in definitions)
                {
                    if (!_xmlVariableDefinitions.ContainsKey(definition.Name)) ctx.XmlVariables.Declare(definition, logMessage: false);
                }
            }

            _contextInitialized = true;
            return ctx;
		}

        public IWINSWorkflowContext CreateContext(IWINSActivity activity, InstanceActivity instanceActivity)
        {
			return ServiceLocator.GetInstance<IWorkflowContextFactory>().CreateWorkflowContext(this, activity, instanceActivity);
        }

		private void _LoadVariables()
        {
	        // Variables
	        Dictionary<string, InstanceVariable> variables = _data.LoadVariablesByInstanceUid(AccountId, Constants.WorkflowUser, Uid);
	        foreach (InstanceVariable variable in variables.Values)
	        {
		        if (!_variables.ContainsKey(variable.Name)) _variables[variable.Name] = variable;
	        }
        }

        private bool _ExecuteFirstActivity()
        {
            InstanceActivity activity = null;
            lock (_unfinishedActivities)
            {
                if (_unfinishedActivities.Count > 0) activity = _unfinishedActivities.Values.FirstOrDefault(n => n.Status == Enums.WorkflowStepStatus.Pending || n.Status == Enums.WorkflowStepStatus.Executing);
            }
            if (activity == null) return false;
            ExecuteActivity(activity);
            return true;
        }

        private bool _ExecuteFirstEvent(bool controlOnly = false)
        {
            // We always try to execute control events first.
            Event e = null;
            lock (_queuedControlEvents)
            {
                if (_queuedControlEvents.Count > 0) e = _queuedControlEvents.Dequeue();
            }
            if (e != null)
            {
                ExecuteEvent(e);
                return true;
            }

            // If we're only caring about control events, we can quit now.
            if (controlOnly) return false;

            // Otherwise, we do the same thing with the rest of the events.
            lock (_queuedEvents)
            {
                if (_queuedEvents.Count > 0) e = _queuedEvents.Dequeue();
            }
            if (e != null)
            {
                ExecuteEvent(e);
                return true;
            }

            return false;
        }

        private void _ForceReload()
        {
            // Reload the object to get new state -- we do this inside the lock so that there's no race condition
            // between this and a worker who may be working on the object
            lock (_lockObject)
            {
                Guid instanceUid = Uid;
                _instance = _data.LoadInstanceByUid(AccountId, Constants.WorkflowUser, instanceUid);
                if (_instance == null) throw new ApplicationException(string.Format("Failed to reload instanceUid: {0}", instanceUid));
            }
        }

        private void _MarkCompleted()
        {
	        if (_instance.IsCompleted)
	        {
		        _log.InfoNoPII($"Instance {_instance.Uid} is already complete with status {_instance.Status}");
		        return;
	        }

            _PersistVariables();
            bool endActivityFailure = _workflowMap.HasEndActivity() && !_instance.EndActivityReached;
            Enums.WorkflowStepStatus endStatus = endActivityFailure ? Enums.WorkflowStepStatus.Failed : Enums.WorkflowStepStatus.Completed;
            _data.SaveInstance(_instance);
            _data.InstanceCompleted(_instance, endStatus);

			if (ShouldUseScopeEventHandler())
			{
				InstanceHandler.Complete(_instance);
			}
			else
			{
				var eventType = _instance.Status == Enums.WorkflowStepStatus.Failed ? ScopeEventType.WorkflowFailed : ScopeEventType.WorkflowFinished;
				try
				{
					WorkflowScopeEvent.ScopeEventInfo scopeEventData = new WorkflowScopeEvent.ScopeEventInfo()
					{
						AccountId = _instance.AccountId,
						ScopeUid = _instance.ScopeUid,
						ScopeName = _instance.ScopeName,
						PeriodStart = _instance.EndDate,
						EventType = (int)eventType,
						WorkflowUid = _instance.Uid,
						WorkflowName = _instance.Name
					};
					WorkflowScopeEvent scopeEvent = new WorkflowScopeEvent(_instance.AccountId, _instance.Uid)
					{
						EventData = scopeEventData
					};
					EventRepo.FireEvent(_instance.AccountId, scopeEvent);
				}
				catch (Exception ex)
				{
					_log.ErrorNoPII($"Error in saving scope event {_instance.ScopeUid} for eventtype {eventType}", ex);
				}
			}

            if (endActivityFailure && _instance.Status == Enums.WorkflowStepStatus.Failed)
            {
                if (_lastActivityUid == Guid.Empty)
                {
                    _log.WarnNoPII($"No activities for workflow.");
                }
                else
                {
                    InstanceActivity activity = _data.LoadActivityByUid(AccountId, Constants.WorkflowUser, _lastActivityUid);
                    IWINSActivity winsActivity = _SafeGetActivity(activity);
                    IWINSWorkflowContext ctx = _CreateContext(winsActivity, activity);
                    LoggingHelper.LogActivityErrorNoPII(ctx, Resources.WINSResources.WorkflowExitedWithoutReachingTheEndActivity);
                }

            }
            if (_instance.Status == Enums.WorkflowStepStatus.Completed || _instance.Status == Enums.WorkflowStepStatus.Failed)
            {
                _data.FireMessageQueueEvent(new WorkflowEvent(AccountId, _instance.Uid, (int)_instance.Status));
            }
        }

        private void _MarkWaiting()
        {
            _PersistVariables();
            _instance.Status = Enums.WorkflowStepStatus.Waiting;
            _data.SaveInstance(_instance);
        }

        private void _PersistVariables()
        {
            InstanceVariables variables = new InstanceVariables();
            foreach (InstanceVariable variable in _variables.Values) if (variable.IsModified) variables.Add(variable);
            _data.SaveInstanceVariables(AccountId, Constants.WorkflowUser, variables);
        }

        private void PersistEvents()
		{
			lock (_queuedControlEvents)
			{
				while (_queuedControlEvents.Count > 0)
				{
					PersistEvent(_queuedControlEvents.Dequeue());
				}
			}

			lock (_queuedEvents)
			{
				while (_queuedEvents.Count > 0)
				{
					PersistEvent(_queuedEvents.Dequeue());
				}
			}
		}

        private void PersistEvent(Event e)
		{
			try
			{
				if (e.Uid == Guid.Empty)
				{
					_data.CreateEvent(e);
					WorkflowEventWorkload wrk = new WorkflowEventWorkload(e, "EventCreatedOnInstanceSave")
					{
						RevisionUid = _instance.RevisionUid,
						WorkflowDB = DataStore.GetDbNameForAccount(e.AccountId)
					};
					wrk.Log();
				}
				else
				{
					_data.UnlockEvent(e.AccountId, e.Uid);
					WorkflowEventWorkload wrk = new WorkflowEventWorkload(e, "EventUnlockedOnInstanceSave")
					{
						RevisionUid = _instance.RevisionUid,
						WorkflowDB = DataStore.GetDbNameForAccount(e.AccountId)
					};
					wrk.Log();
				}
			}
			catch (Exception ex)
			{
				_log.ErrorNoPII($"Failed to save event {e.Uid}, for instance {e.InstanceUid}, activity {e.ActivityUid}, and callback name {e.CallbackName} to db", ex);
			}
		}

		private IWINSActivity _SafeGetActivity(InstanceActivity instanceActivity)
        {
            try
            {
                IWINSActivity activity;
                if (_workflowMap.TryGetActivity(instanceActivity.Name, out activity)) return activity;
                // There is a problem with old MWF workflow data where the name of the activity is really the type name.
                // Don't ask.  It's a stupid and boring story.
                // But we still should handle it, at least for awhile.  So: if we get here because we didn't match name, we should see
                // if we match type name.
                // This whole thing is a huge hack that should get removed when it can.
                InstanceActivity inputActivity = null;
                if (instanceActivity.InputActivityUid != Guid.Empty) inputActivity = _data.LoadActivityByUid(AccountId, Constants.WorkflowUser, instanceActivity.InputActivityUid);
                if (_workflowMap.LegacyTryGetActivityByTypeName(instanceActivity.Name, inputActivity == null ? "" : inputActivity.Name, instanceActivity.InputActivityOutput ?? "", out activity))
                {
                    // Rename it so we get one less instance of this in the future
                    instanceActivity.Name = activity.Name;
                    return activity;
                }
                throw new ApplicationException(string.Format("Invalid activity name '{0}' for instanceUid '{1}'.", instanceActivity.Name, instanceActivity.InstanceUid));
            }
            catch (Exception e)
            {
                _log.ErrorNoPII($"_SafeGetActivity failed.", e);
                return null;
            }
        }

        private bool _ShouldProcess(InstanceActivity instanceActivity)
        {
            switch (instanceActivity.Status)
            {
                case Enums.WorkflowStepStatus.Executing:
                case Enums.WorkflowStepStatus.Pending:
                case Enums.WorkflowStepStatus.Waiting:
                case Enums.WorkflowStepStatus.Aborted:
                    return true;
                default:
                    return false;
            }
        }

        private bool _ShouldProcess()
        {
            lock (_lockObject)
            {
                switch (Model.Status)
                {
                    case Enums.WorkflowStepStatus.Executing:
                    case Enums.WorkflowStepStatus.Pending:
                    case Enums.WorkflowStepStatus.Waiting:
                        return true;
                    default:
                        return false;
                }
            }
        }

        private bool _ShouldProcessEvent()
        {
            lock (_lockObject)
            {
                switch (Model.Status)
                {
                    case Enums.WorkflowStepStatus.Executing:
                    case Enums.WorkflowStepStatus.Pending:
                    case Enums.WorkflowStepStatus.Waiting:
                    case Enums.WorkflowStepStatus.Aborted:
                        return true;
                    default:
                        return false;
                }
            }
        }

        //ITrackItActivity interface is implemented by activities whose data is tracked in DocumentScope and ScopeEvent tables.
        private bool IsTrackItActivity(IWINSActivity activity)
        {
            return activity is ITrackItActivity;
        }

		private void FireWorkflowScopeEvent(UserAccount user, ScopeEventType eventType, IWINSActivity activity = null, IWINSWorkflowContext ctx = null)
		{
			try
			{
				WorkflowScopeEvent.ScopeEventInfo scopeEventData = new WorkflowScopeEvent.ScopeEventInfo
				{
					AccountId = _instance.AccountId,
					ScopeUid = _instance.ScopeUid,
					ScopeName = _instance.ScopeName,
					PeriodStart = DateTime.UtcNow,
					EventType = (int)eventType,
					WorkflowUid = _instance.Uid,
					WorkflowName = _instance.Name,
					EventBy = user == null ? null : new WorkflowScopeEvent.Assignee() { Email = user.EmailAddress, Name = user.FriendlyName, Uid = user.Uid }
				};

				if (activity != null && ctx != null)
				{
					scopeEventData.StepName = ctx.ActivityName;
					scopeEventData.StepDisplayName = GetActivityDisplayName(activity, ctx);
					scopeEventData.StepTypeName = ctx.ActivityTypeName;
					scopeEventData.StepUid = ctx.ActivityUid;
				}

				EventRepo.FireEvent(_instance.AccountId, new WorkflowScopeEvent(_instance.AccountId, _instance.Uid)
				{
					EventData = scopeEventData
				});
			}
			catch (Exception ex)
			{
				_log.ErrorNoPII($"Error in saving scope event scopeUid: {_instance.ScopeUid}, instanceUid: {_instance.Uid} eventType: {eventType}", ex);
			}
		}

		private void RemoveProperties(InstanceActivity instanceActivity, IWINSActivity activity)
	    {
		    // We need to delete properties if this is not a human activity or WaitForNextStepAfterCompletion is not set
		    if (activity is AbstractHumanActivity && ServiceLocator.GetInstance<IWorkflowSettingRepository>().GetWaitForNextStepAfterCompletion(instanceActivity.AccountId))
		    {
				_data.DeleteActivityPropertiesWithExclusion(instanceActivity.AccountId, instanceActivity.InstanceUid, instanceActivity.Uid, new List<string> { nameof(AbstractHumanActivity.WaitForNextStep) });
				_log.InfoNoPII("Removing activity properties except WaitForNextStep");
		    }
		    else
		    {
			    _data.DeleteActivityProperties(instanceActivity.AccountId, instanceActivity.InstanceUid, instanceActivity.Uid);
			    _log.InfoNoPII("Removing activity properties");
		    }
	    }

		private string[] BuildNDCTags(InstanceActivity instanceActivity) => instanceActivity != null ? new[] { $"ActivityId:{instanceActivity.Uid}",$"W:{instanceActivity.InstanceUid}" } : new string[] { };
	    private string[] BuildNDCTags(Event e) => e != null ? new[] { $"ActivityId:{e.ActivityUid}", $"W:{e.InstanceUid}" } : new string[] { };

		/// <summary>
		/// Try to send an e-mail to support when a workflow instance activity execution account exceeds configured threshold.
		/// </summary>
		/// <param name="pausedPurgeDays">Number of days to purge workflow related data</param>
		/// <returns>
		/// True	-> Success.
		/// False	-> Fail to send email.
		/// </returns>
		private bool TrySendActivityExecutionLimitExceedNotification(int pausedPurgeDays)
		{
			try
			{
				var emailRecipients = new List<string>();

				// Support should get the email
				var supportEmailAddress = WorkflowSettingRepository.GetSupportNotificationEmailAddress(AccountId);
				if (!string.IsNullOrEmpty(supportEmailAddress))
				{
					emailRecipients.Add(supportEmailAddress);
				}

				// Also send to AdvancedWorkflowNotificationUserAccountUid, if configured for account
				UserAccount notificationUser = null;
				var notificationUserUid = new AccountSettings(AccountId, Constants.WorkflowUser).AdvancedWorkflowNotificationUserAccountUid;
				if (notificationUserUid != Guid.Empty)
				{
					_log.InfoNoPII($"Loading {nameof(UserAccount)} {notificationUserUid} to receive activity execution limit exceeded notification");
					notificationUser = UserAccountRepository.Instance.LoadByUid(AccountId, notificationUserUid);
				}
				if (!string.IsNullOrEmpty(notificationUser?.EmailAddress))
				{
					emailRecipients.Add(notificationUser.EmailAddress);
				}

				if (!emailRecipients.Any())
				{
					_log.InfoNoPII($"Email support address setting is empty, and there is no AdvancedWorkflowNotificationUser configured. Thus, not sending activity execution limit exceeded notification for account '{AccountId}' and workflow instance '{Uid}'.");
					return false;
				}

				_log.InfoNoPII($"Sending activity execution limit exceeded notification to {emailRecipients.Count} {(emailRecipients.Count > 1 ? "recipients" : "recipient")} for account '{AccountId}' and workflow instance '{Uid}'.");

				var email = new WorkflowPauseActivityExecutionLimitExceededNotificationEmail
				{
					AccountId = AccountId,
					WorkflowInstanceUid = Uid,
					WorkflowRevisionUid = _instance.RevisionUid,
					NumberOfActivitiesExecuted = ActivityExecutionCount,
					EmailRecipients = emailRecipients,
					PausedPurgeDays = pausedPurgeDays
				};

				ServiceLocator.GetInstance<IEmailDistributor>().Init(email).QueueIt();

				return true;
			}
			catch (Exception ex)
			{
				_log.ErrorNoPII($"An exception occurred while sending activity execution limit exceeded notification to support for account '{AccountId}' and workflow instance '{Uid}'.", ex);
				return false;
			}
		}

		private string GetActivityDisplayName(IWINSActivity activity, IWINSWorkflowContext ctx)
		{
			return activity is AbstractHumanActivity abstractHumanActivity
				? abstractHumanActivity.ActivityDisplayName.Evaluate(ctx)
				: null;
		}

		private bool ShouldUseScopeEventHandler() => WorkflowFeatureFlags.UseScopeEventHandler &&
													 Model?.EngineVersion == Core.Workflow.Constants.EngineVersionV2;

		private void PauseInstance(UserAccount user, string comment, Action<UserAccount, string> pauseMethod)
		{
			Requires.NotNull(pauseMethod, nameof(pauseMethod));
			LastProcessDate = DateTime.UtcNow;
			lock (_lockObject)
			{
				pauseMethod(user, comment);
				_ForceReload();
			}
			if (ShouldUseScopeEventHandler())
			{
				InstanceHandler.Pause(_instance, user);
			}
			else
			{
				FireWorkflowScopeEvent(user, ScopeEventType.WorkflowPaused);
			}
		}

		private Activity StartActivity([CallerMemberName] string name = "") => ServiceInstrumentation?.ActivitySource
			?.StartActivity($"{nameof(WorkflowInstance)}.{name}")
			?.SetStatus(ActivityStatusCode.Error)
			.SetTag(TraceSemanticConventions.AttributeCodeFunction, name)
			.SetTag(TraceSemanticConventions.AttributeDocuSignAccountId, _instance.AccountId)
			.SetTag(TraceSemanticConventions.AttributeDocuSignAccountId, _instance.AccountId)
			.SetTag(ServiceTraceSemanticConventions.AttributeDocuSignWorkflowInstanceUid, _instance.Uid)
			.SetTag(ServiceTraceSemanticConventions.AttributeDocuSignWorkflowVersionUid, _instance.VersionUid)
			.SetTag(ServiceTraceSemanticConventions.AttributeDocuSignWorkflowRevisionUid, _instance.RevisionUid)
			.SetTag(ServiceTraceSemanticConventions.AttributeDocuSignWorkflowDefinitionUid, _instance.DefinitionUid);

		private void CheckAndHandleInstanceStateChanges()
		{
			var environmentType = SiteConfig?.Value?.EnvironmentType;
			var isGrpcEnabled = WorkflowClientConfig?.Value?.IsGrpcClientEnabled == true;

			var isRunningOnCloudWithGrpcEnabled = environmentType == Core.Util.Config.EnvironmentType.Cloud && isGrpcEnabled;

			if (isRunningOnCloudWithGrpcEnabled && InstanceStateChangedHelper.IsInstanceStateChanged(_instance.Uid, ActivityExecutionCount, CacheRepository, WorkflowSettingRepository))
			{
				_ForceReload();
				InstanceStateChangedHelper.RemoveInstanceStateChanged(_instance.Uid, CacheRepository);
			}
		}
    }
}

