	public class WorkflowManager
	{
		private delegate void InstanceSetupDelegate(WorkflowInstance instance);

		public static readonly string DefaultEventMSMQName = "AtlasEventsForWins";
		public static readonly string MSMQPathAtlasEventsFormat = "FormatName:Direct=OS:{0}\\Private$\\{1}";

		private static WorkflowManager _instance = null;
		private static readonly IDiscreetLogger _log = DiscreetLogger.GetLogger(typeof(WorkflowManager));

		// Abort notification milestones
		private const int OneDay = 1;
		private const int OneWeek = 7;
		private const int OneMonth = 30;
		private const double TenPercent = .1;
		private const double TwentyFivePercent = .25;
		private const double FiftyPercent = .5;

		public static WorkflowManager Instance
		{
			get { return _instance ?? (_instance = new WorkflowManager(new WINSDataStore())); }
			protected set { _instance = value; }
		}

		public delegate void NeedWorkEvent();
		public delegate void WorkReceivedEvent();

		public event NeedWorkEvent OnNeedWorkEvent;
		public event WorkReceivedEvent OnWorkReceivedEvent;

		private readonly IWINSDataStore _data;
		private readonly ThreadSafeDictionary<Guid, WorkflowInstance> _instances = new ThreadSafeDictionary<Guid, WorkflowInstance>();
		private readonly ThreadSafeDictionary<int, Enums.AccountStatus> _recentAccountStatus = new ThreadSafeDictionary<int, Enums.AccountStatus>();
		// Set to true when we're given work; if this is true and we have no more work for the workers (GetFirstUnlockedInstance returns null),
		// this will signal the NeedWorkEvent and set itself to false.  
		private bool _signalNeedWork = false;

		private IEventRepository _eventRepo;
		private IEventRepository EventRepo => (_eventRepo ?? (_eventRepo = ServiceLocator.GetInstance<IEventRepository>()));

		private IWorkflowSettingRepository _workflowSettingRepo;
		private IWorkflowSettingRepository WorkflowSettingRepo => (_workflowSettingRepo ?? (_workflowSettingRepo = ServiceLocator.GetInstance<IWorkflowSettingRepository>()));

		private IGovernor _governor;
		private IGovernor Governor => _governor ?? (_governor = ServiceLocator.GetInstance<IGovernor>());

		private IRevisionService _revisionService;
		private IRevisionService RevisionService => _revisionService ?? (_revisionService = ServiceLocator.GetInstance<IRevisionService>());

		// Integration Tests pass false for the selfRegister param so they do not cause erroneous event registration
		public WorkflowManager(IWINSDataStore data, bool selfRegister = true)
		{
			_data = data;
			string configuredLocalQueueName = Path.GetFileNameWithoutExtension(Configuration.GetStringAppSetting(
				EventDispatchedWorkerEventsSettings.EventDispatchedWorkerEventQueuePath,
				string.Format(MSMQPathAtlasEventsFormat, Environment.MachineName, DefaultEventMSMQName)));

			// self register for AccountStatusEvent
			EventRegistration reg = new EventRegistration
			{
				DestPath = string.Format(MSMQPathAtlasEventsFormat, Environment.MachineName, configuredLocalQueueName),
				Event = ScmEnums.EventType.AccountStatus,
			};
			ServiceLocator.GetInstance<IEventRepository>().SelfRegisterForEvent(UserAccount.WorkflowUserAccount(Constants.AllAccounts), reg);

			// self register for WorkflowSuspended
			reg = new EventRegistration
			{
				DestPath = string.Format(MSMQPathAtlasEventsFormat, Environment.MachineName, configuredLocalQueueName),
				Event = ScmEnums.EventType.WorkflowRevisionSuspension,
			};
			ServiceLocator.GetInstance<IEventRepository>().SelfRegisterForEvent(UserAccount.WorkflowUserAccount(Constants.AllAccounts), reg);
		}

		internal bool AccountWasRecentlySuspended(int accountId)
		{
			var isSuspended =  _recentAccountStatus.ContainsKey(accountId) && _recentAccountStatus[accountId] == Enums.AccountStatus.Suspended;
			if (isSuspended) return true;

			return ServiceLocator.GetInstance<IWorkflowSettingRepository>().GetIsSuspended(accountId);
		}

		internal bool AccountWasRecentlyCancelled(int accountId)
		{
			return _recentAccountStatus.ContainsKey(accountId) && _recentAccountStatus[accountId] == Enums.AccountStatus.Cancelled;
		}

		internal bool AccountCanHaveActiveWorkflow(Enums.AccountStatus status)
		{
			return (status == Enums.AccountStatus.Suspended || status == Enums.AccountStatus.Paid || status == Enums.AccountStatus.Trial);
		}

		internal void ProcessAccountStatusChange(int accountId, Enums.AccountStatus status)
		{
			_recentAccountStatus[accountId] = status;

			try
			{
				// This app setting name references "Abort" for historical reasons.
				// Check is actually to see whether we should Pause workflow instances, not Abort them
				if (!AccountCanHaveActiveWorkflow(status) && Configuration.GetBooleanAppSetting("WorkflowManagerAbortWorkflowOnAccountStatus", true))
				{
					_data.PauseAllWorkflowsForAccountCancel(accountId, Constants.WorkflowUser);
					_data.SetPurgeDateForAllPausedWorkflows(accountId, Constants.WorkflowUser);
				}
			}
			catch (Exception ex)
			{
				_log.ErrorNoPII($"WorkflowManager:ProcessAccountStatusChange - Trouble aborting workflow for account Id {accountId}.", ex);
			}
		}

		/// <summary>
		/// Suspends revision and pauses all workflow instances of the revision
		/// </summary>
		internal void SuspendRevision(int accountId, Guid revisionUid)
		{
			try
			{
				var revision = _data.LoadRevisionByUid(accountId, Constants.WorkflowUser, revisionUid);
				if (revision != null && revision.Status == Enums.WorkflowRevisionStatus.Suspended)
				{
					_log.WarnNoPII($"WorkflowManager:SuspendRevision - Status is already {revision.Status} for revision Id {revisionUid} in  account Id {accountId} - No action taken.");
					return;
				}

				_log.InfoNoPII($"WorkflowManager:SuspendRevision - Suspending revision Id {revisionUid} in account Id {accountId}");
				_data.SuspendRevision(accountId, Constants.WorkflowUser, revisionUid); // Set revision status

				if (!Core.Domain.FeatureFlags.WorkflowFeatureFlags.DisablePauseEventsForRevisionSuspended)
				{
					_data.InsertPausesForRevisionSuspendedEvents(accountId, Constants.WorkflowUser, revisionUid); // Inserts PauseForRevisionSuspended workflow events
				}
				else
				{
					_log.InfoNoPII($"WorkflowManager:SuspendRevision - Pause for Revision suspended is disabled, revision Id {revisionUid} in account Id {accountId}");
				}

				TrySendRevisionSuspensionNotification(revision);
			}
			catch (Exception ex)
			{
				ex.Report();
				_log.ErrorNoPII($"WorkflowManager:SuspendRevision - Trouble setting revision status/inserting PauseForRevisionSuspended workflow events for revision Id {revisionUid} in  account Id {accountId}.", ex);
			}
		}

		/// <summary>
		/// Unsuspends revision and resumes all workflow instances of the revision
		/// </summary>
		internal void UnsuspendRevision(int accountId, Guid revisionUid)
		{
			try
			{
				var revision = _data.LoadRevisionByUid(accountId, Constants.WorkflowUser, revisionUid);
				if (revision != null && revision.Status == Enums.WorkflowRevisionStatus.Published)
				{
					_log.WarnNoPII($"WorkflowManager:UnsuspendRevision - Status is already {revision.Status} for revision Id {revisionUid} in  account Id {accountId} - No action taken.");
					return;
				}

				_log.WarnNoPII($"WorkflowManager:SuspendRevision - Unsuspending revision Id {revisionUid} in  account Id {accountId}");
				_data.UnsuspendRevision(accountId, Constants.WorkflowUser, revisionUid); // Set revision status
				_data.InsertResumeForSuspendedRevisionEvents(accountId, Constants.WorkflowUser, revisionUid); // Inserts ResumeForSuspendedRevision workflow events
			}
			catch (Exception ex)
			{
				ex.Report();
				_log.ErrorNoPII($"WorkflowManager:UnsuspendRevision - Trouble setting revision status/inserting PauseForRevisionSuspended workflow events for revision Id {revisionUid} in  account Id {accountId}.", ex);
			}
		}

