		/// <summary>
		/// Creates a new instance.
		/// </summary>
		public Guid CreateNewInstance(int accountId, Guid revisionUid, StartParameters startParameters, Guid instanceUid = default(Guid), bool ignoreLimit = false)
		{
			Revision revision = _data.LoadRevisionByUid(accountId, Constants.WorkflowUser, revisionUid);
			if (revision == null) throw new ApplicationException(string.Format("Failed to load workflow definition version {1} for account {0}.", accountId, revisionUid));
			ValidateRevisionIsNotSuspended(accountId, revision);

			WorkflowMap workflowMap = ServiceLocator.GetInstance<IDefinitionReaderFactory>().GetDefinitionReaderFromRevision(revision).BuildWorkflowMap(revision);
			return CreateNewInstance(accountId, revision, workflowMap, startParameters, instanceUid, ignoreLimit);
		}

		public Guid CreateNewInstance(int accountId, Revision revision, WorkflowMap workflowMap, StartParameters startParameters, Guid instanceUid = default(Guid), bool ignoreLimit = false )
		{
			ValidateRevisionIsNotSuspended(accountId, revision);

			var documentScopeRepository = ServiceLocator.GetInstance<IDocumentScopeRepository>();

			Instance instance = new Instance(revision)
			{
				StartBPMUserUid = startParameters.Submitter == null ? Guid.Empty : startParameters.Submitter.Uid,
				ParametersXml = startParameters.ParametersXml,
				ParentActivityUid = startParameters.ParentActivityUid,
				AlertParent = startParameters.AlertParent,
				Uid = instanceUid,
				ScopeUid = startParameters.ScopeUid == Guid.Empty? Guid.NewGuid() : startParameters.ScopeUid,
				SourceType = startParameters.SourceType,
				SourceId = startParameters.SourceId
			};
			if (!string.IsNullOrWhiteSpace(startParameters.RoutingApprovalInstanceName))
			{
				instance.Name = startParameters.RoutingApprovalInstanceName;
				instance.FromApproval = true;
			}

			instance.ScopeName = documentScopeRepository.GetScopeByUid(accountId, instance.ScopeUid)?.Name;

			if (string.IsNullOrEmpty(instance.ParametersXml)) instance.ParametersXml = WorkflowParameterBuilder.GetEmptyParametersAsString();

			// If we don't save successfully, but it's for a handled reason (eg, duplicate) then we just quit now.
			if (_data.CreateInstance(instance) != InstancePersistence.CreateResult.Success) return instance.Uid;

			// At this point we have a newly created instance
			Governor.IncrementStartInstanceCount(accountId);

			if (startParameters.InitialVariables != null && startParameters.InitialVariables.Count > 0)
			{
				InstanceVariables variables = new InstanceVariables();
				foreach (InstanceVariable initialVariable in startParameters.InitialVariables)
				{
					InstanceVariable variable = new InstanceVariable(initialVariable.AccountId, instance.Uid, initialVariable.Name, initialVariable.Type);
					variable.Value = initialVariable.Value;
					variable.IsSecret = initialVariable.IsSecret;

					variables.Add(variable);
				}
				_data.SaveInstanceVariables(accountId, Constants.WorkflowUser, variables);
			}

			bool reachedMaxInstancesInMemory = false;
			bool alreadyMaxedOutForAccount = false;
			bool isInPenaltyBox = false;
			if ((reachedMaxInstancesInMemory = !ignoreLimit && IsFull) || 
			    (alreadyMaxedOutForAccount = AlreadyMaxedOutForAccount(instance.AccountId)) || 
			    (isInPenaltyBox = Governor.IsInPenaltyBox(instance.AccountId)))
			{
				// Someone should process this when they can get around to it.
				UnlockInstance(instance.AccountId, instance.Uid, nextProcessDate: DateTime.MinValue);

				if(reachedMaxInstancesInMemory)
				{
					LogInstanceDeferredMaxInstancesInMemory(instance);
				}
				else if(alreadyMaxedOutForAccount)
				{
					LogInstanceDeferredMaxedOutForAccount(instance);
				}
				else if(isInPenaltyBox)
				{
					LogInstanceDeferredGovernanceLimitReached(instance);
				}
			}
			else
			{
				WorkflowInstance workflowInstance = new WorkflowInstance(instance, workflowMap, _data);
				QueueInstance(workflowInstance, ignoreLimit);
			}

			WorkflowScopeEvent.ScopeEventInfo scopeEventData = new WorkflowScopeEvent.ScopeEventInfo()
			{
				AccountId = instance.AccountId,
				ScopeUid = instance.ScopeUid,
				ScopeName = instance.ScopeName,
				WorkflowUid = instance.Uid,
				WorkflowId = instance.Id,
				WorkflowName = instance.Name,
				VersionNumber = revision.VersionNumber,
				RevisionNumber = revision.RevisionNumber,
				PeriodStart = instance.CreatedDate,
				EventType = (int)ScopeEventType.WorkflowStarted,
                EventBy = startParameters.Submitter == null ? null : new WorkflowScopeEvent.Assignee() { Email = startParameters.Submitter.EmailAddress, Name = startParameters.Submitter.FriendlyName, Uid = startParameters.Submitter.Uid }
            };
			WorkflowScopeEvent scopeEvent = new WorkflowScopeEvent(instance.AccountId, instance.Uid)
			{
				EventData = scopeEventData
			};
			EventRepo.FireEvent(instance.AccountId, scopeEvent);

			return instance.Uid;
		}

		public IWINSDataStore DataStore { get { return _data; } } 

		/// <summary>
		/// Executes an event and waits for it to be completed (synchronously); this method does not return until that has occurred,
		/// or an exception has occurred.  Timing out is an exception.
		/// </summary>
		public void ExecuteEvent(Event evt)
		{
			DateTime timeout = DateTime.UtcNow.AddSeconds(ApplicationSettings.WinsWaitForIdleTimeoutSeconds);
			while (DateTime.UtcNow <= timeout)
			{
				Exception e;
                // Note: This will give up if it can't get the threadlock for the instance -- this may
                // occur if there is another path on the workflow that is infinitely looping or similar. 
                // Ideally, this would be able to force the other worker/thread to stop and give up the lock,
                // but that is currently not implemented.
                WorkflowInstance instance = _LoadExisting(evt.AccountId, evt.InstanceUid, out var wasInMemory, n => n.ThreadLock.TryLock(ThreadOwnerId, _data.LockExpirationDate(), out e));
				if (instance != null && instance.ThreadLock.TryLock(ThreadOwnerId, _data.LockExpirationDate(), out e))
				{
					try
                    {
                        // Because we're acting synchronously here, we don't queue on failures -- the exception will alert
                        // the client instead.
                        instance.ExecuteEvent(evt, queueOnFailure: false);
						return;
					} finally
					{
						instance.ThreadLock.Unlock(ThreadOwnerId);
						FireOnWorkReceived();
					}
				}
				System.Threading.Thread.Sleep(500);
			}
			throw new TimeoutException(string.Format("Failed to process event {0} for instanceUid {1}", evt.CallbackName, evt.InstanceUid));
		}

		/// <summary>
		/// Number of additional instances this manager wants to have in memory
		/// </summary>
		public int FreeCapacity()
		{
			int capacity = MaxInstancesInMemory - Count;
			return Math.Max(capacity, 0);
		}

		/// <summary>
		/// Gets a control instance that lets us abort/pause/resume/resume from errors.
		/// Ideally this returns us a normal instance (with all the baggage that entails -- it'll be locked, ensuring no
		/// other service is processing it, etc).
		/// If we can't return a normal instance (eg, locked by another service), it returns the stripped down WorkflowControlInstance
		/// instead as a fallback.
		/// 
		/// The advantage of returning a normal instance when we can is a) locking (so no other service is processing it), and b) immediate
		/// notification to any workers that the things in a different state.
		/// </summary>
		public IWINSControlInstance GetControlInstance(int accountId, Guid instanceUid)
		{
			WorkflowInstance instance = LoadExisting(accountId, instanceUid);
			if (instance != null) return instance;
			return new WorkflowControlInstance(accountId, instanceUid);
		}

		public WorkflowInstance GetNextInstance(int lastAccountIdServiced = int.MinValue)
		{
			WorkflowInstance instance = GetFirstUnlockedInstance(lastAccountIdServiced);
			if (instance == null) return null;
			string message = null;
			//If Inmemory instance has already expired; then unfinished activities might not reflect the correct status; hence remove the instance so that is can be worked upon later. 
			if (!instance.HasExistingLockExpired())
			{
				// Ensure it's still locked in the database -- if it's not, we should remove it here.
				InstancePersistence.LockResult lockResult;
				if (LockInstance(instance.AccountId, instance, out lockResult))
				{
					DynamicSettingsContext.Items[DynamicSettingsContextItems.AccountId] = instance.AccountId;
					Governor.IncrementInstanceCount(instance.AccountId);
					return instance;
				}
				// If we get here, we couldn't lock it in the db.  We should remove it.
				message = $"Failed to lock instanceUid {instance.Uid} for lock owner {_data.LockOwner()}, received result: {lockResult}";
			}
			else
			{
				message = $"Lock has already expired on in-memory instance with instanceUid {instance.Uid} for lock owner {_data.LockOwner()}, lockexpirationdate: {instance.LockExpirationDate}";
			}

			_log.InfoNoPII(message);
			RemoveInstance(instance, message: message);
			return null;
		}

		public bool IsFull { get { return Count >= MaxInstancesInMemory; } }

		public bool IsAcceptingWork { get; set; } = true;

		internal bool AlreadyMaxedOutForAccount(int accountId)
		{
			if (Count < 1)
			{ // no need to do the calculation if we are idle
				return false;
			}

			int totalPerAccount = 0;
			try
			{
				using (_instances.GetReadLock())
				{
					totalPerAccount = _instances.Values.Count(instance => instance.AccountId == accountId);
				}
			}
			catch (Exception ex)
			{	// if we fail to count it should not cause a failure
				_log.WarnNoPII(string.Format("AlreadyMaxedOutForAccount could not count instances for accountId {0}.", accountId), ex);
			}

			// if adding one more for an account puts us over our configured max
			if ((((decimal)totalPerAccount + 1) / ((decimal)MaxInstancesInMemory)) >= (decimal)WorkflowSettingRepo.GetWorkflowManagerAccountMaxPercentOfWork())
			{
				return true;
			}
			return false;
		}

		public bool CanResume(Instance instance)
		{
			if (IsRevisionExecutionSuspended(instance.AccountId, instance.RevisionUid)) return false;
			if (instance.ParentActivityUid == Guid.Empty) return true;
			var parentActivity = _data.LoadActivityByUid(instance.AccountId, Constants.WorkflowUser, instance.ParentActivityUid);
			if (parentActivity == null) return true;
			var parentInstance = _data.LoadInstanceByUid(instance.AccountId, Constants.WorkflowUser, parentActivity.InstanceUid);
			if (parentInstance == null) return true;
			if (!instance.IsCompleted && (parentInstance.Status == Enums.WorkflowStepStatus.Aborted ||
			    parentInstance.Status == Enums.WorkflowStepStatus.Failed))
			{
				WINSManager client = new WINSManager();
				client.KillWorkflow(UserAccount.WorkflowUserAccount(instance.AccountId), instance.Uid, Resources.WINSResources.AbortedBecauseParentAborted);
				_log.InfoNoPII(string.Format("Parent of instance {0} failed or was aborted", instance.Uid));
				return false;
			}
			return true;
		}

		public bool ShouldAbortInstance(Instance instance)
		{
			if (WorkflowSettingRepo.GetAutomaticAbort(instance.AccountId))
			{
				var daysExecuting = (int) Math.Floor((DateTime.UtcNow - instance.CreatedDate).Duration().TotalDays);
				return (daysExecuting > GetExecutionDaysBeforeAbort(instance));
			}
			return false;
		}

