		private DateTime? GetLastAbortNotificationDate(Instance instance)
		{
			WorkflowInstance workflowInstance = LoadExisting(instance.AccountId, instance.Uid);
			return workflowInstance?.GetLastAbortNotificationDate();
		}

		private bool TrySaveLastAbortNotificationDate(Instance instance, DateTime lastAbortNotificationDate)
		{
			try
			{
				WorkflowInstance workflowInstance = LoadExisting(instance.AccountId, instance.Uid);
				if (workflowInstance == null)
				{
					return false;
				}

				workflowInstance.SaveLastAbortNotificationDate(instance.AccountId, instance.Uid, lastAbortNotificationDate);
			}
			catch (Exception ex)
			{
				_log.WarnNoPII("Could not save last abort notification date.", ex);
				return false;
			}

			return true;
		}

		public bool ShouldSendAbortNotification(Instance instance, out Enums.WorkflowAbortNotificationType abortNotificationType)
		{
			abortNotificationType = Enums.WorkflowAbortNotificationType.None;

			if (!WorkflowSettingRepo.GetEnableAbortNotifications(instance.AccountId) || !WorkflowSettingRepo.GetAutomaticAbort(instance.AccountId))
			{
				return false;
			}

			int daysExecuting = (int) Math.Floor((DateTime.UtcNow - instance.CreatedDate).Duration().TotalDays);
			var lastAbortNotificationDateLazy = new Lazy<DateTime?>(() => GetLastAbortNotificationDate(instance));
			int daysBeforeAbort = GetExecutionDaysBeforeAbort(instance);
			DateTime abortDate = instance.CreatedDate.AddDays(daysBeforeAbort);

			return ShouldSendAbortNotification(daysExecuting, lastAbortNotificationDateLazy, abortDate, daysBeforeAbort, out abortNotificationType);
		}

		public bool ShouldSendAbortNotification(int daysExecuting, Lazy<DateTime?> lastAbortNotificationDateLazy, DateTime abortDate, int daysBeforeAbort, out Enums.WorkflowAbortNotificationType abortNotificationType)
		{
			abortNotificationType = Enums.WorkflowAbortNotificationType.None;

			// No need to check last abort notification date if we aren't even half way there
			// Or if the workflow has been executing for longer than allowed
			int daysTillAbort = daysBeforeAbort - daysExecuting;
			if (daysTillAbort > (FiftyPercent * daysBeforeAbort) || daysTillAbort < 0)
			{
				return false;
			}

			// Don't send notification if we can't get last abort notification date
			DateTime? lastAbortNotificationDate = lastAbortNotificationDateLazy.Value;
			if (lastAbortNotificationDate == null)
			{
				_log.WarnNoPII("Could not get last abort notification date.");
				return false;
			}

			int daysBetweenLastNotificationAndAbort = (int) Math.Ceiling((abortDate - lastAbortNotificationDate.Value).TotalDays);
			if (daysTillAbort <= OneDay)
			{
				abortNotificationType = Enums.WorkflowAbortNotificationType.OneDay;
				return daysBetweenLastNotificationAndAbort > OneDay;
			}
			else if (daysTillAbort <= OneWeek)
			{
				abortNotificationType = Enums.WorkflowAbortNotificationType.OneWeek;
				return daysBetweenLastNotificationAndAbort > OneWeek;
			}
			else if (daysTillAbort <= OneMonth)
			{
				abortNotificationType = Enums.WorkflowAbortNotificationType.OneMonth;
				return daysBetweenLastNotificationAndAbort > OneMonth;
			}
			else if (daysTillAbort < (TenPercent * daysBeforeAbort))
			{
				abortNotificationType = Enums.WorkflowAbortNotificationType.TenPercent;
				return daysBetweenLastNotificationAndAbort >= (TenPercent * daysBeforeAbort);
			}
			else if (daysTillAbort < (TwentyFivePercent * daysBeforeAbort))
			{
				abortNotificationType = Enums.WorkflowAbortNotificationType.TwentyFivePercent;
				return daysBetweenLastNotificationAndAbort >= (TwentyFivePercent * daysBeforeAbort);
			}
			else if (daysTillAbort < (FiftyPercent * daysBeforeAbort))
			{
				abortNotificationType = Enums.WorkflowAbortNotificationType.FiftyPercent;
				return daysBetweenLastNotificationAndAbort >= (FiftyPercent * daysBeforeAbort);
			}
			else
			{
				return false;
			}
		}

		public bool TrySendAbortNotification(Instance instance, Enums.WorkflowAbortNotificationType abortNotificationType)
		{
			_log.InfoNoPII("Sending abort notification for instance.");

			// Don't send the email if we're unable to persist the date, so that we don't send duplicates
			DateTime? previousLastAbortNotificationDate = GetLastAbortNotificationDate(instance);
			if (previousLastAbortNotificationDate == null || !TrySaveLastAbortNotificationDate(instance, DateTime.UtcNow))
			{
				_log.WarnNoPII("Could not get or set last abort notification date.");
				return false;
			}

			try
			{
				UserAccounts accountSuperAdmins = ServiceLocator.GetInstance<IUserAccountRepository>()
					.LoadByAccountIdAndRole(instance.AccountId, Enums.UserRole.Primary);

				var email = new WorkflowAbortNotificationEmail
				{
					AccountId = instance.AccountId,
					Recipients = accountSuperAdmins,
					AbortDate = instance.CreatedDate.AddDays(GetExecutionDaysBeforeAbort(instance)),
					NotificationType = abortNotificationType,
					WorkflowId = instance.Id,
					WorkflowName = instance.Name
				};

				ServiceLocator.GetInstance<IEmailDistributor>().Init(email).QueueIt();
			}
			catch (Exception ex)
			{
				_log.WarnNoPII("Could not send abort notification.", ex);
				TrySaveLastAbortNotificationDate(instance, previousLastAbortNotificationDate.Value);
				return false;
			}

			return true;
		}

		public bool IsInstanceInMemory(Guid instanceUid)
		{
			return _instances.ContainsKey(instanceUid);
		}

		/// <summary>
		/// Returns a locked and loaded instance or returns null
		/// </summary>
		public WorkflowInstance LoadAndLockExisting(int accountId, Guid instanceUid, out bool wasInMemory)
        { 
            Exception e = null;
            var workflowInstance = _LoadExisting(accountId, instanceUid, out wasInMemory, n => n.ThreadLock.TryLock(ThreadOwnerId, _data.LockExpirationDate(), out e));
            if (e != null || workflowInstance == null)
            {
                _log.InfoNoPII($"Failed to load and lock {instanceUid} for owner {ThreadOwnerId}");
                return null;
            }

            return workflowInstance;
        }

        private WorkflowInstance _LoadExisting(int accountId, Guid instanceUid, out bool wasInMemory, InstanceSetupDelegate setup = null)
		{
			wasInMemory = _instances.TryGetValue(instanceUid, out WorkflowInstance instance);

			// If we are no longer accepting work, just return null
			// Unless the instance is already in memory and the caller hasn't passed a delegate method (ie tried to lock or queue the instance)
			// In which case we will just return the instance that's already in memory
			if (!IsAcceptingWork && !(wasInMemory && setup == null))
			{
				_log.InfoNoPII($"Did not load instanceUid {instanceUid} because manager is no longer accepting work.");
				return null;
			}

			if (wasInMemory)
			{
				if (setup != null) setup(instance);
				return instance;
			}

            instance = WorkflowInstance.LoadExisting(accountId, instanceUid, _data);
			if (instance == null) return null;

			if (!IsAcceptingWork)
			{
				_log.InfoNoPII($"Will not add instance to memory instanceUid {instanceUid} because manager is no longer accepting work.");
				return null;
			}

			lock (_instances)
			{
				if (_instances.ContainsKey(instanceUid))
				{
					if (setup != null) setup(_instances[instanceUid]);
					return _instances[instanceUid];
				}
				if (setup != null) setup(instance);
				_instances[instance.Uid] = instance;
				_log.DebugNoPII($"Added instanceUid {instance.Uid}; {_instances.Count} instances in memory");

                var workload = ManagerWorkload.CreateFromWorkflowInstance("InstanceAdded", instance);
				workload.InMemInstanceCount = _instances.Count;
                workload.Log();

                _signalNeedWork = true;
			}
			return instance;
		}

		public DateTime LockExpirationDate { get { return _data.LockExpirationDate(); } }

		/// <summary>
		/// Locks a workflow instance to this service; ensures that other services won't pick it up and work on it.
		/// Our locks have an expiration date -- calling this when we're already locked may still be done to set an
		/// expiration date further in the future.
		/// </summary>
		public bool LockInstance(int accountId, WorkflowInstance workflowInstance, out InstancePersistence.LockResult result)
		{
			DateTime lockExpirationDate;
			result = _data.LockInstance(accountId, workflowInstance.Uid, out lockExpirationDate);
			workflowInstance.LockExpirationDate = lockExpirationDate;
			return result == InstancePersistence.LockResult.Locked;
		}
		public bool LockInstance(int accountId, WorkflowInstance workflowInstance)
		{
			InstancePersistence.LockResult result;
			return LockInstance(accountId, workflowInstance, out result);
		}

