		/// <summary>
		/// Queues up a new activity to be executed in a specifc instance; it will try to keep this in-memory, if possible,
		/// but it may simply write to the db if we're at our limit of in-memory items.
		/// </summary>
		public void QueueActivity(InstanceActivity instanceActivity)
		{
			if (instanceActivity == null) throw new ArgumentNullException("instanceActivity");
			WorkflowInstance instance = _LoadExisting(instanceActivity.AccountId, instanceActivity.InstanceUid, out var wasInMemory, n => n.QueueActivity(instanceActivity));
			if (instance == null && instanceActivity.Uid == Guid.Empty) _data.CreateActivity(instanceActivity);
		}

		/// <summary>
		/// Queues up an event to be executed in a specifc instance; it will try to keep this in-memory, if possible,
		/// but it may simply write to the db if we're at our limit of in-memory items.
		/// </summary>
		public void QueueEvent(Event e)
		{
			if (e == null) throw new ArgumentNullException("e");
			WorkflowInstance instance = null;
			try
			{
                instance = _LoadExisting(e.AccountId, e.InstanceUid, out var wasInMemory, n => n.QueueEvent(e));

			}
			catch (InstanceLockedException ex)
			{
				_log.WarnNoPII($"QueueEvent: Instance locked, could not enqueue event in-memory, InstanceUid {e.InstanceUid} ActivityUid {e.ActivityUid} with callback {e.CallbackName}, creating event in data store instead: {ex.Message}");
			}
			catch (ApplicationException ex)
			{
				_log.WarnNoPII(string.Format("Exception queueing event for InstanceUid {0} ActivityUid {1} with callback {2}, creating event in data store instead.", e.InstanceUid, e.ActivityUid, e.CallbackName), ex);
			}
			finally
			{
				if (instance == null && e.Uid == Guid.Empty)
				{
					CreateEvent(e);
					WorkflowEventWorkload wrk = new WorkflowEventWorkload(e, "EventCreatedOnNotQueued") { WorkflowDB = DataStore.GetDbNameForAccount(e.AccountId) };
					wrk.Log();
				}
				else
				{
					FireOnWorkReceived();
				}
			}
		}

		/// <summary>
		/// Skips the in-memory queue check of the QueueEvent method and inserts into
		/// BPMEvent table for later consumption
		/// </summary>
		public void CreateEvent(Event e)
		{
			if (e == null) throw new ArgumentNullException("e");
			_data.CreateEvent(e);
		}

		/// <summary>
		/// Queues up an instance to be processed by this service.  This may result in a no-op if we're
		/// already at our limit.  
		/// As a side effect, locks the instance to us if it's keeping it in memory (this will not occur if we're at our limit)
		/// </summary>
		public void QueueInstance(WorkflowInstance instance, bool ignoreLimit = false)
		{
			if (instance == null) throw new ArgumentNullException("instance");
			if (_instances.ContainsKey(instance.Uid)) throw new ApplicationException(string.Format("Duplicate queueing of instanceUid {0}", instance.Uid));
			// Don't bother if we're over our limit or we aren't accepting any more work
			if ((!ignoreLimit && IsFull) || !IsAcceptingWork)
			{
				_log.InfoNoPII($"Did not queue instanceUid {instance.Uid}. IsFull - {IsFull}; IsAcceptingWork - {IsAcceptingWork}");
				// Someone should process this when they can get around to it.
				UnlockInstance(instance.AccountId, instance.Uid, nextProcessDate: DateTime.MinValue);
				return;
			}
			// Don't bother if we can't lock it
			if (!LockInstance(instance.AccountId, instance)) return;
			bool success = false;
			try
			{
				lock (_instances)
				{
					// Double check stuff now that we have our _instances lock
					if (_instances.ContainsKey(instance.Uid)) throw new ApplicationException(string.Format("Duplicate queueing of instanceUid {0}", instance.Uid));
					if ((!ignoreLimit && IsFull) || !IsAcceptingWork)
					{
						_log.InfoNoPII($"Did not queue instanceUid {instance.Uid} after db lock. IsFull - {IsFull}; IsAcceptingWork - {IsAcceptingWork}");
						return;
					}
					_instances.Add(instance.Uid, instance);
					_log.InfoNoPII($"Queued instanceUid {instance.Uid}; {_instances.Count} instances in memory.");

                    var workload = ManagerWorkload.CreateFromWorkflowInstance("InstanceQueued", instance);
                    workload.InMemInstanceCount = _instances.Count;
					workload.Log();

					_signalNeedWork = true;
					success = true;
				}
			} finally
			{
				if (!success)
				{
					// Do not remove the instance if it's already in the queue
					if (!_instances.ContainsKey(instance.Uid))
					{
						RemoveInstance(instance, nextProcessDate: DateTime.MinValue, message: string.Format("Too many items in memory: {0} of {1}", Count, MaxInstancesInMemory));
					}
					else
					{
						_log.InfoNoPII($"Instance {instance.Uid} is already in memory, so it will not be removed");
					}
				}
			}
			FireOnWorkReceived();
		}

		public void QueueInstance(Instance instance, bool ignoreLimit = false)
		{
			if (_instances.ContainsKey(instance.Uid)) return;
			if (!ignoreLimit && IsFull)
			{
				// Someone should process this when they can get around to it.
				UnlockInstance(instance.AccountId, instance.Uid, nextProcessDate: DateTime.MinValue);
				LogInstanceDeferredMaxInstancesInMemory(instance);
				return;
			}
			WorkflowInstance wfInstance = WorkflowInstance.LoadExisting(instance.AccountId, instance.Uid, _data);
			QueueInstance(wfInstance, ignoreLimit);
		}

		/// <summary>
		/// Removes all of our locks on an instance, and removes it.  Use this when we know we're done with the instance.
		/// </summary>
		public void RemoveInstance(WorkflowInstance instance, DateTime? nextProcessDate = null, string message = null)
		{
			if (instance == null) throw new ArgumentNullException("instance");
			// Reduce race conditions by making sure nobody else can lock, or has locked, the instance before trying to remove it
			if(!instance.ThreadLock.TryLock(ThreadOwnerId, DateTime.UtcNow.AddSeconds(10), out Exception ex))
			{
				_log.InfoNoPII($"Tried to remove instance {instance.Uid} while locked by another thread (Current:{ThreadOwnerId}, Owner:{instance.ThreadLock.Owner})");
				return;
			}

            try
            {
                instance.SaveToDatabase();
            }
            catch (Exception e)
            {
                _log.ErrorNoPII(string.Format("Failed to save unsaved information for instance {0} to db", instance.Uid), e);
            }

            // We reverse the locking mechanism (lock in memory first, then lock in db) here -- unlock db 
            // first, then unlock in memory to avoid any in-memory race conditions.
            try
			{
				UnlockInstance(instance.AccountId, instance.Uid, nextProcessDate);
			}
			catch (Exception excp)
			{
				_log.ErrorNoPII(string.Format("Failed to unlock instanceUid {0} for lock owner {1}", instance.Uid, _data.LockOwner()), excp);
			}

			lock (_instances)
			{
				Exception e;
				instance.ThreadLock.TryUnlock(ThreadOwnerId, out e);
				if (_instances.ContainsKey(instance.Uid))
				{
					_instances.Remove(instance.Uid);
                    var workload = ManagerWorkload.CreateFromWorkflowInstance("InstanceRemoved", instance);
                    workload.InMemInstanceCount = _instances.Count;
					workload.Log();
				}

				_log.DebugNoPII($"Removed instanceUid {instance.Uid} ({message ?? "No message"}); {_instances.Count} instances in memory");
			}

			// Save any remaining events in queue on the off chance that an event came in between the last SaveToDatabase and the instance being removed
			instance.SaveEventsToDatabase();

			Governor.EmitActivityExecutionCountExceededTelemetry(instance.AccountId, instance.Uid, instance.RevisionUid, instance.RevisionCreatedDate, instance.ActivityExecutionCount, instance.ActivityExecutionCountEnforcedLimit, instance.IsActivityExecutionLimitReached);

			_signalNeedWork = true;
		}

		/// <summary>
		/// Removes locks and persist events/variables for all instances that are not being worked on by a thread
		/// Use this when manager needs to stop working
		/// </summary>
		public void RemoveAllUnlockedInstances(DateTime? nextProcessDate, string message)
		{
			using (ScmNDC.Push($"M:{message ?? "No message"}"))
			{
				_log.InfoNoPII($"Removing all unlocked instances. Next process date {nextProcessDate}");
				lock (_instances)
				{
					// Find all unlocked instances and lock them first, in case a thread is about to pick one up
					var unlockedInstances = new List<Guid>();
					foreach (WorkflowInstance instance in _instances.Values)
					{
						using (ScmNDC.Push($"W:{instance.Uid}"))
						{
							// Would be better to use a TryLockIfNotLocked method...
							if (instance.ThreadLock.IsLocked)
							{
								_log.InfoNoPII("Instance is already locked.");
							}
							else if (instance.ThreadLock.TryLock(ThreadOwnerId, _data.LockExpirationDate(), out Exception ex))
							{
								unlockedInstances.Add(instance.Uid);
							}
							else
							{
								_log.InfoNoPII("Could not lock instance.", ex);
							}
						}
					}

					// Remove locks and persist all of the previously unlocked instances
					foreach (Guid instanceUid in unlockedInstances)
					{
						using (ScmNDC.Push($"W:{instanceUid}"))
						{
							_log.InfoNoPII("Attempting to remove instance");

							try
							{
								RemoveInstance(_instances[instanceUid], nextProcessDate, message);
								_log.InfoNoPII("Removed instance from memory, removed locks, and persisted events/variables");
							}
							catch (Exception e)
							{
								_log.ErrorNoPII("Failed to remove locks on instance", e);
							}
						}
					}
				}
			}
		}

        public void RemoveLock(WorkflowInstance instance)
        {
            instance.ThreadLock.Unlock(ThreadOwnerId);
            _log.DebugNoPII($"Removed thread lock for instanceUid {instance.Uid}; {_instances.Count} instances in memory");
        }

        /// <summary>
        /// Unlocks an instance from us.  This should only be called when we're done with it.
        /// </summary>
        public void UnlockInstance(int accountId, Guid instanceUid, DateTime? nextProcessDate = null)
        {
            _data.UnlockInstance(accountId, instanceUid, nextProcessDate);
		}

		/// <summary>
		/// Goes through each instance, find the lockable one with the oldest lastprocessed date, and tries to lock it to this thread.  
		/// Will also attempt to give preference to an instance that is for an Account that was not last serviced.
		/// Returns it if it can, returns null if there's nothing it could do.
		/// </summary>
		/// <returns></returns>
		private WorkflowInstance GetFirstUnlockedInstance(int lastAccountIdServiced)
		{
			if (_instances.Count > 0)
			{
				string owner = ThreadOwnerId;
				DateTime expiration = _data.LockExpirationDate();

				WorkflowInstance first = null;
				WorkflowInstance firstNew = null;

				lock (_instances)
				{
					var instancesInPenaltyBox = new List<WorkflowInstance>();
					foreach (WorkflowInstance instance in _instances.Values)
					{
						if (!instance.ThreadLock.CanLock(owner)) continue;

						DynamicSettingsContext.Items[DynamicSettingsContextItems.AccountId] = instance.AccountId;

						if (Governor.IsInPenaltyBox(instance.AccountId))
						{
							instancesInPenaltyBox.Add(instance);
							continue;
						}

						// try to find the instance that is oldest in the list
						if (first == null || instance.LastProcessDate < first.LastProcessDate) first = instance;
						if (instance.AccountId != lastAccountIdServiced && (firstNew == null || instance.LastProcessDate < firstNew.LastProcessDate)) firstNew = instance;
					}

					foreach (WorkflowInstance instance in instancesInPenaltyBox)
					{
						_log.InfoNoPII($"Removing instance {instance.Uid} as it's account ({instance.AccountId}) is in the penalty box");
						RemoveInstance(instance);
						LogInstanceDeferredGovernanceLimitReached(instance.Model);
					}

					Exception e;
					if (firstNew != null && firstNew.ThreadLock.TryLock(owner, expiration, out e)) return firstNew;
					if (first != null && first.ThreadLock.TryLock(owner, expiration, out e)) return first;
				}
			}
			// This gets hit if we found no work to do.
			if (_signalNeedWork && OnNeedWorkEvent != null) OnNeedWorkEvent();
			_signalNeedWork = false;
			return null;
		}

		private static string ThreadOwnerId
		{
			get { return System.Threading.Thread.CurrentThread.ManagedThreadId.ToString(); }
		}

		private void FireOnWorkReceived()
		{
			try
			{
				if (OnWorkReceivedEvent != null) OnWorkReceivedEvent();
			} catch
			{
				// Ignore
			}
		}

		public void LogInstanceDeferredGovernanceLimitReached(Instance instance)
		{
			LogInstanceDeferred(instance, "InstanceDeferredGovernanceLimitReached");
		}

		public void LogInstanceDeferredMaxedOutForAccount(Instance instance)
		{
			LogInstanceDeferred(instance, "InstanceDeferredMaxedOutForAccount");
		}

		public void LogInstanceDeferredMaxInstancesInMemory(Instance instance)
		{
			LogInstanceDeferred(instance, "InstanceDeferredMaxInstancesInMemory");
		}

		private void LogInstanceDeferred(Instance instance, string action)
        {
			var workload = ManagerWorkload.CreateFromInstance(action, instance);
			workload.WorkflowDB = DataStore.GetDbNameForAccount(instance.AccountId);
            workload.InMemInstanceCount = _instances.Count;
			workload.Log();
		}

		private int GetExecutionDaysBeforeAbort(Instance instance)
		{
			Revision revision =
				_data.LoadRevisionByUid(instance.AccountId, Constants.WorkflowUser, instance.RevisionUid);

			if (revision == null)
			{
				_log.ErrorNoPII($"Revision does not exist {instance.RevisionUid}");
			}
			else if(revision.CreatedDate >= WorkflowSettingRepo.GetExecutionDaysBeforeAbortEffectiveDate(instance.AccountId))
			{
				return WorkflowSettingRepo.GetExecutionDaysBeforeAbort(instance.AccountId);
			}

			return WorkflowSettingRepo.GetExecutionDaysBeforeAbortLegacy(instance.AccountId);
		}

		public bool IsRevisionExecutionSuspended(int accountId, Guid revisionUid)
		{
			return RevisionService.IsRevisionSuspended(accountId, UserAccount.WorkflowUserAccount(accountId).Uid, revisionUid);
		}

		public bool IsInstanceRevisionExecutionSuspended(int accountId, Guid instanceUid, out Guid revisionUid)
		{
			Guid userAccountUid = UserAccount.WorkflowUserAccount(accountId).Uid;
			Instance instance = _data.LoadInstanceByUid(accountId, userAccountUid, instanceUid);
			revisionUid = instance.RevisionUid;
			return IsRevisionExecutionSuspended(accountId, instance.RevisionUid);
		}

		private void ValidateRevisionIsNotSuspended(int accountId, Revision revision)
		{
			if (revision.Status == Enums.WorkflowRevisionStatus.Suspended)
			{
				string message =
					$"Revision {revision.Uid} for definition {revision.DefinitionUid} and account {accountId} is currently suspended";
				_log.WarnNoPII(message);
				throw new ApplicationException(message);
			}
		}

		private bool TrySendRevisionSuspensionNotification(Revision revision)
		{
			var accountId = revision.AccountId;

			try
			{
				var emailRecipients = new List<string>();

				UserAccount notificationUser = null;
				var notificationUserUid = UserAccount.WorkflowUserAccount(accountId).Settings.AdvancedWorkflowNotificationUserAccountUid;
				if (notificationUserUid != Guid.Empty)
				{
					_log.InfoNoPII($"Loading {nameof(UserAccount)} {notificationUserUid} to receive workflow revision suspension notification");
					notificationUser = ServiceLocator.GetInstance<UserAccountRepository>().LoadByUid(accountId, notificationUserUid);
				}

				if (!string.IsNullOrEmpty(notificationUser?.EmailAddress))
				{
					emailRecipients.Add(notificationUser.EmailAddress);
				}

				if (!emailRecipients.Any())
				{
					_log.InfoNoPII($"There is no AdvancedWorkflowNotificationUser configured. Unable to send workflow revision suspension notification for account '{accountId}' and revision '{revision.Uid}'.");
					return false;
				}

				var email = new WorkflowRevisionSuspensionNotificationEmail
				{
					AccountId = accountId,
					WorkflowRevisionUid = revision.Uid,
					WorkflowRevisionName = revision.Name,
					EmailRecipients = emailRecipients
				};

				ServiceLocator.GetInstance<IEmailDistributor>().Init(email).QueueIt();

				return true;
			}
			catch (Exception ex)
			{
				_log.ErrorNoPII($"An exception occurred while sending workflow revision suspension notification for account '{accountId} and revision {revision.Uid}'", ex);
				ex.Report();
				return false;
			}
		}
	}
}

﻿using System;
using ADEXS.Core.Domain.Repository.Workflow;
using ADEXS.Core.Domain.Repository.Workflow.Interfaces;
using ADEXS.Core.Domain.Repository.Workflow.PropertyExpressions;

namespace ADEXS.Atlas.Workflow.Activities.PropertyTypes
{
	[Serializable]
	public class StringProperty : IProperty<string>
	{
		private readonly IPropertyExpression _propertyExpression;

		public StringProperty(IPropertyExpression propertyExpression)
		{
			_propertyExpression = propertyExpression;
		}

		bool IProperty.TryEvaluate(IWorkflowContext ctx, out object result)
		{
			string value;
			bool valid = TryEvaluate(ctx, out value);
			result = value;
			return valid;
		}

		bool IProperty.TryEvaluateWithoutContext(out object result)
		{
			string value;
			bool valid = TryEvaluateWithoutContext(out value);
			result = value;
			return valid;
		}

		public string Evaluate(IWorkflowContext ctx)
		{
			string value;
			TryEvaluate(ctx, out value);
			return value;
		}

		public bool IsEmpty
		{
			get { return _propertyExpression == null || _propertyExpression.IsEmpty; }
		}

		public object Serialize()
		{
			return _propertyExpression.Serialize();
		}

		public string Text
		{
			get { return _propertyExpression.Text; }
		}

		public bool TryEvaluate(IWorkflowContext ctx, out string result)
		{
			string errorMessage;
			if (!_propertyExpression.TryEvaluate(ctx, out result, out errorMessage)) return false;
			WorkflowExpression expression = WorkflowExpression.Evaluate(ctx, result);
			result = expression.ProcessedExpression;
			return expression.IsValid;
		}

		public bool TryEvaluateWithoutContext(out string result)
		{
			string errorMessage;
			if (!_propertyExpression.TryEvaluateWithoutContext(out result, out errorMessage)) return false;
			WorkflowExpression expression = WorkflowExpression.EvaluateWithoutContext(result);
			result = expression.ProcessedExpression;
			return expression.IsValid;
		}

		/// <summary>
		/// This exists so you can do 'MyActivity.MyProperty = "blah";'.  Mostly used by the unit tests.
		/// </summary>
		public static implicit operator StringProperty(string s)
		{
			return new StringProperty(new StringPropertyExpression("Literal", s));
		}
	}
}

