public class HttpRedirectEnrichmentMiddleware
{
    private readonly RequestDelegate _next;
    public HttpRedirectEnrichmentMiddleware(RequestDelegate next) => _next = next;

    public async Task Invoke(HttpContext context)
    {
        var activity = Activity.Current;
        // Capture Referer early (request already read)
        var referer = context.Request.Headers["Referer"].FirstOrDefault();
        if (!string.IsNullOrEmpty(referer))
            activity?.SetTag("HTTP_REFERER", referer);

        // Capture redirect location after response completed
        context.Response.OnStarting(state =>
        {
            var httpContext = (HttpContext)state!;
            if (httpContext.Response.StatusCode >= 300 && httpContext.Response.StatusCode <= 399)
            {
                var location = httpContext.Response.Headers["Location"].FirstOrDefault();
                if (!string.IsNullOrEmpty(location))
                    Activity.Current?.SetTag("HTTP_RESPONSE_REDIRECT_LOCATION", location);
            }
            return Task.CompletedTask;
        }, context);

        await _next(context);
    }
}

