﻿using System;
using System.Collections.Specialized;
using System.Diagnostics;
using System.Text;
using System.Threading;
using System.Web;

using ADEXS.Core.Domain;
using ADEXS.Core.Domain.Model;
using ADEXS.Core.Domain.Utilities;
using ADEXS.Core.Types;
using ADEXS.Core.Util.Loggers;
using ADEXS.Web.Common.Helpers;

using DocuSign.DynamicSettings;
using DocuSign.Monitoring;

namespace ADEXS.Web.Common;
/// <summary>
/// Temporary Base class to redirect the ASHX API call to new Controller API as part of the effort of web migration to .net6
/// This will eventually cleaned up once we move to .net6
/// </summary>
public abstract class BaseApiHandler : IHttpHandler
{
	[EventSource("Exception", "CLM.FeatureFlags.PopulateFeatureFlagContext")]
	[Counter("Exception", "CLM.FeatureFlags.PopulateFeatureFlagContext", InstanceProperty = "Description")]
	private static ExceptionEventSource _featureFlagContextExceptionEventSource = new ExceptionEventSource();

	private static readonly IDiscreetLogger Logger = DiscreetLogger.GetLogger(typeof(BaseApiHandler));

	// reletive path for new controller api. ex) "A/TileView/GetTiles"
	// where full url is https://localhost/atlas/A/TileView/GetTiles
	protected abstract string Path { get; }

	public virtual bool IsReusable { get; set; }

	public virtual void ProcessRequest(HttpContext context)
	{
		try
		{
			PopulateFeatureFlagContext(AtlasContext.Current.UserAccount, context);
			Activity.Current?.SetTagsWithCommonFields(context);
		}
		catch (Exception ex)
		{
			Logger.ErrorNoPII($"Failed to Populate Feature Flag Context: {LogData()}", ex);
		}

		if (ShouldRedirectHelper())
		{
			Redirect(context);
		}
		else
		{
			HandleRequest(context);
		}
	}

	protected abstract bool ShouldRedirect();
	protected abstract void HandleRequest(HttpContext context);

	private string LogData()
	{
		return $"aid:{AtlasContext.Current.AccountId} uid:{AtlasContext.Current.UserAccountUid} relativeUrl: {Path}";
	}
	private void Redirect(HttpContext context)
	{
		try
		{
			if (context == null)
			{
				throw new ArgumentNullException(nameof(context));
			}

			// Get the original path info
			var pathInfo = !string.IsNullOrEmpty(context.Request.PathInfo) ? context.Request.PathInfo : "";

			// Get the original query string
			var queryString = context.Request.QueryString;

			// URL-encode pathInfo and queryString before building the new URL
			var encodedPathInfo = HttpUtility.UrlPathEncode(pathInfo);
			var encodedQueryString = ToUrlEncodedQueryString(queryString);

			var baseUrl = AtlasUrlHelper.WebApplicationBasePath();
			var newUrl = $"{baseUrl}/{Path}{encodedPathInfo}{encodedQueryString}";

			// Set the status code to 307 Temporary Redirect
			context.Response.StatusCode = Microsoft.AspNetCore.Http.StatusCodes.Status307TemporaryRedirect;

			// Set the Location header with the new Controller URL
			context.Response.AddHeader("Location", newUrl);

		}
		catch (ThreadAbortException)
		{
			// Do nothing intentionally.
			// The above Redirect call is *meant* to abort the thread with an exception and we do _not_ want it caught by the below catch block.
			// See this article for why we are catching it here and why we do nothing in this block.
			// https://learn.microsoft.com/en-us/dotnet/standard/threading/destroying-threads#handling-threadabortexception
		}
		catch (Exception ex)
		{
			Logger.ErrorNoPII($"Failed to Redirect: {LogData()}", ex);
			throw;
		}
	}

	private bool ShouldRedirectHelper()
	{
		try
		{
			return ShouldRedirect();
		}
		catch (Exception ex)
		{
			Logger.ErrorNoPII($"Failed to evaluate ShouldRedirect: {LogData()}", ex);
			return false;
		}
	}

	private static void PopulateFeatureFlagContext(UserAccount currentUser, HttpContext context)
	{
		if (context == null)
		{
			throw new ArgumentNullException(nameof(context));
		}

		if (currentUser == null || !ApplicationSettings.EnableFeatureFlagsContext)
		{
			if (AtlasContext.Current.AccountId > 0)
			{
				DynamicSettingsContext.Items[DynamicSettingsContextItems.AccountId] = AtlasContext.Current.AccountId;
			}
			else if (context.Request.QueryString[Constants.QueryStringParameters.AccountIdAbbreviated] != null)
			{
				if (int.TryParse(context.Request.QueryString[Constants.QueryStringParameters.AccountIdAbbreviated], out var accountId))
				{
					DynamicSettingsContext.Items[DynamicSettingsContextItems.AccountId] = accountId;
				}
			}


			return;
		}

		try
		{
			DynamicSettingsContext.Items[DynamicSettingsContextItems.AccountId] = currentUser.AccountId;
			DynamicSettingsContext.Items[DynamicSettingsContextItems.UserId] = currentUser.Uid;
			if (currentUser.Account != null)
			{
				DynamicSettingsContext.Items[DynamicSettingsContextItems.AccountType] = currentUser.Account.AccountType;
				DynamicSettingsContext.Items[DynamicSettingsContextItems.PlanName] = NpsUtilities.GetPlanIdName(currentUser.Account);
			}
		}
		catch (Exception ex)
		{
			_featureFlagContextExceptionEventSource.Emit(ex);
		}
	}

	private static string ToUrlEncodedQueryString(NameValueCollection collection)
	{
		if (collection == null || collection.Count == 0)
		{
			return string.Empty;
		}

		var builder = new StringBuilder();
		var first = true;
		for (var i = 0; i < collection.Count; i++)
		{
			var key = collection.GetKey(i);
			var value = collection.Get(i);
			builder.Append(first ? '?' : '&');
			first = false;
			builder.Append(HttpUtility.UrlEncode(key));
			builder.Append('=');
			builder.Append(HttpUtility.UrlEncode(value));
		}

		return builder.ToString();
	}
}

﻿using System;
using System.Diagnostics;
using System.IO;
using System.Runtime.Serialization.Formatters.Binary;
using System.Threading;
using System.Web;
using System.Web.SessionState;
using ADEXS.Core.Domain;
using ADEXS.Core.Domain.Model;
using ADEXS.Core.Domain.RequestHeaders;
using ADEXS.Core.Domain.Utilities;
using ADEXS.Core.Types;
using ADEXS.Core.Util.Config.Context;
using ADEXS.Core.Util.Core;
using ADEXS.Core.Util.Loggers;
using ADEXS.Core.Util.ServiceLocator;
using ADEXS.Web.Common.MVC;
using ADEXS.Web.Common.Session;

using DocuSign.CLM.Monitoring;
using DocuSign.Monitoring;
using log4net;

namespace ADEXS.Web.Common.Profile
{
	public class ProfileModule : IHttpModule
	{
		private static readonly IDiscreetLogger _log = DiscreetLogger.GetLogger("ADEXS.Web.Common");


		private PerformanceLog _performanceLog;
		private IDisposable _logContext;

		[EventSource("Session Size", "CLM.SessionState")]
		[Counter("Session Size", "CLM.SessionState", DisplayUnit = CounterUnit.kilobytes,
			Ranges = new double[10] { 1, 5, 10, 50, 100, 500, 1000, 5000, 10000, 20000 },
			MeasureProperty = "Count", InstanceProperty = "Description", ShowVolume = true)]
		private static readonly CountEventSource _sessionSizeEventSource = new CountEventSource();

		[EventSource("Session Serialization Timing", "CLM.SessionState")]
		[Counter("Session Serialization Timing", "CLM.SessionState", DisplayUnit = CounterUnit.milliseconds,
			Ranges = new double[10] { 10, 20, 50, 100, 200, 500, 1000, 2000, 5000, 10000 },
			MeasureProperty = "Duration", SuccessProperty = "Success", InstanceProperty = "Description")]
		private static readonly TimingEventSource _sessionSerializationTimingEventSource = new TimingEventSource();

		[EventSource("Large Session", "CLM.SessionState")]
		[Counter("Large Session", "CLM.SessionState")]
		private static readonly PropertyBagEventSource _largeSessionEventSource = new PropertyBagEventSource();

		/// <summary>
		/// You will need to configure this module in the web.config file of your
		/// web and register it with IIS before being able to use it. For more information
		/// see the following link: http://go.microsoft.com/?linkid=8101007
		/// </summary>
		#region IHttpModule Members

		public void Dispose()
		{
			//clean-up code here.
		}

		public void Init(HttpApplication context)
		{
			// Below is an example of how you can handle LogRequest event and provide
			// custom logging implementation for it
			context.BeginRequest += OnBeginRequest;
			context.PreRequestHandlerExecute += OnPreRequestHandlerExecute;
			context.PostRequestHandlerExecute += OnPostRequestHandlerExecute;
			context.EndRequest += OnEndRequest;
			context.Error += OnError;
		}

		/* IIS request life cycle BeginRequest ->  AuthenticateRequest -> AuthorizeRequest -> ResolveRequestCache -> AcquireRequestState ->
		 * PreRequestHandlerExecute -> ProcessRequest -> PostRequestHandlerExecute -> ReleaseRequestState -> UpdateRequestCache -> EndRequest */

		private void OnBeginRequest(object sender, EventArgs e)
		{
			if (_performanceLog == null)
				_performanceLog = new PerformanceLog();

			_performanceLog.Start(HttpContext.Current.Request);
		}

		private void OnPostRequestHandlerExecute(object sender, EventArgs e)
		{
			Activity.Current?.SetTagsWithCommonFields(HttpContext.Current);
			LogSessionSize(HttpContext.Current?.Session);
			_performanceLog.SetSession(HttpContext.Current?.Session);
		}

		private void OnPreRequestHandlerExecute(object sender, EventArgs e)
		{
			try
			{
				HttpContext context = HttpContext.Current;
				AtlasContext.Reset();

				// MonitoringContext is initialized in the ScmLogRequestHttpModule module on
				// BeginRequest. This runs before this event handler. By this time the
				// CorrelationToken is set. Including getting it from the X-DocuSign-CorrelationToken header if available.
				Guid corrId = MonitoringContext.CorrelationToken ?? Guid.Empty;
				if (Guid.Empty == corrId)
				{
					// Only in extremely unlikely scenario
					Guid.TryParse(AtlasContext.Current.TransactionId, out corrId);
					MonitoringContext.CorrelationToken = corrId;
				}
				else
				{
					AtlasContext.Current.TransactionId = corrId.ToString();
				}

				_logContext?.Dispose();
				// Now we'll have TransactionId, CorrelationId, and CorrelationToken all set to the same value. Three is a crowd?...
				_logContext = LogContext.Set(AtlasContext.Current.TransactionId);

				if (_log.IsDebugEnabled)
				{
					_log.DebugNoPII($"ServiceLocator:\n{ServiceLocator.Container.WhatDoIHave(typeof(ILogContextStore))}");
				}

				if (_performanceLog != null) _performanceLog.Reset();
				if (context != null)
				{
					if (context.Request != null)
					{
						AtlasContext.Current.ClientIpAddress = IPAddressUtil.FetchIpAddress(context.Request);
						AtlasContext.Current.ClientUserAgent = context.Request.UserAgent;
					}

					// Similar code exists for web services, which don't use session.
					// That code is in SetContextIds
					HttpSessionState session = context.Session;
					if (session != null)
					{
						UserAccount user = CommonData.CurrentUser;
						if (user != null)
						{
							AtlasContext.Current.AccountId = user.AccountId;
							AtlasContext.Current.UserAccountUid = user.Uid;
							LogicalThreadContext.Properties["accountid"] = user.AccountId == int.MinValue ? "null" : user.AccountId.ToString();

							var userContextSetter = AtlasWebDependencyResolver.ScopedInstance?.GetService<IUserContextSetter>();
							if (userContextSetter != null)
							{
								_log.InfoNoPII($"The user's AccountId: {user.AccountId} for user id: {user.Uid} in OnPreRequestHandlerExecute.");
								// ensure that DI consumers have access the user/account context
								userContextSetter.AccountId = user.AccountId;
								userContextSetter.UserId = user.Uid;
							}
							else
							{
								_log.WarnNoPII("No UserContextSetter is available - account context propagation will not work.");
							}

							var docusignAccountId = CommonData.DocuSignAccountId;
							if (!string.IsNullOrEmpty(docusignAccountId))
								AtlasContext.Current.DocuSignAccountId = docusignAccountId;

							var docusignUserId = CommonData.DocuSignUserId;
							if (!string.IsNullOrEmpty(docusignUserId))
								AtlasContext.Current.DocuSignUserId = docusignUserId;
						}
					}
				}
			}
			catch (Exception ex)
			{
				_log.WarnNoPII("Trouble OnPreRequestHandlerExecute in ProfileModule.", ex);
				ex.Report();
			}
		}

		/// <summary>
		/// Measuring session size ATL-56207
		/// </summary>
		private static void LogSessionSize(HttpSessionState session)
		{
			if (session == null || !Configuration.GetBooleanAppSetting("EnableSessionSizeLogging", false))
				return;

			try
			{
				long totalSessionBytes = 0;
				string largestItemKey = string.Empty;
				long largestItemSizeBytes = 0;
				int itemCount = 0;

				using (OperationTimer sessionSerializationTimer = _sessionSerializationTimingEventSource.CreateTimer())
				{
					BinaryFormatter formatter = new BinaryFormatter();
					foreach (var sessionKey in session)
					{
						using (Stream s = new MemoryStream())
						{
							string key = (string)sessionKey;
							var item = session[key];
							if (item != null)
							{
								itemCount++;

								formatter.Serialize(s, item);

								if (s.Length > largestItemSizeBytes)
								{
									largestItemKey = key;
									largestItemSizeBytes = s.Length;
								}

								totalSessionBytes += s.Length;
							}
						}
					}

					sessionSerializationTimer.SetResult(true);
				}

				_sessionSizeEventSource.Emit(totalSessionBytes);

				if (totalSessionBytes > Configuration.GetInt64AppSetting("LargeSessionSizeLoggingThreshold", 1000000)) // default to 1MB
				{
					string aid = AtlasContext.Current?.AccountId.ToString();
					string userId = AtlasContext.Current?.UserAccountUid.ToString();

					using (var propertyBag = _largeSessionEventSource.CreateEventPropertyBag())
					{
						propertyBag.AddOrUpdate("AccountId", aid);
						propertyBag.AddOrUpdate("UserAccountUid", userId);

						if (_log.IsDebugEnabled)
						{
							propertyBag.AddOrUpdate("SessionId", session.SessionID);
						}

						propertyBag.AddOrUpdate("SessionSizeInBytes", totalSessionBytes.ToString());
						propertyBag.AddOrUpdate("LargestSessionItemKey", largestItemKey);
						propertyBag.AddOrUpdate("LargestSessionItemSizeInBytes", largestItemSizeBytes.ToString());
						propertyBag.AddOrUpdate("TotalSessionItemCount", itemCount.ToString());
						propertyBag.AddOrUpdate("ThreadId", Thread.CurrentThread.ManagedThreadId.ToString());
					}

					_log.InfoNoPII($"Detected Large SessionState. AccountId {aid} UserAccountUid {userId} SessionId {session.SessionID} SessionSizeInBytes {totalSessionBytes} TotalItemCount {itemCount} Largest Item Key {largestItemKey} ({largestItemSizeBytes})");
				}
			}
			catch (Exception ex)
			{
				_log.WarnNoPII("Exception occurred while attempting to measure session.", ex);
				ex.Report();
			}
		}

		private void OnError(object sender, EventArgs e)
		{
			try
			{
				//get latest exception (top level exception is probably an HTTPUnhandledException which contains underlying exceptions via .InnerException)

				Exception baseException = null;
				var lastError = HttpContext.Current.Server.GetLastError();
				if (lastError == null)
				{
					var allError = HttpContext.Current.AllErrors;
					if (allError != null && allError.Length > 0)
					{
						baseException = allError[allError.Length - 1];
					}
				}
				else
				{
					baseException = lastError.GetBaseException();
				}

				if (_performanceLog != null) _performanceLog.Stop(baseException);
			}
			catch (Exception ex)
			{
				_log.WarnNoPII("Trouble OnError in ProfileModule.", ex);
				ex.Report();
			}
		}

		private void OnEndRequest(object sender, EventArgs e)
		{
			try
			{
				if (_performanceLog != null)
				{
					_performanceLog.Stop();
					_performanceLog.Dispose();
					_performanceLog = null;
				}

				_logContext?.Dispose();
				_logContext = null;
				LogicalThreadContext.Properties["accountid"] = "null";

				// we are no longer looking for outstanding DB transactions here.
				// because the module does not have to run in the same thread as the request handler
			}
			catch (Exception ex)
			{
				_log.WarnNoPII("Trouble OnEndRequest in ProfileModule.", ex);
				ex.Report();
			}

			try
			{
				AtlasContext.Current.CacheClient.FlushCache();
			}
			catch (Exception ex)
			{
				_log.WarnNoPII("Could not clear the user settings on the thread for the request.", ex);
				ex.Report();
			}
		}

		#endregion
	}
}

﻿using System;
using System.Diagnostics;
using System.Threading.Tasks;
using ADEXS.Web.Telemetry;
using ADEXS.Core.Util.Extensions;
using ADEXS.Web.Common.AspNetCore.Extensions;

using DocuSign.CLM.Monitoring.HttpModules;
using DocuSign.Monitoring;
using DocuSign.Msf.AspNet.OpenTelemetry.SemanticConventions;
using DocuSign.Platform;

using Microsoft.AspNetCore.Http;
using ADEXS.Core.Domain.Utilities;

namespace ADEXS.Web.Middlewares;

/// <summary>
/// Middleware that logs requests to KazMon. Code ensures monitoring context runs in both IIS HttpContext and async CallContext.
/// </summary>
public class LogAsyncRequestHttpMiddleware : AbstractLogRequestHttpMiddleware
{
	[EventSource("LogRequestHttpMiddleware", "CLM.WebNet")]
	private static readonly PropertyBagEventSource PropBagEvent = new();

	private readonly IServiceInstrumentation _serviceInstrumentation;

	static LogAsyncRequestHttpMiddleware()
	{
		// Indicate that we're running in both IIS HttpContext and async CallContext.
		// This ensures that logging works properly.
		SystemUtil.RunningInAsyncAndIisContexts = true;
		SystemUtil.RunningInDotNetCore = true;
	}

	public LogAsyncRequestHttpMiddleware(RequestDelegate next, IServiceInstrumentation serviceInstrumentation)
		: base(next)
	{
		_serviceInstrumentation = serviceInstrumentation;
	}

	public override async Task Invoke(HttpContext context)
	{
		var activity = Activity.Current;

		try
		{
			context.AddKazMonServerVariables();
			activity?.SetTagsWithCommonFields(context);
			await base.Invoke(context);
			activity?.SetStatus(context?.Response.StatusCode < 400 ? ActivityStatusCode.Ok : ActivityStatusCode.Error);
		}
		catch (Exception ex) when (ex.TraceAndIgnore(activity, ActivityStatusCode.Error))
		{
			throw;
		}
	}

	protected override PropertyBagEventSource LogRequestHttpModulePropBagEvent => PropBagEvent;
}

