﻿using System;
using System.Diagnostics;
using System.Threading.Tasks;

using ADEXS.Core.Domain;
using ADEXS.Core.Domain.Utilities;
using ADEXS.Core.Types;
using ADEXS.Core.Util.Core;
using ADEXS.Core.Util.Loggers;
using ADEXS.Core.Util.ServiceLocator;
using ADEXS.Web.Common.Session;

using DocuSign.CLM.Monitoring;
using DocuSign.Monitoring;

using log4net;

using Microsoft.AspNetCore.Http;

using Microsoft.AspNetCore.Http.Features;

namespace ADEXS.Web.Common.AspNetCore.Middlewares;

public sealed class ProfileMiddleware : IDisposable
{
	private static readonly IDiscreetLogger Log = DiscreetLogger.GetLogger(typeof(ProfileMiddleware));
	private readonly RequestDelegate _next;
	private IDisposable? _logContext;

	[EventSource("Session Size", "CLM.SessionState")]
	[Counter(
		"Session Size",
		"CLM.SessionState",
		DisplayUnit = CounterUnit.kilobytes,
		Ranges = new double[10] { 1, 5, 10, 50, 100, 500, 1000, 5000, 10000, 20000 },
		MeasureProperty = "Count",
		InstanceProperty = "Description",
		ShowVolume = true)]
	private static readonly CountEventSource SessionSizeEventSource = new CountEventSource();

	[EventSource("Session Serialization Timing", "CLM.SessionState")]
	[Counter(
		"Session Serialization Timing",
		"CLM.SessionState",
		DisplayUnit = CounterUnit.milliseconds,
		Ranges = new double[10] { 10, 20, 50, 100, 200, 500, 1000, 2000, 5000, 10000 },
		MeasureProperty = "Duration",
		SuccessProperty = "Success",
		InstanceProperty = "Description")]
	private static readonly TimingEventSource SessionSerializationTimingEventSource = new TimingEventSource();

	[EventSource("Large Session", "CLM.SessionState")]
	[Counter("Large Session", "CLM.SessionState")]
	private static readonly PropertyBagEventSource LargeSessionEventSource = new PropertyBagEventSource();

	// Avoid using HttpContext.Session directly because it will throw an exception if the session is not available.
	private static bool HasSession(HttpContext context) =>
		context?.Features?.Get<ISessionFeature>() != null && context?.Session != null;

	public ProfileMiddleware(RequestDelegate next)
	{
		_next = next;
	}

	public async Task InvokeAsync(HttpContext context)
	{
		if (context != null)
		{
			// Do something with context near the beginning of request processing.
			OnPreRequestHandlerExecute(context);

			await _next.Invoke(context);

			// Clean up.
			OnPostRequestHandlerExecute(context);
			OnEndRequest();
		}
	}

	public void Dispose()
	{
		_logContext?.Dispose();
	}

	private void OnPreRequestHandlerExecute(HttpContext context)
	{
		try
		{
			Log.DebugNoPII("OnPreRequestHandlerExecute called.");
			AtlasContext.Reset();

			// MonitoringContext is initialized in the AbstractLogRequestHttpMiddleware on
			// BeginRequest. This runs before this event handler. By this time the
			// CorrelationToken is set. Including getting it from the X-DocuSign-CorrelationToken header if available.
			var corrId = MonitoringContext.CorrelationToken ?? Guid.Empty;
			if (corrId == Guid.Empty)
			{
				// Only in extremely unlikely scenario
				if (Guid.TryParse(AtlasContext.Current.TransactionId, out corrId))
				{
					Log.WarnNoPII(
						"MonitoringContext.CorrelationToken was not set, but AtlasContext.Current.TransactionId was set. Using it to set MonitoringContext.CorrelationToken.");
					MonitoringContext.CorrelationToken = corrId;
				}
				else
				{
					Log.WarnNoPII(
						"MonitoringContext.CorrelationToken was not set, and AtlasContext.Current.TransactionId was not a valid Guid. Using Guid.Empty.");
				}
			}
			else
			{
				Log.DebugNoPII($"MonitoringContext.CorrelationToken set to: {corrId}");
				AtlasContext.Current.TransactionId = corrId.ToString();
			}

			// Overwrite TransactionId to use Activity.Current.Id
			if (!string.IsNullOrWhiteSpace(Activity.Current?.Id))
			{
				Log.DebugNoPII($"Activity.Current.Id is set to: {Activity.Current.Id}. Overwriting AtlasContext.Current.TransactionId.");
				AtlasContext.Current.TransactionId = Activity.Current.Id;
			}

			Log.DebugNoPII($"AtlasContext.Current.TransactionId set to: {AtlasContext.Current.TransactionId}");
			_logContext?.Dispose();
			_logContext = LogContext.Set(AtlasContext.Current.TransactionId);

			if (Log.IsDebugEnabled)
			{
				Log.DebugNoPII($"ServiceLocator:\n{ServiceLocator.Container.WhatDoIHave(typeof(ILogContextStore))}");
			}

			if (context.Connection != null)
			{
				Log.DebugNoPII($"Connection RemoteIpAddress: {context.Connection.RemoteIpAddress?.ToString()}");
				AtlasContext.Current.ClientIpAddress = context.Connection.RemoteIpAddress?.ToString();
			}

			if (context.Request != null)
			{
				Log.DebugNoPII($"Request Path: {context.Request.Path}");
				AtlasContext.Current.ClientUserAgent = context.Request.Headers?.UserAgent.ToString();
			}

			// Similar code exists for web services, which don't use session.
			// That code is in SetContextIds
			if (HasSession(context))
			{
				Log.DebugNoPII($"Session Id: {context.Session.Id}");
				var user = CommonData.CurrentUser;
				if (user != null)
				{
					Log.DebugNoPII($"User AccountId: {user.AccountId}, User Uid: {user.Uid}");
					AtlasContext.Current.AccountId = user.AccountId;
					AtlasContext.Current.UserAccountUid = user.Uid;
					LogicalThreadContext.Properties["accountid"] =
						user.AccountId == int.MinValue ? "null" : user.AccountId.ToString();

					var docusignAccountId = CommonData.DocuSignAccountId;
					if (!string.IsNullOrEmpty(docusignAccountId))
					{
						Log.DebugNoPII($"DocuSign AccountId: {docusignAccountId}");
						AtlasContext.Current.DocuSignAccountId = docusignAccountId;
					}

					var docusignUserId = CommonData.DocuSignUserId;
					if (!string.IsNullOrEmpty(docusignUserId))
					{
						Log.DebugNoPII($"DocuSign UserId: {docusignUserId}");
						AtlasContext.Current.DocuSignUserId = docusignUserId;
					}
				}
				else
				{
					Log.DebugNoPII("No user found in session.");
				}
			}
			else
			{
				Log.DebugNoPII("No session found.");
			}
		}
#pragma warning disable CA1031 // Do not catch general exception types
		catch (Exception ex)
#pragma warning restore CA1031 // Do not catch general exception types
		{
			Log.WarnNoPII("Error setting AtlasContext.Current properties.", ex);
			ex.Report();
		}
	}

	private static void OnPostRequestHandlerExecute(HttpContext context)
	{
		Activity.Current?.SetTagsWithCommonFields(context);
		if (HasSession(context))
		{
			LogSessionSize(context.Session);
		}
	}

	/// <summary>
	/// Measuring session size ATL-56207
	/// </summary>
	private static void LogSessionSize(ISession session)
	{
		if (!Configuration.GetBooleanAppSetting("EnableSessionSizeLogging", false))
		{
			return;
		}

		try
		{
			long totalSessionBytes = 0;
			var largestItemKey = string.Empty;
			long largestItemSizeBytes = 0;
			var itemCount = 0;

			using (var sessionSerializationTimer = SessionSerializationTimingEventSource.CreateTimer())
			{
				foreach (var sessionKey in session.Keys)
				{
					if (session.TryGetValue(sessionKey, out var bytes))
					{
						itemCount++;
						if (bytes.Length > largestItemSizeBytes)
						{
							largestItemKey = sessionKey;
							largestItemSizeBytes = bytes.Length;
						}

						totalSessionBytes += bytes.Length;
					}
				}

				sessionSerializationTimer.SetResult(true);
			}

			SessionSizeEventSource.Emit(totalSessionBytes);

			// default to 1MB
			if (totalSessionBytes > Configuration.GetInt64AppSetting("LargeSessionSizeLoggingThreshold", 1000000))
			{
				var aid = AtlasContext.Current?.AccountId.ToString();
				var userId = AtlasContext.Current?.UserAccountUid.ToString();

				using (var propertyBag = LargeSessionEventSource.CreateEventPropertyBag())
				{
					propertyBag.AddOrUpdate("AccountId", aid);
					propertyBag.AddOrUpdate("UserAccountUid", userId);

					if (Log.IsDebugEnabled)
					{
						propertyBag.AddOrUpdate("SessionId", session.Id);
					}

					propertyBag.AddOrUpdate("SessionSizeInBytes", totalSessionBytes.ToString());
					propertyBag.AddOrUpdate("LargestSessionItemKey", largestItemKey);
					propertyBag.AddOrUpdate("LargestSessionItemSizeInBytes", largestItemSizeBytes.ToString());
					propertyBag.AddOrUpdate("TotalSessionItemCount", itemCount.ToString());
					propertyBag.AddOrUpdate("ThreadId", Environment.CurrentManagedThreadId.ToString());
				}

				Log.InfoNoPII(
					$"Detected Large SessionState. AccountId {aid} UserAccountUid {userId} SessionId {session.Id} SessionSizeInBytes {totalSessionBytes} TotalItemCount {itemCount} Largest Item Key {largestItemKey} ({largestItemSizeBytes})");
			}
		}
#pragma warning disable CA1031 // Do not catch general exception types
		catch (Exception ex)
#pragma warning restore CA1031 // Do not catch general exception types
		{
			Log.WarnNoPII("Exception occurred while attempting to measure session.", ex);
			ex.Report();
		}
	}

	private void OnEndRequest()
	{
		try
		{
			_logContext?.Dispose();
			_logContext = null;
			LogicalThreadContext.Properties["accountid"] = "null";

			// we are no longer looking for outstanding DB transactions here.
			// because the module does not have to run in the same thread as the request handler
		}
#pragma warning disable CA1031 // Do not catch general exception types
		catch (Exception ex)
#pragma warning restore CA1031 // Do not catch general exception types
		{
			Log.WarnNoPII("Trouble OnEndRequest in ProfileModule.", ex);
			ex.Report();
		}

		try
		{
			AtlasContext.Current.CacheClient.FlushCache();
		}
#pragma warning disable CA1031 // Do not catch general exception types
		catch (Exception ex)
#pragma warning restore CA1031 // Do not catch general exception types
		{
			Log.WarnNoPII("Could not clear the user settings on the thread for the request.", ex);
			ex.Report();
		}
	}
}

﻿using System;
using System.Diagnostics;
#if NETFRAMEWORK
using System.Web;
using System.Web.Mvc;
using System.Web.Routing;
using ADEXS.Web.CommonUI.Helpers;
#else
using Microsoft.AspNetCore.Mvc.Filters;
using Microsoft.AspNetCore.Http;
#endif
using ADEXS.Core.Domain;
using ADEXS.Core.Domain.FeatureFlags;
using ADEXS.Core.Domain.Exceptions;
using ADEXS.Core.Types;
using ADEXS.Core.Util.Extensions;

using DocuSign.CLM.Monitoring;
using DocuSign.DynamicSettings;
using DocuSign.Monitoring;
using ADEXS.Core.Domain.Utilities;

namespace ADEXS.Web.CommonUI.MVC.Filters
{
	public abstract class MvcAuditedRequestAttribute : ActionFilterAttribute, IActionFilter, IExceptionFilter
	{
		/// <summary>
		/// Should always return the same *statically* allocated instance. Example:
		/// [EventSource("Request Duration", "CLM.API")]
		/// [Counter("Request Duration", "CLM.API",
		/// InstanceProperty = "Description",
		/// DisplayUnit = CounterUnit.milliseconds,
		/// Ranges = new double[10] { 10, 25, 50, 100, 200, 500, 1000, 2000, 5000, 10000 },
		/// MeasureProperty = "Duration",
		/// SuccessProperty = "Success")]
		/// static readonly TimingEventSource apiEventSource = new TimingEventSource();
		/// </summary>
		protected abstract TimingEventSource ApiEventSource { get; }

		/// <summary>
		/// Should always return the same *statically* allocated instance. Example:
		/// [EventSource("Request Attempts", "CLM.API")]
		/// [Counter("Request Attempts", "CLM.API", InstanceProperty = "Description")]
		/// static readonly SimpleEventSource attemptEventSource = new SimpleEventSource();
		/// </summary>
		protected abstract SimpleEventSource AttemptEventSource { get; }

		/// <summary>
		/// Name of the property under which the timing Stopwatch instance will be stored.
		/// Example: "ClmApi.Stopwatch"
		/// </summary>
		protected abstract string PropName_StopWatch { get; }

		/// <summary>
		/// Name of the property under which the the name of the counter instance will be stored.
		/// Example: "ClmApi.InstanceName"
		/// </summary>
		protected abstract string PropName_CounterInstance { get; }

		private static string NO_CONTROLLER_STRING = "_no-controller_";
		private static string NO_ACTION_NAME = "_no-action_";
		private static string NO_HTTP_VERB = "_no-verb_";

		public override void OnActionExecuting(ActionExecutingContext filterContext)
		{
#if NETFRAMEWORK
			string controllerName = filterContext.ActionDescriptor.ControllerDescriptor.ControllerName;
			ServiceContext.Log(DataPoint.ApiController, controllerName);
			string actionName = filterContext.ActionDescriptor.ActionName;
			ServiceContext.Log(DataPoint.Action, actionName);
#else
			var controllerName = filterContext.ActionDescriptor.RouteValues["controller"] ?? NO_CONTROLLER_STRING;
			ServiceContext.Log(DataPoint.ApiController, controllerName);
			var actionName = filterContext.ActionDescriptor.RouteValues["action"] ?? NO_ACTION_NAME;
			ServiceContext.Log(DataPoint.Action, actionName);
#endif

			if (ApplicationSettings.EnableFeatureFlagsContext)
			{
				DynamicSettingsContext.Items[DynamicSettingsContextItems.ControllerName] = controllerName;
				DynamicSettingsContext.Items[DynamicSettingsContextItems.ActionName] = actionName;
			}

			var httpContext = filterContext.HttpContext;
#if NETFRAMEWORK
			string counterInstanceName = FormatInstanceName(controllerName, actionName, httpContext.Request.HttpMethod);
#else
			var counterInstanceName = FormatInstanceName(controllerName, actionName, httpContext.Request.Method);
#endif
			AttemptEventSource.Emit(counterInstanceName);

#if NETFRAMEWORK
			Activity.Current?.SetTagsWithCommonFields(httpContext?.ApplicationInstance?.Context);
#else
			Activity.Current?.SetTagsWithCommonFields(httpContext);
#endif

			Stopwatch sw = Stopwatch.StartNew();
			httpContext.Items[PropName_StopWatch] = sw;
			httpContext.Items[PropName_CounterInstance] = counterInstanceName;

			if (ApplicationSettings.EnableFeatureFlags && ApplicationSettings.TestFeatureFlagEvaluation)
			{
				int intSetting = AtlasFeatureFlags.DummyIntSetting;
				string stringSetting = AtlasFeatureFlags.DummyStringSetting;
				ServiceContext.Log(
					DataPoint.ApiRequestLogging,
					$"intSetting: {intSetting} | stringSetting: {stringSetting}");
			}
		}

		public override void OnActionExecuted(ActionExecutedContext filterContext)
		{
			SetCommonDataPoints();

			(Stopwatch sw, string instanceName) = GetStopWatchAndInstanceName(filterContext.HttpContext);
			FinalizeLogging(sw, instanceName, false);
		}

		public virtual void OnException(ExceptionContext filterContext)
		{
			(Stopwatch sw, string instanceName) = GetStopWatchAndInstanceName(filterContext.HttpContext);
			bool isServerError = IsServerError(filterContext.Exception);
			if (filterContext.Exception != null)
			{
				filterContext.Exception.Trace().Report();
				ServiceContext.Log(DataPoint.ApiErrorMessage, filterContext.Exception.Message);
			}

			FinalizeLogging(sw, instanceName, isServerError);
		}

#if NETFRAMEWORK
		private (Stopwatch sw, string instanceName) GetStopWatchAndInstanceName(HttpContextBase context)
		{
			Stopwatch sw = context.Items[PropName_StopWatch] as Stopwatch;
			if (null == sw)
			{
				sw = Stopwatch.StartNew();
			}

			string instanceName = context.Items[PropName_CounterInstance] as string;
			if (null == instanceName)
			{
				instanceName = FormatInstanceName(context);
			}

			return (sw, instanceName);
		}
#else
		private (Stopwatch sw, string instanceName) GetStopWatchAndInstanceName(HttpContext context)
		{
			var sw = context.Items[PropName_StopWatch] as Stopwatch ?? Stopwatch.StartNew();

			var instanceName = context.Items[PropName_CounterInstance] as string ?? FormatInstanceName(context);

			return (sw, instanceName);
		}
#endif

#if NETFRAMEWORK
		protected virtual string FormatInstanceName(HttpContextBase httpContext)
		{
			string controllerName = NO_CONTROLLER_STRING;
			string actionName = NO_ACTION_NAME;
			string httpMethod = NO_HTTP_VERB;
			try
			{
				RouteData rd = httpContext.Request.RequestContext.RouteData;
				actionName = rd.GetRequiredString("action");
				controllerName = rd.GetRequiredString("controller");
				httpMethod = httpContext.Request.HttpMethod;
			}
			catch(Exception ex)
			{
				ex.Report();
			}
			return FormatInstanceName(controllerName, actionName, httpMethod);
		}
#else
		protected virtual string FormatInstanceName(HttpContext httpContext)
		{
			object? actionNameObj = null;
			object? controllerNameObj = null;
			var httpMethod = NO_HTTP_VERB;
			try
			{
				httpContext.Request.RouteValues.TryGetValue("action", out actionNameObj);
				httpContext.Request.RouteValues.TryGetValue("controller", out controllerNameObj);
				httpMethod = httpContext.Request.Method;
			}
			catch (Exception ex)
			{
				ex.Report();
			}

			var controllerName = controllerNameObj as string ?? NO_CONTROLLER_STRING;
			var actionName = actionNameObj as string ?? NO_ACTION_NAME;
			return FormatInstanceName(controllerName, actionName, httpMethod);
		}
#endif

		protected virtual string FormatInstanceName(string controllerName, string actionName, string httpMethod)
		{
			return $"{controllerName}.{actionName}.{httpMethod}";
		}

		private void FinalizeLogging(Stopwatch sw, string instanceName, bool isError)
		{
			TimeSpan apiRunTime = TimeSpan.MinValue;
			if (sw != null)
			{
				apiRunTime = sw.Elapsed;
			}

			ApiEventSource.Emit(apiRunTime, !isError, instanceName);
		}

		protected virtual bool IsServerError(Exception exception)
		{
			bool isError = !(exception is AdexsDisplayException);

			return isError;
		}


		public static void SetCommonDataPoints()
		{
			if (AtlasContext.Current.AccountId != int.MinValue)
				ServiceContext.Log(DataPoint.AccountId, AtlasContext.Current.AccountId);
			if (AtlasContext.Current.UserAccountUid != Guid.Empty)
				ServiceContext.Log(DataPoint.UserId, AtlasContext.Current.UserAccountUid);
			if (AtlasContext.Current.UserAccount != null)
				ServiceContext.Log(DataPoint.AccountType, Enum.GetName(typeof(Enums.AccountType), AtlasContext.Current.UserAccount.Account.AccountType));
		}
	}
}

﻿using System;
using System.Diagnostics;
using System.Linq;
using System.Web;
using System.Web.Mvc;
using ADEXS.Core.Domain;
using ADEXS.Core.Domain.Exceptions;
using ADEXS.Core.Domain.Utilities;
using ADEXS.Core.Types;
using ADEXS.Core.Util.Extensions;
using DocuSign.CLM.Monitoring;
using DocuSign.Monitoring;

namespace ADEXS.Web.CommonUI.Helpers
{
	public abstract class HttpRequestAuditHelper
	{
		/// <summary>
		/// Should always return the same *statically* allocated instance. Example:
		/// [EventSource("Request Duration", "CLM.API")]
		/// [Counter("Request Duration", "CLM.API",
		///	InstanceProperty = "Description",
		///	DisplayUnit = CounterUnit.milliseconds,
		///	Ranges = new double[10] { 10, 25, 50, 100, 200, 500, 1000, 2000, 5000, 10000 },
		///	MeasureProperty = "Duration",
		///	SuccessProperty = "Success")]
		/// static readonly TimingEventSource apiEventSource = new TimingEventSource();
		/// </summary>
		protected abstract TimingEventSource ApiEventSource { get; }

		/// <summary>
		/// Should always return the same *statically* allocated instance. Example:
		/// [EventSource("Request Attempts", "CLM.API")]
		/// [Counter("Request Attempts", "CLM.API", InstanceProperty = "Description")]
		/// static readonly SimpleEventSource attemptEventSource = new SimpleEventSource();
		/// </summary>
		protected abstract SimpleEventSource AttemptEventSource { get; }

		/// <summary>
		/// Name of the property under which the timing Stopwatch instance will be stored.
		/// Example: "ClmApi.Stopwatch"
		/// </summary>
		protected abstract string PropName_StopWatch { get; }

		/// <summary>
		/// Name of the property under which the the name of the counter instance will be stored.
		/// Example: "ClmApi.InstanceName"
		/// </summary>
		protected abstract string PropName_CounterInstance { get; }

		/// <summary>
		/// Name of the property under which any error will be stored.
		/// Example: "ClmApi.Error"
		/// </summary>
		protected abstract string PropName_Error { get; }

		/// <summary>
		/// Implementations are intended to be used as singletons.
		/// </summary>
		protected HttpRequestAuditHelper()
		{
		}

		public static void SetCommonDataPoints()
		{
			if (AtlasContext.Current.AccountId != int.MinValue)
				ServiceContext.Log(DataPoint.AccountId, AtlasContext.Current.AccountId);
			if (AtlasContext.Current.UserAccountUid != Guid.Empty)
				ServiceContext.Log(DataPoint.UserId, AtlasContext.Current.UserAccountUid);
			if (AtlasContext.Current.UserAccount != null)
				ServiceContext.Log(DataPoint.AccountType, Enum.GetName(typeof(Enums.AccountType), AtlasContext.Current.UserAccount.Account.AccountType));
		}

		public virtual void BeginRequest(HttpContext httpContext)
		{
			Activity.Current?.SetTagsWithCommonFields(httpContext);

			if (httpContext.Handler is MvcHandler)
			{
				// Handled via MvcAuditedRequestAttribute
				return;
			}

			string counterInstanceName = FormatInstanceName(httpContext);
			AttemptEventSource.Emit(counterInstanceName);

			Stopwatch sw = Stopwatch.StartNew();
			httpContext.Items[PropName_StopWatch] = sw;
			httpContext.Items[PropName_CounterInstance] = counterInstanceName;
		}

		public virtual void EndRequest(HttpContext httpContext)
		{
			if(httpContext.Handler is MvcHandler)
			{
				// Handled via MvcAuditedRequestAttribute
				return;
			}

			SetCommonDataPoints();
			(Stopwatch sw, string instanceName) = GetStopWatchAndInstanceName(httpContext);

			bool isError = false;
			Exception exError = httpContext.Items[PropName_Error] as Exception;
			if (null != exError && !(exError is AdexsDisplayException))
			{
				isError = true;
			}

			FinalizeLogging(sw, instanceName, isError);
		}

		public virtual void SetError(HttpContext httpContext, Exception ex)
		{
			if(httpContext.Handler is MvcHandler)
			{
				// Handled via MvcAuditedRequestAttribute
				return;
			}

			if(null != ex)
			{
				ex.Trace().Report();
				ServiceContext.Log(DataPoint.ApiErrorMessage, ex.Message);
				httpContext.Items[PropName_Error] = ex;
			}
		}

		private (Stopwatch, string) GetStopWatchAndInstanceName(HttpContext httpContext)
		{
			Stopwatch sw = httpContext.Items[PropName_StopWatch] as Stopwatch;
			if (null == sw)
			{
				sw = Stopwatch.StartNew();
			}

			string instanceName = httpContext.Items[PropName_CounterInstance] as string;
			if (null == instanceName)
			{
				instanceName = FormatInstanceName(httpContext);
			}

			return (sw, instanceName);
		}

		protected virtual string FormatInstanceName(HttpContext httpContext)
		{
			var pageName = "_no-page_";
			var httpMethod = "_no-verb_";
			try
			{
				httpMethod = httpContext.Request.HttpMethod;
				pageName = httpContext.Request.Url.Segments.Last();
			}
			catch(Exception ex)
			{
				ex.Report();
			}

			return FormatInstanceName(pageName, httpMethod);
		}

		protected virtual string FormatInstanceName(string pageName, string httpMethod)
		{
			return $"{pageName}.{httpMethod}";
		}

		private void FinalizeLogging(Stopwatch sw, string instanceName, bool isError)
		{
			TimeSpan apiRunTime = TimeSpan.MinValue;
			if (sw != null)
			{
				apiRunTime = sw.Elapsed;
			}

			ApiEventSource.Emit(apiRunTime, !isError, instanceName);
		}
	}
}

