		protected void Application_Error(Object sender, EventArgs e)
		{
			Exception exception = Server.GetLastError();
			if (exception == null)
			{
				Exception[] allError = HttpContext.Current?.AllErrors;
				if (allError != null && allError.Length > 0)
					exception = allError[allError.Length - 1];
			}
			var guid = Activity.Current?.Id ?? Guid.NewGuid().ToString();
			var message = $"An unhandled exception occured. ReferenceId: {guid}";
			_log.ErrorNoPII(message, exception);
			Response.Redirect($"Error.aspx?{Constants.QueryStringParameters.MessageRef}={guid}");

			exception?.Trace().Report();
		}

