using RabbitMQ.Client;

using ConfigurationProvider = ADEXS.Core.Util.Cache.ConfigurationProvider;
using ADEXS.AtlasDataManipulationService.Extensions;

namespace ADEXS.AtlasDataManipulationService;

public class Startup
{
	private const string ServiceName = "clm-data-manipulation";

	private readonly IConfiguration _configuration;

	private readonly IWebHostEnvironment _webHostEnvironment;

	public Startup(IConfiguration configuration, IWebHostEnvironment webHostEnvironment)
	{
		_configuration = configuration;
		_webHostEnvironment = webHostEnvironment;
	}

	// This method gets called by the runtime. Use this method to add services to the container.
	// For more information on how to configure your application, visit https://go.microsoft.com/fwlink/?LinkID=398940
	public void ConfigureServices(IServiceCollection services)
	{
		// We need to update config sooner than Hosting startup.
		ConfigurationProvider.UseConfiguration(_configuration);
		ApplicationSettingsCache.Flush();

		services
			.Configure<ServicedComponentHostedServiceOptions>(
				_configuration.GetSection(ServicedComponentHostedServiceOptions.Section))
			.Configure<VersionEndpointOptions>(_configuration.GetSection(VersionEndpointOptions.Section))
			.Configure<ForwardedHeadersOptions>(_configuration.GetSection("ForwardedHeaders"))
			.Configure<ForwardedHeadersOptions>(options =>
			{
				options.KnownProxies.Clear();
				options.KnownNetworks.Clear();
			}); ;

		var telemetryConsoleTargets = _configuration
			.GetSection("Telemetry:Console:Targets")
			?.Get<ConsoleExporterOutputTargets>();

		services
			.AddHttpContextAccessor()
			.AddServiceHealthChecks(_configuration)
			.AddAtlasDataManipulationHealthChecks(_configuration);

		services.AddHeaderPropagation(options => options.ConfigureMicroservices())
			.AddComponentsInstrumentation()
			.AddServiceTelemetry(
				ServiceName,
				configureTracer: options =>
				{
					options
						.AddHttpClientInstrumentation()
						.AddSqlClientInstrumentation()
						.AddRedisInstrumentation()
						.ConfigureRedisCacheInstrumentation()
						.AddScmCassandraPersistenceInstrumentation();

					if (telemetryConsoleTargets != null)
					{
						options.AddConsoleExporter(o => o.Targets = telemetryConsoleTargets.Value);
					}
				},
				configureMeter: options =>
				{
					options
						.AddHttpClientInstrumentation()
						.AddServiceMetrics()
						.AddProcessInstrumentation()
						.AddCassandraInstrumentation()
						.AddScmCassandraPersistenceInstrumentation()
						.AddRedisCacheMetrics();

					if (telemetryConsoleTargets != null)
					{
						options.AddConsoleExporter(o => o.Targets = telemetryConsoleTargets.Value);
					}
				})
			.AddVersionEndpoint()
			.AddSingleton<IConnectionFactory>(sp =>
			{
				var configuration = sp.GetRequiredService<RabbitMessageQueueWrapperConnectionConfig>();

				return new ConnectionFactory
				{
					Uri = RabbitMessageQueueWrapperConnectionConfig.BuildUri(),
					ClientProvidedName = configuration.GetClientDefinedConnectionName(),
					AutomaticRecoveryEnabled = true
				};
			});

		services.AddSingleton<ICassandraSettings, ScmCassandraSettings>();
		services.AddSingleton<IAtlasCassandraCluster, AtlasCassandraCluster>();
	}
	public void Configure(IApplicationBuilder app)
	{
		if (_configuration.GetValue<bool>("CassandraWireLogging"))
		{
			app.WireCassandraLogging();
		}

		if (_webHostEnvironment.IsDevelopment())
		{
			app.UseDeveloperExceptionPage();
		}

		app.UseHeaderPropagation();

		app.UseRouting();

		app.UseEndpoints(endpoints =>
		{
			endpoints.MapServiceHealthChecks();

			endpoints.MapVersionEndpoint($"{ServiceName}/v1/version");

			endpoints.MapGet("/", () => $"No WebAPI defined for {ServiceName}");
		});
	}
}

