[Route($"{WorkflowActivitiesConstants.ServiceUrlPrefix}[controller]")] // Cloud routing
public partial class WorkflowActivitiesController : Controller
{
	private readonly ILogger _logger;
	private readonly IWorkflowDesignToWorkflowMapMapper _workflowDesignToWorkflowMapMapper;

	public WorkflowActivitiesController(
		IServiceInstrumentation serviceInstrumentation,
		IWorkflowDesignToWorkflowMapMapper workflowDesignToWorkflowMapMapper,
		ILoggerFactory loggerFactory)
	{
		_serviceInstrumentation = serviceInstrumentation;
		_workflowDesignToWorkflowMapMapper = workflowDesignToWorkflowMapMapper;
		_logger = loggerFactory.CreateLogger(GetType());
