	[HttpPatch("complete")]
	[ProducesResponseType(typeof(ActivityCompleteResponse), (int)HttpStatusCode.OK)]
	public async Task<IActionResult> CheckActivityForCompletionAsync([FromBody] ActivityContext activityContext)
	{
		ValidationUtils.ValidateActivityExecuteContext(activityContext, _logger, out var accountId, out var userAccountUid);
		using (_logger.BeginScope($"[AccountId: {accountId}, UserAccountUid: {userAccountUid}]"))
		{
			try
			{
				using var activity = StartActivity(accountId, userAccountUid, activityContext.WorkflowActivityContext.InstanceActivity.InstanceUid, activityContext.WorkflowActivityContext.InstanceActivity.Uid);
				var winsActivity = _workflowDesignToWorkflowMapMapper.MapSingleActivity(accountId, activityContext.ActivityJsonDefinition);
				var output = activityContext.WorkflowActivityContext.InstanceActivity.Output;
				var activityCompleteResponse = new ActivityCompleteResponse { ActivityOutput = null };
				if (winsActivity.CanBeMarkedAsCompleted(activityContext.WorkflowActivityContext, ref output))
				{
					activityContext.WorkflowActivityContext.InstanceActivity.Output = output;
					var instanceActivityOutputs = winsActivity.OnActivityCompleted(activityContext.WorkflowActivityContext);
					var activityOutput = new ActivityOutput
					{
						WorkflowActivityContext = activityContext.WorkflowActivityContext
					};
					activityOutput.InstanceActivityOutput.AddRange(instanceActivityOutputs);

					activityCompleteResponse.ActivityOutput = activityOutput;
				}

				activity?.SetStatus(ActivityStatusCode.Ok);
				return await Task.FromResult(Ok(activityCompleteResponse));
			}
			catch (Exception ex)
			{
				var msg = $"Failed to check the activity for completion due to exception: {ex.GetType().Name}: {ex.Message}";
				_logger.LogError(ex, msg);
				ServiceContext.Log(DataPoint.ErrorMessage, msg);
				throw;
			}
		}
	}

